import React, { useEffect, useState } from 'react';
import SignInCarosel from '../employer/signInCarousel'
import { ReactComponent as LoginBg } from '../../assets/images/sign-in-bg.svg';
import { Button } from '@mui/material';
import { ReactComponent as LoginBack } from '../../assets/images/left-nav.svg';
import { useNavigate } from 'react-router-dom';



import './style.scss'

const EmployerBanner = (props) => {
  const navigate = useNavigate();
  const [locationValue,setLocationValue] = useState('');
  const handleGoBack = () => {
    navigate(-1); 
  };
  useEffect(() => {
    locationIp();
}, []);
const locationIp = () =>{
  try {
    fetch(
      `https://ipapi.co/json/`)
      .then((res) => res.json())
      .then((data) => {
        setLocationValue(data?.city)     
      })

  } catch (error) {
    console.log("Error fetching city name:", error);
  }
}
  return (
    <div className="SinginFormLeft" style={{backgroundColor: '#F5FAFF'}}>
      <Button className='loginBackBtn' onClick={handleGoBack} style={{backgroundColor: 'rgb(245, 250, 255)'}}>
          <LoginBack/>
          <span>Back</span>
      </Button>
      <LoginBg className="SinginFormBgImg BannerSVG"/>
            {/* <img src={require('../../assets/images/sign-in-bg.png')} className="SinginFormBgImg" alt="" /> */}
            <div className="SinginFormCarouselContainer">
              <div className="SinginFormCarouselTitle">
                Join over 15,000+ <br />
                users around {locationValue}
              </div>
              <SignInCarosel />
            </div>
          </div>
  );
}

export default EmployerBanner;
