import { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import EmployerHeader from '../header/employersHeader';
import '../header/style.scss';
import ExplorePageComponent from '../../pages/parttimer/explore';
import ExploreCertificationPageComponent from '../../pages/parttimer/certification-explore';

import EmployeeHeader from '../header/employee-header';
import DescriptionPage from '../../pages/parttimer/description';
import PersonalInfo from '../../pages/parttimer/registration-wizard/personal-info';
import PartTimerRegistrationComponent from '../../pages/parttimer/registration-wizard';
import MyJobsComponent from '../../pages/parttimer/my-jobs';
import PartTimerUserProfileComponent from '../../pages/parttimer/user-profile';
import SubscriptionComponent from '../../pages/subscription';
import EmployeeSubscriptionComponent from '../../pages/subscription/employe-subscription';
import RejectionSubscriptionComponent from '../../pages/parttimer/rejection-subscription';

import DescriptionCertificate from '../../pages/parttimer/descriptionCertificate';

export default function EmployeeLayout(props) {
    const { pageTitle ,roles} = props;
    console.log("roles",roles);
    const location = useLocation();
    const loginType = JSON.parse(localStorage.getItem('currentUser'));
    console.log("roles && loginType?.types",roles && loginType?.types);
    // debugger
    if (roles && loginType?.types) {
        if (!roles.every((val)=>loginType?.types?.includes(val)) ){                  
            window.open(window.location.origin + '/', "_self")
        }
    }

    useEffect(() => {
        const body = document.querySelector('body');
        if (location.pathname === '/explorejobs') {
            body.classList.add('noSideBar');
        } else {
            body.classList.remove('noSideBar');
        }
        if (location.pathname === '/descriptionjob') {
            body.classList.add('noSideBar');
        }
        if (location.pathname === '/descriptionCertificate') {
            body.classList.add('noSideBar');
        }
        if (location.pathname === '/my-jobs') {
            body.classList.add('noSideBar');
        }
        if (location.pathname === '/parttimer-profile') {
            body.classList.add('noSideBar');
        }
        return () => {
            body.classList.remove('noSideBar');
        };

    }, [location.pathname]);

    return (
        <>
            <div className="employerDashboardLayout">
                {/* <div className="employerDashboardRight"> */}
                    <EmployeeHeader />
                    {pageTitle === 'explore-certifications' ? <ExploreCertificationPageComponent /> : null}

                    {pageTitle === 'explorejobs' ? <ExplorePageComponent /> : null}
                    {pageTitle === 'descriptionjob' ? <DescriptionPage /> : null}
                    {pageTitle === 'employeesignin' ? <PartTimerRegistrationComponent /> : null}
                    {pageTitle === 'myJobs' ? <MyJobsComponent /> : null}
                    {pageTitle === 'parttimer-profile' ? <PartTimerUserProfileComponent /> : null}
                    {pageTitle === 'employee-subscription' ? <EmployeeSubscriptionComponent/> : null}
                    {/* {pageTitle === 'employeesubscription' ? <SubscriptionComponent/> : null} */}
                    {pageTitle === 'my-jobs' ? <MyJobsComponent /> : null}
                    {pageTitle === 'rejection-subscription' ? <RejectionSubscriptionComponent /> : null}


                    {pageTitle === 'descriptionCertificate' ? <DescriptionCertificate /> : null}

                {/* </div> */}
            </div>

        </>
    );
}
