import React, { useEffect, useState } from 'react';
import { Grid, Button, FormControl, TextField, TextareaAutosize, Select, MenuItem, InputAdornment, RadioGroup, InputLabel, FormControlLabel, Radio, Checkbox, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from '@mui/material';
import { ReactComponent as ProfileBanner } from '../../assets/images/post-job-banner.svg';
import './part-timer-style.scss'
import { EmployeeSubscriptionView, employeeBankUpdate, employeePhotosUpdate, employeeProfile, employeeProfilesUpdate, getEmployeeBankDetails, sendOtp, verifyOtp } from '../../sevices/account';
import { Modal, Input } from 'antd';
import { ReactComponent as UncheckIcon } from '../../assets/images/radio-uncheck.svg';
import { ReactComponent as CheckIcon } from '../../assets/images/radio-check.svg';
import { MuiOtpInput } from 'mui-one-time-password-input';
import DoneIcon from '@mui/icons-material/Done';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DemoContainer } from '@mui/x-date-pickers/internals/demo';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import dayjs from 'dayjs';
import _ from 'lodash';
import { useNavigate } from 'react-router-dom';

import { removeItemFrmLocalStorage, isEmptyObj } from '../../utils/genericUtilities';
import CustomSnackBar from '../../shared/snackBar';

import { Spin } from 'antd';

import { VALIDATION_MSG } from '../../constant';
// import { geocodeAddress } from './my-jobs-tabs/GeocodingService';


const PartTimerUserProfileComponent = () => {
    const [profileData, setprofileData] = useState('')
    const [selectedFile, setSelectedFile] = useState(null);
    const [visible, setVisible] = useState(false);
    const [profilevisible, setProfileVisible] = useState(false);
    const [aboutvisible, setaboutVisible] = useState(false);
    const [bankVisible, setBankVisible] = useState(false);
    const [checkedItems, setCheckedItems] = useState([]);
    const [numberVerified, setNumberVerified] = useState(false);
    const [previewImage, setPreviewImage] = useState(null);
    const [otpSent, setOtpSent] = useState(false);
    const [otp, setOtp] = useState('');
    const [otpResponse, setOtpResponse] = useState('')
    const [phone, setPhone] = useState('');
    const [firstname, setfirstname] = useState('')
    const [lastname, setlastname] = useState('')
    const currentDate = dayjs();
    const defaultDate = currentDate.subtract(18, 'year');
    const defaultDateStr = defaultDate.format('YYYY-MM-DD');
    // const [dobValue, setdobValue] = useState(dayjs(defaultDateStr));
    const [dob, setdob] = useState('');
    const [email, setemail] = useState('')
    const [Loading, setLoading] = useState(false);
    const [gender, setgender] = useState('')
    const [language, setlanguage] = useState('')
    const [address, setaddress] = useState('')
    const [aadharnum, setaadharnum] = useState('')
    const [city, setcity] = useState('')
    const [state, setstate] = useState('')
    const [pincode, setpincode] = useState('')
    const [education, seteducation] = useState('')
    const [college, setcollege] = useState('')
    const [university, setuniversity] = useState('')
    const [yearoafpassing, setyearoafpassing] = useState('')
    const [fieldeducation, setfieldeducation] = useState('')
    const [experincedetails, setexperincedetails] = useState('')
    const [experincecategory, setexperincecategory] = useState('')
    const [about, setabout] = useState('')
    const [loading, setloading] = useState(false)
    const [photo, setphoto] = useState('')
    const [photostatus, setphotostatus] = useState(false)
    const [validity, setvalidity] = useState('')
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});
    const navigate = useNavigate();
    const [errorPopupOpen, setErrorPopupOpen] = useState(false);
    const [errorMessage, setSrrorMessage] = useState('');
    const [photoload, setphotoload] = useState(false)
    const [validityload, setvalidityload] = useState(false)
    // for bank details
    const [accountNumber, setAccountNumber] = useState('')
    const [accountType, setAccountType] = useState('')
    const [accountHolderName, setAccountHolderName] = useState('')
    const [ifscCode, setIfscCode] = useState('')
    const [bankDetails, setBankDetails] = useState({})
    const ErrorPopup = () => {
        return (
            <Dialog open={errorPopupOpen} onClose={() => setErrorPopupOpen(false)}>
                <DialogTitle>Error</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        {errorMessage}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setErrorPopupOpen(false)}>OK</Button>
                </DialogActions>
            </Dialog>
        );
    };
    const [educationError, seteducationError] = useState('')
    const [collegeError, setcollegeError] = useState('')
    const [universityError, setuniversityError] = useState('')
    const [fielldError, setfieldError] = useState('')
    const [yearError, setyearError] = useState('')


    const handleLogout = () => {
        removeItemFrmLocalStorage(['token', 'currentUser'])
        navigate("/")
    }

    const handleCheckboxChange = (event) => {
        const { name, checked } = event.target;
        if (checked) {
            setCheckedItems([...checkedItems, name]);
        } else {
            setCheckedItems(checkedItems.filter((item) => item !== name));
        }
    };
    const checkboxData = [
        { id: 'communication', label: 'Communication' },
        { id: 'technical', label: 'Technical' },
        { id: 'marketing', label: 'Marketing' },
        { id: 'leadership', label: 'Leadership' },
        { id: 'interpersonal', label: 'Interpersonal' },
        { id: 'sales', label: 'Sales' },
        { id: 'teamwork', label: 'Teamwork' },
        { id: 'customer_service', label: 'Customer service' },
        { id: 'analytical', label: 'Analytical' },
        { id: 'creative_design', label: 'Creative or Design' },
        { id: 'financial_analysis', label: 'Financial analysis' },
        { id: 'data analysis', label: 'Data analysis' },
        { id: 'Project_management', label: 'Project management' },
        { id: 'research', label: 'Research' },
        { id: 'writing_editing', label: 'Writing and editing' },
    ];


    useEffect(() => {
        fetchJobData();
        SubscriptionView();
        getBankDetails()
    }, [loading])



    const getBankDetails = async () => {
        setLoading(true);
        try {
            const response = await getEmployeeBankDetails();
            let data = response?.data?.metadata
            setBankDetails(data)
            console.log("bankdetails",data);
            setLoading(false);

        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: 'Something Went Wrong' })
        }
    };


    const SubscriptionView = async () => {
        setvalidityload(true)
        try {
            const View = await EmployeeSubscriptionView({})
            if (View.status === 200) {
                let timestamp = View.data.details.current_period_end
                const targetDate = new Date(timestamp * 1000)
                const currentDate = new Date();
                const timeDiff = targetDate.getTime() - currentDate.getTime();
                const daysRemaining = Math.floor(timeDiff / (1000 * 3600 * 24));
                setvalidity(daysRemaining)
                setvalidityload(false)
                if (View?.data?.details?.status != 'active') {
                    navigate("/employee-subscription?roles=employee&data=renewal")
                }
            }
        } catch (error) {
            setvalidityload(false)
            console.error('BJJKNJ error:', error);
            handleSnackbarClick(true, { type: 'error', msg: 'Subscription not yet completed' })
        }

    }


    const fetchJobData = async () => {
        setLoading(true);
        try {
            const employee = await employeeProfile({});
            let data = employee?.data?.details
            let photo = employee?.data
            let photo_path = `${_.get(photo, 'meta.employee_photo.path')}/${_.get(photo, 'meta.employee_photo.folder')}/${_.get(photo, 'meta.employee_photo.sizes.medium')}/${_.get(photo, 'details.employee_photo')}`

            setphoto(photo)

            setfirstname(data?.first_name)
            setlastname(data?.last_name)
            setPhone(data?.phone)
            setdob(data?.dob)
            setemail(data?.email)
            setgender(data?.gender)
            setlanguage(data?.language_known)
            setaddress(data?.address)
            setaadharnum(data?.aadhar_number)
            setcity(data?.city)
            setstate(data?.state)
            setpincode(data?.pincode)
            setprofileData(data)
            seteducation(data?.education_qualification)
            setcollege(data?.college_name)
            setuniversity(data?.University)
            setyearoafpassing(data?.year_of_passing)
            setfieldeducation(data?.field_of_education)
            setexperincedetails(data?.field_of_experience)
            setexperincecategory(data?.experience_category)
            // const stringWithoutCurlyBraces = data?.experience_list.substring(1, data?.experience_list.length - 1);
            // const nameArray = stringWithoutCurlyBraces.split(',');
            setCheckedItems(data?.experience_list)
            console.log("AASBABSBABSA", data?.experience_list)
            setabout(data?.about_you)
            setLoading(false);
            setphotoload(false)
        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: 'Something Went Wrong' })
        }
    };
    let trimed_photo = _.trim(_.get(profileData, 'employee_photo'));
    const showModal = () => {
        setVisible(true);
    };
    const showBankModal = () => {
        setBankVisible(true);
    };
    const hideBankModal = () => {
        setBankVisible(false);
    };
    const showaboutModal = () => {
        setaboutVisible(true);
    };
    const hideModal = () => {
        setVisible(false);
    };

    const showModalprofile = () => {
        setProfileVisible(true);
    };

    const hideModalprofile = () => {
        setProfileVisible(false);
    };
    const hideModalabout = () => {
        setaboutVisible(false);
    };

    const handleQualificationChange = (e) => {
        //setQualification(e.target.value);
    };

    const handleCollegeNameChange = (e) => {
        //setCollegeName(e.target.value);
    };
    const otpSend = async () => {
        let data = {
            "phone": "+91" + phone
        }
        try {
            const response = await sendOtp(data);
            if (response?.status == 200) {
                setOtpSent(true)
                setOtpResponse(response?.data?.details)
            } else {

            }
        } catch (err) {
            console.log('error', err)
            handleSnackbarClick(true, { type: 'error', msg: 'Something Went Wrong' })
        }
    }
    const handleChange = (fieldName) => (event) => {
        // Update the value of the specified field in the component's state
        if (fieldName === 'phone') {
            // setPhone('91')
            const value = event.target.value;
            if (value?.length <= 10) {
                setPhone(value);
            }
            // setPhone(event.target.value);

        }
        if (fieldName === 'otp') {
            setOtp(event);
        }
    };

    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };

    const otpVerify = async () => {

        let data = {
            "phone": otpResponse.to,
            "otp": otp,
            "serviceSid": otpResponse.serviceSid
        }
        try {
            const response = await verifyOtp(data);
            if (response?.status == 200) {
                // setNumberVerified(true)
                if (response?.data?.details?.valid) {
                    setNumberVerified(response?.data?.details?.valid)
                } else {
                    // alert("Enter Valid OTP")
                    handleSnackbarClick(true, { type: 'error', msg: 'Enter Valid OTP' })
                    setOtpSent(false)
                }

            } else {

            }
        } catch (err) {

            handleSnackbarClick(true, { type: 'error', msg: 'Something Went Wrong' })
        }
    }

    const handleDateChange = (date) => {
        const inputDate = new Date(date);
        const formattedDate = `${inputDate.getFullYear()}-${(inputDate.getMonth() + 1).toString().padStart(2, '0')}-${inputDate.getDate().toString().padStart(2, '0')}`;
        setdob(formattedDate);
    };
    const shouldDisableDate = (date) => {
        return date.isAfter(defaultDate, 'day');
    };
    const handleUpdateBank = async () => {


        let bankDetailsData = {
            "account_number": accountNumber,
            "account_type": accountType,
            "account_holder_name": accountHolderName,
            "IFSC_code": ifscCode,
        };
        setloading(true)
        try {
            const response = await employeeBankUpdate(bankDetailsData)
            if (response?.status === 200) {        
                setloading(false)
                setBankVisible(false)
                fetchJobData()            
            }

        } catch (error) {
            if (error?.response?.data?.errors[0]?.rules[0]?.message) {
                setSrrorMessage(error?.response?.data?.errors[0]?.rules[0]?.message);
                setErrorPopupOpen(true);
                setloading(false)

            } else {
                setSrrorMessage("somthing went wrong");
                setErrorPopupOpen(true);
                setloading(false)
            }

        }



    }

    const handleupdate = async () => {
        let body = {}
        body.first_name = firstname
        body.last_name = lastname
        body.pincode = pincode
        body.dob = dob
        body.language_known = language
        body.gender = gender
        body.state = state
        body.email = email
        body.city = city
        body.address = address
        body.aadhar_number = aadharnum
        body.education_qualification = education
        body.college_name = college
        body.University = university
        body.year_of_passing = yearoafpassing
        body.field_of_education = fieldeducation
        body.experience_category = experincecategory
        body.about_you = about
        body.experience_list = checkedItems
        body.phone = phone

        // let update = await employeeProfilesUpdate(body)
        // console.log("HANDSUPDATE", update);
        setloading(true)
        try {
            const response = await employeeProfilesUpdate(body)
            if (response?.status === 200) {
                setphotoload(true)
                setloading(false)
                setVisible(false)
                fetchJobData()
                setProfileVisible(false);
                setaboutVisible(false);

            }

        } catch (error) {
            if (error?.response?.data?.errors[0]?.rules[0]?.message) {
                setSrrorMessage(error?.response?.data?.errors[0]?.rules[0]?.message);
                setErrorPopupOpen(true);
                setloading(false)

            } else {
                setSrrorMessage("somthing went wrong");
                setErrorPopupOpen(true);
                setloading(false)
            }

        }

        if (photostatus === true) {
            try {
                let userid = profileData.id
                const formData = new FormData();
                formData.append('photo', selectedFile);
                const updatePHOTO = await employeePhotosUpdate(formData, userid)
                if (updatePHOTO?.status === 200) {
                    // setphotoload(true)
                    fetchJobData()
                }

                //setloading(true)
            } catch (err) {
                console.log("error", err);
            }
        }
        // setloading(true)
    }

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        setSelectedFile(file);
        setphotostatus(true)
        setPreviewImage(URL.createObjectURL(file));
    };
    const handleTextInput = (event) => {
        const regex = /^[A-Za-z]+$/;
        if (!regex.test(event.target.value)) {
            event.target.value = event.target.value.replace(/[^A-Za-z]+/g, '');
        }
    };





    const validateEducationQualification = (event) => {
        const inputValue = event.target.value;
        // const containsNumbers = /\d/.test(inputValue);

        if (inputValue) {
            seteducationError(VALIDATION_MSG.education);
        } else {
            seteducationError('')
        }
        seteducation(inputValue);
    };
    const validatecollege = (event) => {

        const inputValue = event.target.value;
        const containsNumbers = /\d/.test(inputValue);

        if (containsNumbers) {
            setcollegeError(VALIDATION_MSG.instite);
        } else {
            setcollegeError('')
        }
        // setInstitute(inputValue);
    };
    const validateuniversity = (event) => {

        const inputValue = event.target.value;
        const containsNumbers = /\d/.test(inputValue);

        if (containsNumbers) {
            setuniversityError(VALIDATION_MSG.university);
        } else {
            setuniversityError('')
        }
        setuniversity(inputValue);
    };
    const validatefield = (event) => {

        const inputValue = event.target.value;
        const containsNumbers = /\d/.test(inputValue);

        if (containsNumbers) {
            setfieldError(VALIDATION_MSG.educationfeild);
        } else {
            setfieldError('')
        }
        setfieldeducation(inputValue);
    };
    const validateyearofpssing = (event) => {

        const inputValue = event.target.value;
        const containsNumbers = /\D/g.test(inputValue);

        if (containsNumbers) {
            setyearError(VALIDATION_MSG.yearpusing);
        } else {
            setyearError('')
        }
        setyearoafpassing(inputValue);
    };





    return (
        <div className="userProfilePage">
            {loading && (
                <div className="SiginLoader">
                    <Spin spinning={true} />
                </div>
            )}
            <Grid container spacing={2.5} justifyContent={'center'}>
                <Grid item xs={12} sm={12} lg={8}>
                    <div className="profileMainCard">
                        <div className="profileEditCard">
                            <div className="userInfoContainer">
                                {/* {_.get(photo, 'details.photo')?.replace(/\s/g, "") == "" ?
                                    <img
                                        src={require('../../assets/images/default_logo.png')}
                                        className="userImg"
                                    /> :
                                    <img
                                        src={`${_.get(photo, 'meta.employee_photo.path')}/${_.get(photo, 'meta.employee_photo.folder')}/${_.get(photo, 'meta.employee_photo.sizes.medium')}/${_.get(photo, 'details.employee_photo')}`}
                                        className="userImg"
                                    // alt="User Image"
                                    />
                                } */}



                                {profileData?.employee_photo ? <img src={`${photo?.meta?.employee_photo?.path}/${photo?.meta?.employee_photo?.folder}/${photo?.meta?.employee_photo?.sizes?.medium}/${profileData?.employee_photo}`} className="userImg" />
                                    : <img
                                        src={require('../../assets/images/default-logo1.png')}
                                        className="userImg"
                                    />}

                                <div className="userInfoContent">
                                    <div className="userName">{firstname}</div>
                                    <div className="infoContent">
                                        {about}
                                    </div>
                                </div>
                                <Button title='Edit' onClick={showaboutModal} className="editBtn">
                                    <span>Edit</span>
                                    <img title='Edit' src={require('../../assets/images/edit-icon-2.png')} />
                                </Button>
                            </div>
                            <div className="userSkilsContainer">
                                {checkedItems?.length > 0 ? checkedItems.map((e, i) => {
                                    return (
                                        <div key={i} className="userSkilSet">
                                            <strong>{e}</strong>
                                        </div>
                                    )
                                }) : null}

                            </div>
                        </div>
                        <div className="profileEditCard">
                            <div className="titleContainer">
                                <div className="title">Profile</div>
                                <Button title='Edit' className="editBtn" onClick={showModalprofile}  >
                                    <span>Edit</span>
                                    <img title='Edit' src={require('../../assets/images/edit-icon-2.png')} />
                                </Button>
                            </div>
                            <Modal

                                visible={aboutvisible}
                                onOk={hideModalabout}
                                onCancel={hideModalabout}
                                className="profile-edit-modal PartTimerRegistrationPage"
                                width={800}
                                footer={[
                                    <Button key="cancel" className="cancel-btn" onClick={hideModalabout}>
                                        Cancel
                                    </Button>,
                                    <Button key="update" className="update-btn" onClick={handleupdate} type="primary">
                                        Update
                                    </Button>
                                ]}
                            >
                                <div className="scrollableContainer registrationWizard">
