                            className="form-control"
                            value={accountHolderName}
                            // onChange={(e) => setAccountHolderName(e.target.value.replace(/[^A-Za-z ]/ig, ''))}
                            onChange={(e) => {
                                const inputVal = e.target.value;
                                if (inputVal.length <= 30) {
                                    setAccountHolderName(inputVal);
                                }
                            }}
                            InputProps={{
                                type: 'text',
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={12}>
