                            {/* <Button disableRipple>Find your IFSC code</Button> */}
                            <TextField
                                id="ifsc_code"
                                label="IFSC code"
                                className="form-control"
                                value={ifscCode}
                                onChange={(e) => {
                                    const inputVal = e.target.value;
                                    if (inputVal.length <= 30) {
                                        setIfscCode(inputVal);
                                    }
                                }}
                                 InputProps={{
                                    type: 'text',
                                }}
                            />
                        </div>
                    </Grid>
                    {/* <Grid item xs={12} sm={12}>
                        <div className="WizardNextStep">
                            <Button variant="contained" className="WizardNextBtn" onClick={handleNext}>
                                <span>Next</span>
                                <EastIcon />
                            </Button>

                            <Button variant="outlined" className="WizardBackBtn" onClick={onBack}>
                                <EastIcon />
                                <span>Back</span>
                            </Button>
                        </div>
                    </Grid> */}
                </Grid>
            </form>
                                </div>
                            </div>
                        </Modal>
                        <Button onClick={handleLogout} className="logoutBtn">
                            <img src={require('../../assets/images/logout.png')} />
                            <span>Logout</span>
                        </Button>
                    </div>
                </Grid>
                <Grid item xs={12} sm={6} lg={4}>
                    <div className="postedJobsSideBannerContainer">
                        <div className="postedJobsSideBanner">
                            <ProfileBanner />
                        </div>
                        <div className="PostJobBannerDays">
                            {validity ? (
                                <>
                                    <div className="PostJobBannerTitle">Your Subscription Pack Ends Within</div>
                                    <div className="PostJobBannerDays">
                                        {validityload ? (
                                            <>
                                                <Spin spinning={true} />
                                                <div style={{ fontSize: 22 }}>Currently your subscription not completed</div>
                                            </>
                                        ) : (
                                            <>
                                                {validity} Days
                                            </>
                                        )}
                                    </div>
                                </>
                            ) : (
                                <>
                                    {validityload ? (
                                        <Spin spinning={true} />
                                    ) : (
                                        <>
                                            <div style={{ fontSize: 22 }}>Currently your subscription not completed</div>
                                        </>
                                    )}
                                </>
                            )}
                        </div>


                        <div className="PostJobBannerCheckListContainer">
                            <div className="PostJobBannerCheckListSet">
                                <img src={require('../../assets/images/check-icon.png')} alt="" />
                                <div className="checkListContent">
                                    <strong>Access to more job opportunities</strong>
                                    <span>Subscribing to a job portal often gives employees
                                        access to a larger pool of job opportunities than they
                                        might find on their own.</span>
                                </div>
                            </div>
                            <div className="PostJobBannerCheckListSet">
                                <img src={require('../../assets/images/check-icon.png')} alt="" />
                                <div className="checkListContent">
                                    <strong>Customized job alerts</strong>
                                    <span>Many job portals offer customized job alerts based
                                        on the employee's preferences and qualifications,
                                        which can save time and effort in job searching.</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </Grid>
            </Grid>
            {openSnackbar ? <CustomSnackBar
                openSnackbar={openSnackbar}
                snackBarMessage={customMsg?.msg}
                status={customMsg?.type}
                handleSnackbarClick={handleSnackbarClick}
            /> : null}
            <ErrorPopup />
        </div>
    )
}
