import React, { useEffect, useState, } from 'react';
import { Grid, TextField, Button, FormControl, InputLabel, Select, MenuItem, TextareaAutosize, Autocomplete, Chip, FormControlLabel, RadioGroup, Radio } from '@mui/material';
import { ReactComponent as PostJobBanner } from '../../../assets/images/post-job-banner.svg';
import EastIcon from '@mui/icons-material/East';
import { DemoContainer, DemoItem } from '@mui/x-date-pickers/internals/demo';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import '../employerStyle.scss'
import { PostJobCreate, PostJobUpdate, PostJobView, JobCategoryTypes, JobQualification, JobCertification, JobSkillSet, createNewSkill } from '../../../sevices/job';
import { useLocation } from 'react-router-dom';
import axios from "axios";
import { ENV_CONFIG, VALIDATION_MSG } from '../../../constant';
import { useNavigate } from "react-router-dom";
import dayjs from 'dayjs';
import { Space, TimePicker } from 'antd';
import Loader from '../../Loader';
import CustomSnackBar from '../../../shared/snackBar';
import { isEmptyObj } from '../../../utils/genericUtilities';
import StateJson from '../../../shared/json/state.json';
import { ReactComponent as UncheckIcon } from '../../../assets/images/radio-uncheck.svg';
import { ReactComponent as CheckIcon } from '../../../assets/images/radio-check.svg';
import moment from 'moment';
const PostJob = (props) => {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [jobTitle, setJobTitle] = useState('');
    const [jobtimingslot, setJobTimingSlot] = useState('');
    const [workingTimg, setWorkingTiming] = useState(moment());
    const [qualification, setQualification] = useState('');
    const [experince, setExperince] = useState('');
    const [state, setState] = useState('');
    const [city, setCity] = useState('');
    const [jobDescription, setJobDescription] = useState('');
    const [skils, setskils] = useState([]);
    const [gender, setGender] = useState('');
    const [jobaddress, setJobAddress] = useState('');
    const [zipcode, setZipcode] = useState('');
    const [selectedDate, setSelectedDate] = useState('');
    const [jobTitleError, setJobTitleError] = useState('');
    const [category, setCategory] = useState('');
    const [certification, setCertification] = useState('');
    const [selectedcertification, setSelectedCertification] = useState('');
    const [selectedcategory, setSelectedCategory] = useState('');
    const [selectedqualification, setSelectedQualification] = useState('');
    const [jobSkills, setJobSkills] = useState([]);
    const [skillsValue, setSkillsValue] = useState([]);
    const Filter = require('bad-words');
    const badWordLists = new Filter({ regex: /\*|\.|$/gi });
    const [badWords, setBadWords] = useState([]);
    const [selectedSkills, setSelectedSkills] = useState('');
    const [newSkillValues, setNewSkillValues] = useState('');
    const [errors, setErrors] = useState({});
    const [discError, setDiscError] = useState('')
    const [categoryError, setcategoryError] = useState('')
    const [timingError, settimingError] = useState('')
    const [experinceError, setexperinceError] = useState('')
    const [skillError, setskillError] = useState('')
    const [addressError, setaddressError] = useState('')
    const [dressCode, setDressCode] = useState('')
    const [pickpDrop, setpickpDrop] = useState('')
    const [update, setupdate] = useState(false)
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const viewid = searchParams.get('viewid');
    const currentDate = dayjs();

    const handleSelectedQualification = (selectedOption) => {
        const value = selectedOption?.name;
        setSelectedQualification(value)

        const newErrors = {};
        if (!value) {
            newErrors.qualification = 'Qualification is required';
        }
        setcategoryError(newErrors)
    };

    const handleSelectcategory = (selectedOption) => {
        let value = selectedOption?.name
        setSelectedCategory(selectedOption?.name)
        const newErrors = {};
        if (!value) {
            newErrors.category = 'Category is required';
        }
        setcategoryError(newErrors)
    };

    const handleSelectcertification = (selectedOption) => {
        const value = selectedOption?.name
        const newErrors = {}
        if (!value) {
            newErrors.certification = 'Certification is required';
        }
        setErrors(newErrors);
        setSelectedCertification(value)
    };

    const currentMoment = dayjs();
    const fourHoursFromNow = dayjs(currentMoment).add(4, 'hours');

    const isTimeDisabled = (time) => {
        if (selectedDate && selectedDate.isSame(currentDate, 'day')) {
            return time.isBefore(fourHoursFromNow);
        }
        return false;
    };

    const disabledHours = () => {
        const hours = [];
        if (selectedDate && selectedDate.isSame(currentDate, 'day')) {
            for (let hour = currentMoment.hour(); hour < 24; hour++) {
                const timeToCheck = dayjs().hour(hour).minute(0);

                if (isTimeDisabled(timeToCheck)) {
                    hours.push(hour);
                }
            }
        }
        return hours;
    };

    const disabledMinutes = (selectedHour) => {
        const minutes = [];
        if (selectedDate && selectedDate.isSame(currentDate, 'day') && selectedHour === currentMoment.hour()) {
            for (let minute = currentMoment.minute(); minute < 60; minute++) {
                const timeToCheck = dayjs().hour(selectedHour).minute(minute);
                if (isTimeDisabled(timeToCheck)) {
                    minutes.push(minute);
                }
            }
        }
        return minutes;
    };

    const handleTimeChange = (time, timeString) => {

        console.log('Selected Time:', timeString);
        setWorkingTiming(timeString);
    };

    const handleDateChange = (date) => {
        const inputDate = new Date(date);
        const formattedDate = `${inputDate.getFullYear()}-${(inputDate.getMonth() + 1).toString().padStart(2, '0')}-${inputDate.getDate().toString().padStart(2, '0')}`;
        const selectedDate = dayjs(formattedDate);
        console.log("SSDADADAA", selectedDate, formattedDate);

        setSelectedDate(selectedDate);
    };

    const handleJobTitleChange = (event) => {
        let value = event.target.value
        if (!value) {
            setJobTitleError(VALIDATION_MSG.titlerequired);
        } else {
            setJobTitleError('');
        }
        setJobTitle(value.replace(/[^A-Za-z0-9 ]/ig, ''));
    }

    const handleZipcode = (event) => {
        const Zipcode = event.target.value;
        const sanitizedInput = Zipcode.replace(/[^0-9]/g, '').slice(0, 10);
        setZipcode(sanitizedInput);
    }

    const handleAddress = async (event) => {
        try {
            let value = event.target.value;
            const newErrors = {}

            setJobAddress(value.replace(/[^A-Za-z0-9,./ ]/ig, ''))
            const response = await axios.get(`https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(value)}&key=${ENV_CONFIG.GOOGLE_API_KEY}`);
            const results = response?.data?.results;

            if (results && results?.length > 0) {
                const addressComponents = results[0]?.address_components;

                const stateComponent = addressComponents.find(component => {
                    return component.types.includes("administrative_area_level_1");
                });
                const cityComponent = addressComponents.find(component => {
                    return component.types.includes("locality");
                });
                const postalCodeComponent = addressComponents.find(component => {
                    return component.types.includes("postal_code");
                });

                const state = stateComponent ? stateComponent.long_name : '';
                const city = cityComponent ? cityComponent.long_name : '';
                const zipCode = postalCodeComponent ? postalCodeComponent.long_name : '';

                setState(state)
                setCity(city)
                setZipcode(zipCode)

                if (!state) {
                    setaddressError('State is required');
                }
            } else {
            }
        } catch (error) {
            console.error("Error occurred:", error);
        }
    }

    useEffect(() => {
        console.log("StateJson");
        console.log("StateJson", StateJson);
        if (viewid) {
            HandleViewjob();
        }
        GetCategory();
        GetCertificationList();
        GetQualificationList();
        getJobSkill();
        const badWordList = badWordLists?.list
        setBadWords(badWordList)
    }, [viewid]);

    useEffect(() => {
        if (jobSkills?.length && update === true) {
            const employeeSkills = skils.filter(skill => jobSkills.some(jobSkill => jobSkill.name === skill));
            setSkillsValue(employeeSkills);
        }
    }, [jobSkills, skils]);
    const GetCategory = async (event) => {
        const body = {
            status: 1
        }
        const response = await JobCategoryTypes(body);
        setCategory(response?.data?.items)
    };
    const GetCertificationList = async (event) => {
        const body = {
            status: 1
        }
        const response = await JobCertification(body);
        setCertification(response?.data?.items)
    };
    const GetQualificationList = async (event) => {
        const body = {
            status: 1
        }
        const response = await JobQualification(body);
        setQualification(response?.data?.items)
    };
    const getJobSkill = async (event) => {
        const body = {
            status: 1
        }
        const response = await JobSkillSet(body);
        setJobSkills(response?.data?.items)
    };

    const createNewSkills = async (newSkills) => {
        const body = {
            names: newSkills,
            status: 1
        }
        try {
            const response = await createNewSkill(body);
        }
        catch (errors) {
            handleSnackbarClick(true, { type: 'error', msg: errors?.response?.data?.message ? errors?.response?.data?.message : errors?.response?.data?.errors[0]?.rules[0]?.message })
        }

    };

    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };

    const HandleViewjob = async () => {
        setLoading(true);
        const params = { id: viewid }
        try {


            const response = await PostJobView({ params: params });
            let ViewData = response?.data?.details
            setupdate(true);
            setJobTitle(ViewData.title)
            setJobTimingSlot(ViewData.job_timing_slot);
            setSelectedDate(ViewData?.working_date)
            setJobDescription(ViewData.job_description)
            setSelectedQualification(ViewData.required_qualifications)
            const defaultTime = dayjs(ViewData.working_timing, 'HH:mm');
            setWorkingTiming(defaultTime)
            setCity(ViewData.city)
            setState(ViewData.state)
            setZipcode(ViewData.zip_code)
            setExperince(ViewData.experience)
            setGender(ViewData.gender)
            const skilValue = ViewData?.skills
            const skillArray = skilValue.split(',');
            setskils(skillArray)
            setJobAddress(ViewData.address)
            setSelectedCategory(ViewData?.category_type);
            setSelectedCertification(ViewData.certification_required);
        } catch (error) {
            console.log("error", error);
        } finally {
            setLoading(false);
        }
    }

    const handleButton = async () => {
        setLoading(true)
        const inputDate = new Date(selectedDate);
        const formattedDate = `${inputDate.getFullYear()}-${(inputDate.getMonth() + 1).toString().padStart(2, '0')}-${inputDate.getDate().toString().padStart(2, '0')}`;

        const time24HourFormat = moment(workingTimg, 'h:mm A').format('HH:mm');
        if (!jobTitle) {
            setLoading(false)
            setJobTitleError('Job title is required');
            return;
        }
        if (!jobDescription) {
            setLoading(false)
            setDiscError('Description is required');
            return;
        }

        let body = {};
        body.title = jobTitle;
        body.job_timing_slot = jobtimingslot;
        body.working_date = formattedDate;
        body.working_timing = time24HourFormat;
        body.job_description = jobDescription;
        body.required_qualifications = selectedqualification;
        body.city = city;
        body.state = state;
        body.zip_code = zipcode;
        body.experience = experince;
        body.gender = gender;
        body.pickup_drop = pickpDrop;
        body.address = jobaddress;
        body.category_type = selectedcategory;
        body.certification_required = selectedcertification;
        body.dress_code = dressCode;
        body.skills = skillsValue.join(',');

        if (viewid) {
            try {
                let params = { id: viewid }
                const updateJobPost = await PostJobUpdate(params, body);
                if (updateJobPost.status === 200) {
                    handleSnackbarClick(true, { type: 'success', msg: "Job Post Successfully Updated" })

                    setLoading(false)
                    newSkillValues?.length && createNewSkills(newSkillValues)
                    setTimeout(() => {
                        navigate("/jobs")
                    }, 1000);
                    // navigate("/jobs")
                }
            } catch (error) {
                setLoading(false)
                handleSnackbarClick(true, { type: 'error', msg: error?.response?.data?.message ? error?.response?.data?.message : error?.response?.data?.errors[0]?.rules[0]?.message })
                console.error('PostJob Error:', error);
            }
        } else {
            try {
                const createJobPost = await PostJobCreate(body);
                console.log("CREACAVACVS", createJobPost);
                if (createJobPost.status === 200) {
                    newSkillValues?.length && createNewSkills(newSkillValues)
                }
                setLoading(false)
                handleSnackbarClick(true, { type: 'success', msg: "Job Post Successfully Added" })
                setTimeout(() => {
                    navigate(`/jobPostPayment?value=${createJobPost.data.details.id}`);
                }, 1000);

            } catch (error) {
                setLoading(false)
                console.error('PostJob ErrorGSDFGDS:', error);
                handleSnackbarClick(true, { type: 'error', msg: error?.response?.data?.message ? error?.response?.data?.message : error?.response?.data?.errors[0]?.rules[0]?.message })
            }
        }

    };
    const handleChangestate = (event) => {
        setState(event.target.value);
    }
    const handleChangeCity = (event) => {
        setCity(event.target.value);
    }
    const handleSkillsChange = (event, value) => {

        const uniqueSkills = value
            .map(skill => skill.toLowerCase())
            .filter((skill, index, self) => self.indexOf(skill) === index);
        const containsBadWord = uniqueSkills.findIndex(skill => {
            return badWords.some(badWord => skill.includes(badWord.toLowerCase()));
        });
        if (containsBadWord > -1) {
            uniqueSkills.splice(containsBadWord, 1);
        }

        setSkillsValue(uniqueSkills)
        setSelectedSkills(uniqueSkills);
        const valuesNotInSkillSet = uniqueSkills.filter(skill => !jobSkills.map(s => s.name.toLowerCase()).includes(skill));
        setNewSkillValues(valuesNotInSkillSet);
    };

    const handleJobDescriptionChange = (event) => {
        let value = event.target.value
        const newErrors = {};
        setJobDescription(value);
        if (!value) {
            newErrors.description = VALIDATION_MSG.descriptionrequired
        }
        setDiscError(newErrors)
    }

    const validateForm = () => {
        const newErrors = {};
        setErrors(newErrors);

        return Object.keys(newErrors)?.length === 0;
    };

    const handleTimingBlur = () => {
        validateForm();
    };

    const handletimes = () => {
        if (!jobtimingslot) {
            settimingError("Job timing is required")
        } else {
            settimingError('')
        }
    }
    const handleexperince = () => {
        if (!experince) {
            setexperinceError("Experince  is required")
        } else {
            setexperinceError('')
        }
    }

    return (

        <div className="dashboardPageContainer">
            <div className="mainTitle">{viewid ? "Update Post Job" : "Post Job"}</div>
            <div className="fullWidth">
                <Grid container spacing={5}>
                    <Grid item xs={12} sm={8}>
                        <div className="WizardFormContainer">
                            <form>
                                <Grid container spacing={5}>
                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            id="job_title"
                                            label="Job title"
                                            value={jobTitle}
                                            onBlur={handleJobTitleChange}
                                            className="form-control"
                                            error={!!jobTitleError}
                                            helperText={jobTitleError}
                                            InputProps={{
                                                type: 'text',
                                            }}
                                            inputProps={{
                                                maxLength: 50
                                            }}
                                            onChange={(e) => {
                                                if (e.target.value.length <= 50) {
                                                    handleJobTitleChange(e)
                                                }
                                            }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth>
                                            <InputLabel id="type_of_company">Job timing slot</InputLabel>
                                            <Select
                                                error={!!timingError}
                                                helperText={timingError}
                                                onBlur={handletimes}
                                                labelId="type_of_company"
                                                value={jobtimingslot}
                                                id="demo-simple-select"
                                                className="form-control"

                                                label="Job timing slot"
                                                onChange={(e) => setJobTimingSlot(e.target.value)}
                                            >
                                                <MenuItem value={"04:00 hrs"}>4 hrs</MenuItem>
                                                <MenuItem value={"06:00 hrs"}>6 hrs</MenuItem>
                                                <MenuItem value={"08:00 hrs"}>8 hrs</MenuItem>
                                                <MenuItem value={"10:00 hrs"}>10 hrs</MenuItem>
                                                <MenuItem value={"12:00 hrs"}>12 hrs</MenuItem>
                                                <MenuItem value={"14:00 hrs"}>14 hrs</MenuItem>
                                                <MenuItem value={"16:00 hrs"}>16 hrs</MenuItem>
                                                <MenuItem value={"18:00 hrs"}>18 hrs</MenuItem>

                                            </Select>
                                            {timingError && <p className='errorMsg'>{timingError}</p>}
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>

                                        <LocalizationProvider dateAdapter={AdapterDayjs}>
                                            <DatePicker
                                                format="DD/MM/YYYY"
                                                value={selectedDate ? dayjs(selectedDate) : null}
                                                label="Working Date"
                                                onChange={handleDateChange}
                                                minDate={currentDate}

                                            />
                                        </LocalizationProvider>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <Space wrap style={{ width: '100%' }}>

                                            <TimePicker
                                                style={{ width: '100%' }}
                                                use12Hours
                                                format="h:mm a"
                                                onChange={handleTimeChange}
                                                showNow={false}
                                                disabledHours={disabledHours}
                                                disabledMinutes={disabledMinutes}
                                                renderExtraFooter={() => null}
                                            />
                                        </Space>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth>
                                            <Autocomplete
                                                disablePortal
                                                id="combo-box-demo"
                                                error={!!categoryError}
                                                helperText={categoryError}
                                                //onBlur={handleTimingBlur}
                                                options={category}
                                                value={category && category?.find((option) => option?.name === selectedcategory)}
                                                onChange={(event, newValue) => handleSelectcategory(newValue)}
                                                className="form-control"
                                                getOptionLabel={(option) => option.name}
                                                renderInput={(params) => <TextField {...params} label="Category" />}
                                            />
                                            {categoryError?.category && <p className='errorMsg'>{categoryError?.category}</p>}

                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth>
                                            <Autocomplete
                                                error={!!errors?.certification}
                                                helperText={errors?.certification}
                                                // onBlur={handleTimingBlur}
                                                disablePortal
                                                id="combo-box-demo"
                                                options={certification}
                                                value={certification && certification?.find((option) => option?.name === selectedcertification)}
                                                className="form-control"
                                                onChange={(event, newValue) => handleSelectcertification(newValue)}
                                                getOptionLabel={(option) => option.name}
                                                renderInput={(params) => <TextField {...params} label="Certification" />}
                                            />
                                            {errors?.certification && <p className='errorMsg'>{errors?.certification}</p>}
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={12}>
                                        <TextareaAutosize
                                            minRows={8}
                                            maxRows={8}
                                            error={!!discError}
                                            helperText={discError}
                                            onBlur={handleJobDescriptionChange}
                                            //disabled={!workingTimg}
                                            value={jobDescription}
                                            className="form-control"
                                            placeholder="Job description"
                                            onChange={handleJobDescriptionChange}
                                        />
                                        {discError?.description && <p className='errorMsg'>{discError?.description}</p>}
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth>
                                            <Autocomplete
                                                disablePortal
                                                error={!!categoryError.qualification}
                                                helperText={categoryError.qualification}
                                                //onBlur={handleTimingBlur}
                                                id="combo-box-demo"
                                                options={qualification}
                                                value={qualification && qualification?.find((option) => option?.name === selectedqualification)}
                                                onChange={(event, newValue) => handleSelectedQualification(newValue)}
                                                className="form-control"
                                                getOptionLabel={(option) => option?.name}
                                                renderInput={(params) => <TextField {...params} label="Required Qualification" />}
                                            />
                                            {categoryError?.qualification && <p className='errorMsg'>{categoryError?.qualification}</p>}
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth>
                                            <InputLabel id="type_of_company">Experience</InputLabel>
                                            <Select
                                                labelId="type_of_company"
                                                id="demo-simple-select"
                                                className="form-control"
                                                error={!!experinceError}
                                                helperText={experinceError}
                                                onBlur={handleexperince}
                                                value={experince}
                                                label="Experience"
                                                onChange={(e) => setExperince(e.target.value)}
                                            >
                                                <MenuItem value={"No Experience required"}>No Experience required</MenuItem>
                                                <MenuItem value={"2 years"}>2 Years</MenuItem>
                                                <MenuItem value={"3 years"}>3 Years</MenuItem>
                                                <MenuItem value={"5 years"}>5 Years</MenuItem>
                                            </Select>
                                            {experinceError && <p className='errorMsg'>{experinceError}</p>}
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={12}>
                                        <Autocomplete
                                            multiple
                                            id="tags-filled"
                                            // options={jobSkills?.map(skill => skill.name)}
                                            options={[...jobSkills.map((skill) => skill.name), ...selectedSkills]}
                                            freeSolo
                                            value={skillsValue}
                                            onChange={handleSkillsChange}
                                            className="form-control"
                                            renderInput={(params) => (
                                                <TextField {...params} variant="outlined" label="Skills" />
                                            )}
                                            filterSelectedOptions
                                        />
                                    </Grid>
                                    <Grid item xs={12} sm={12}>
                                        <TextField
                                            id="dress code"
                                            label="Dress Code"
                                            value={dressCode}
                                            //disabled={!skils}
                                            className="form-control"
                                            InputProps={{
                                                type: 'text',
                                            }}
                                            inputProps={{
                                                maxLength: 50
                                            }}
                                            onChange={(e) => setDressCode(e.target.value.replace(/[^A-Za-z ]/ig, ''))}

                                        />
                                    </Grid>
                                    <Grid item xs={12} sm={12}>
                                        <label style={{ marginLeft: '10px', fontSize: '14px', fontWeight: '400', color: '#605757' }} >Pickup/Drop</label>
                                        <RadioGroup
                                            aria-labelledby="demo-radio-buttons-group-label"
                                            defaultValue="fresher"
                                            name="radio-buttons-group"
                                            className="picukpradio"
                                            value={pickpDrop}
                                            onChange={(e) => setpickpDrop(e.target.value)}
                                        >
                                            <FormControlLabel value="true" control={<Radio icon={<UncheckIcon />}
                                                checkedIcon={<CheckIcon />} />} label="Yes" />
                                            <FormControlLabel value="false" control={<Radio icon={<UncheckIcon />}
                                                checkedIcon={<CheckIcon />} />} label="No" />
                                        </RadioGroup>
                                    </Grid>
                                    <Grid item xs={12} sm={12}>
                                        <TextField
                                            id="first_name"
                                            label="Address"
                                            value={jobaddress}
                                            //disabled={!skils}
                                            className="form-control"
                                            InputProps={{
                                                type: 'text',
                                            }}
                                            onChange={(e) => {
                                                if (e.target.value.length <= 100) {
                                                    handleAddress(e)
                                                }
                                            }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth>
                                            <InputLabel id="type_of_company">State</InputLabel>
                                            <Select
                                                labelId="State"
                                                id="demo-simple-select"
                                                className="form-control"
                                                label="State"
                                                value={state}
                                                onChange={handleChangestate}
                                            >
                                                {Object.keys(StateJson).map((stateName) => (

                                                    <MenuItem key={stateName} value={stateName}>
                                                        {stateName}
                                                    </MenuItem>
                                                ))}

                                            </Select>
                                        </FormControl>

                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth>
                                            <InputLabel id="type_of_company">City</InputLabel>
                                            <Select
                                                labelId="City"
                                                id="demo-simple-select"
                                                className="form-control"
                                                label="City"
                                                value={city}
                                                onChange={handleChangeCity}
                                            >
                                                {StateJson[state] && StateJson[state].map((cityName) => (

                                                    <MenuItem key={cityName} value={cityName}>
                                                        {cityName}
                                                    </MenuItem>
                                                ))}

                                            </Select>

                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            id="first_name"
                                            label="Pin code"
                                            value={zipcode}
                                            className="form-control"
                                            InputProps={{
                                                type: 'number',
                                            }}
                                            onChange={handleZipcode}
                                            inputProps={{
                                                type: 'tel',
                                                maxLength: 6,
                                            }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} sm={6}>

                                        <FormControl fullWidth>
                                            <InputLabel id="type_of_company">Gender</InputLabel>
                                            <Select
                                                labelId="type_of_company"
                                                id="demo-simple-select"
                                                className="form-control"
                                                label="Gender"
                                                value={gender}
                                                onChange={(e) => setGender(e.target.value)}
                                            >
                                                <MenuItem value={"male"}>Male</MenuItem>
                                                <MenuItem value={"female"}>Female</MenuItem>
                                                <MenuItem value={"notapplicable"}>Not Applicable</MenuItem>
                                            </Select>
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={12}>
                                        <div className="WizardNextStep">
                                            <Button onClick={handleButton} variant="contained" className="WizardNextBtn">
                                                <span>{update === true ? 'Update Job' : 'Post a job'}</span>
                                                <EastIcon />
                                            </Button>
                                        </div>
                                    </Grid>
                                </Grid>
                            </form>
                        </div>
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        <div className="postedJobsSideBannerContainer">
                            <div className="postedJobsSideBanner">
                                <PostJobBanner />
                            </div>
                            <div className="PostJobBannerTitle">
                                For posting a job pay 100 per job
                                the benefits are
                            </div>
                            <div className="PostJobBannerCheckListContainer">
                                <div className="PostJobBannerCheckListSet">
                                    <img src={require('../../../assets/images/check-icon.png')} alt="" />
                                    <span>
                                        Posting job openings on job portals can
                                        increase the visibility of the job.
                                    </span>
                                </div>
                                <div className="PostJobBannerCheckListSet">
                                    <img src={require('../../../assets/images/check-icon.png')} alt="" />
                                    <span>
                                        We offers a cost-effective way to advertise
                                        job openings, especially for small and
                                        medium-sized businesses.
                                    </span>
                                </div>
                                <div className="PostJobBannerCheckListSet">
                                    <img src={require('../../../assets/images/check-icon.png')} alt="" />
                                    <span>
                                        We allow you to target your job posting to
                                        specific audiences based on location,
                                        industry, experience, and other criteria.
                                    </span>
                                </div>
                                <div className="PostJobBannerCheckListSet">
                                    <img src={require('../../../assets/images/check-icon.png')} alt="" />
                                    <span>
                                        Part Timer attract candidates who are a
                                        better fit for the company's culture and
                                        values.
                                    </span>
                                </div>
                            </div>
                        </div>
                    </Grid>
                </Grid>
            </div>

            <Loader loading={loading} />
            {openSnackbar ? <CustomSnackBar
                openSnackbar={openSnackbar}
                snackBarMessage={customMsg?.msg}
                status={customMsg?.type}
                handleSnackbarClick={handleSnackbarClick}
            /> : null}

        </div>
    );
}
export default PostJob;
