import React, { useState } from 'react';
import { Button, TextField, Grid, FormControl, InputLabel, Select, MenuItem, Link } from '@mui/material';
import { Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from '@mui/material';
import EastIcon from '@mui/icons-material/East';
import { EmployeeSignup, employeePhotosUpdate } from '../../../sevices/account';
import { ENV_CONFIG } from '../../../constant';
import { isEmptyObj, setCurrentUser, removeItemFrmLocalStorage } from '../../../utils/genericUtilities';
import Loader from '../../Loader';


function BankDetails(props) {

    const { onNext, onBack, employeeDetails, handleSnackbarClick = f => f } = props;
    const [errorPopupOpen, setErrorPopupOpen] = useState(false);
    const [loading, setLoading] = useState(false);
    const [accountNumber, setAccountNumber] = useState('')
    const [accountType, setAccountType] = useState('')
    const [accountHolderName, setAccountHolderName] = useState('')
    const [ifscCode, setIfscCode] = useState('')

    const uploadProfile = async (id) => {
        const formData = new FormData();
        formData.append('photo', employeeDetails?.personalInfo?.selectedFile);
        try {
            const response = await employeePhotosUpdate(formData, id);
            if (response?.status == 200) {
            } else {
            }
        } catch (err) {
            handleSnackbarClick(true, { type: 'error', msg: err?.response?.data?.errors?.message })
        }

    }
    const handleNext = async () => {

        setLoading(true);
        const body = {
            "first_name": employeeDetails?.personalInfo?.firstname,
            "last_name": employeeDetails?.personalInfo?.lastname,
            "email": employeeDetails?.personalInfo?.email,
            "phone": employeeDetails?.personalInfo?.phone,
            "password": employeeDetails?.personalInfo?.password,
            "dob": employeeDetails?.personalInfo?.dobValue,
            "gender": employeeDetails?.personalInfo?.gender,
            "address": employeeDetails?.personalInfo?.address,
            "city": employeeDetails?.personalInfo?.city,
            "state": employeeDetails?.personalInfo?.state,
            "pincode": employeeDetails?.personalInfo?.pincode,
            "language_known": employeeDetails?.personalInfo?.language,
            "aadhar_number": employeeDetails?.personalInfo?.aadharNum,
            "grant_type": "password",
            "client_id": ENV_CONFIG.CLIENT_ID,
            "client_secret": ENV_CONFIG.CLIENT_SECRET,
            "login_type": "password",
            "username": employeeDetails?.personalInfo?.email,
            "education_qualification": employeeDetails?.educationDetails?.educationQualification,
            "college_name": employeeDetails?.educationDetails?.institute,
            "University": employeeDetails?.educationDetails?.university,
            "year_of_passing": employeeDetails?.educationDetails?.yearOfPassing,
            "field_of_education": employeeDetails?.educationDetails?.fieldOfEducation,
            "about_you": employeeDetails?.educationDetails?.aboutYou,
            "experience_category": employeeDetails?.educationDetails?.experience,
            "experience_list": employeeDetails?.educationDetails?.checkedItems,
            "field_of_experience": employeeDetails?.educationDetails?.fieldOfExperience,
            "job_title": employeeDetails?.educationDetails?.jobTitle,
            "account_number": accountNumber,
            "account_type": accountType,
            "account_holder_name": accountHolderName,
            "IFSC_code": ifscCode,
            "pan_number": employeeDetails?.personalInfo?.panNum,
        }
        let bankDetailsData = {
            "account_number": accountNumber,
            "account_type": accountType,
            "account_holder_name": accountHolderName,
            "IFSC_code": ifscCode,
        };

        // bankDetailsData.account_number,
        // bankDetailsData.account_type,
        // bankDetailsData.account_holder_name,
        // bankDetailsData.IFSC_code,
        // onNext({  tabKey: 3,bankDetailsData });
        try {
            const response = await EmployeeSignup(body);
            let UserData = { response }

            if (response?.status == 200) {
                let employeeId = response?.data?.details?.id;
                let date = new Date();
                let currentTime = date.getTime();
                const expires_in = currentTime + response?.data?.token?.expires_in * 1000;
                setCurrentUser({ ...response?.data?.token, expires_in });

                // if (selectedFile ) {
                if (employeeDetails?.personalInfo?.selectedFile && !isEmptyObj(employeeDetails?.personalInfo?.selectedFile)) {
                    uploadProfile(employeeId)
                }

                // setLoading(false);
