import React, { useState, useEffect } from 'react';
import { Select, MenuItem, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Grid, TextField, TableFooter, Pagination } from '@mui/material';
import { Link } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';
// import OwlCarousel from "react-owl-carousel";
import { ReactComponent as ListIcon } from '../../../assets/images/card-view.svg';
import { ReactComponent as GridIcon } from '../../../assets/images/list-icon.svg';
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import { Spin } from 'antd';
import '../employerStyle.scss'
import { useNavigate, useLocation } from 'react-router-dom';
import { CertificationCourseList, CertificationCourseApplicationList, PostJobView, EmployerTag, CertificationStatus, CertificateAppliedView } from '../../../sevices/job';
import { ReactComponent as NoDataImg } from '../../../assets/images/no-data-employer.svg';
import dayjs from 'dayjs';
import CustomSnackBar from '../../../shared/snackBar';
import { isEmptyObj } from '../../../utils/genericUtilities';
import Loader from '../../Loader';


function ActiveTabComponent() {
    const [gridView, setGridView] = useState(true);
    const [listView, setListView] = useState(false);
    const [courseList, setCourseList] = useState([]);
    const [loading, setLoading] = useState(true);
    const [page, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [metadata, setMetaData] = useState(null);
    const [containerRef, setContainerRef] = useState(null);
    const [jobApplyList, setJobApplylist] = useState([]);
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});
    const location = useLocation();
    const [photo, setphoto] = useState('')
    const searchParams = new URLSearchParams(location.search);
    const value = searchParams.get('jobId');
    const navigate = useNavigate();
    const [view, setview] = useState('')
    const [load, setload] = useState(false)
    const [selectedValue, setSelectedValue] = useState(courseList.map(() => "1"));
    const [courseListUpdate, setCourseListUpdate] = useState([]);
    useEffect(() => {
        fetchCertificateData();
        fetchJobApplication();
    }, [])

    const options = {
        items: 2,
        loop: false,
        autoplayTimeout: 5000,
        dots: false,
        nav: false,
        //  center: true,
        margin: 30

    };


    const HandleViewjob = async (id) => {

        const params = { id: id }
        const response = await PostJobView({ params: params });

    }


    const HandleButoonHide = (index) => {
        const duplicateJobList = [...new Set(courseList)];
        duplicateJobList[index].Working_date_reached = false;
        setCourseListUpdate(duplicateJobList);
    }

    useEffect(() => {

        if (courseListUpdate?.length) {
            setTimeout(() => {
                setCourseList([...courseListUpdate]);
            }, 600);
        }
    }, [courseListUpdate]);


    const showGridView = () => {
        setGridView(true);
        setListView(false);
    }
    const showListView = () => {
        setListView(true);
        setGridView(false);
    }
    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };
    const fetchCertificateData = async (page = 1) => {
        setLoading(true);
        try {
            const response = await CertificationCourseList({ page, status: 1, limit: 200 });
            if (response?.status === 200) {
                const jobItems = response?.data?.items || [];
                const jobMeta = response?.data?.meta;
                const nextPage = page + 1;
                setPage(nextPage);
                // setCourseList(prevState => [...prevState, ...jobItems]);
                setCourseList(jobItems);

                setMetaData({ ...jobMeta });

            }
        } catch (error) {
            return [];
        } finally {
            setLoading(false);
        }
    };

    const EmployerTagSubmit = async (value, index) => {
        setload(true)
        const body = { id: value, employer_tag: 9 }
        try {
            const response = await EmployerTag(body);
            console.log("HHCAHCACAC", response);


            if (response?.status === 200) {
                const duplicateJobList = [...new Set(courseList)];
                duplicateJobList[index].Working_date_reached = false;
                setCourseListUpdate(duplicateJobList);
            }



            setload(false)

        } catch (error) {
            setload(false)
        }

    }


    const handleViewClick = async (id) => {
        setload(true);

        try {
            // setLoading(true);
            const response = await CertificateAppliedView(id)
            if (response?.status === 200) {
                const jobApplicationList = response?.data?.items;
                setload(false);
                if (jobApplicationList.length === 0) {
                    handleSnackbarClick(true, { type: 'error', msg: "No applicants have applied for this course yet" })
                } else {
                    // navigate(`/viewCandidate?id=${id}`);
                    setLoading(false);

                    navigate(`/viewAppliedList?id=${id}`);
                }

            }
        } catch (error) {
            setLoading(false);
            handleSnackbarClick(true, { type: 'error', msg: "Error fetching job data" })
        } finally {
            setLoading(false);
        }

    }

    const handleChange = async (event, index, rowId, data) => {
        const newSelectedValues = [...selectedValue];
        newSelectedValues[index] = event.target.value;
        const body = {
            id: rowId,
            status: event.target.value
        }
        try {

            const response = await CertificationStatus(body);
            if (response?.status == 200) {
                await fetchCertificateData();
                handleSnackbarClick(true, { type: 'success', msg: `${response?.data?.details?.course_name} is now inactive` })
            }

            // fetchCertificateData();
        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: "Something Went wrong" })
        }


        // }

    };

    const handleScroll = () => {
        if (
            containerRef &&
            containerRef.scrollTop + containerRef.clientHeight >= containerRef.scrollHeight
        ) {
            handleFetchJobsForScroll();
        }
    };

    const handleFetchJobsForScroll = () => {
        const totalJobCount = metadata?.total ? metadata?.total : 0;
        if (!loading && totalJobCount !== courseList?.length) {
            fetchCertificateData(page);
        }
    }
    const handlepressViewJob = (id) => {
        navigate(`/viewCertificate?certificateId=${id}`);
    }

    const handleEditchange = async (id) => {
        navigate(`/postjob?viewid=${id}`)
    }

    const fetchJobApplication = async () => {
        try {
            setLoading(true);
            const response = await CertificationCourseApplicationList()
            if (response?.status === 200) {
                const jobApplicationList = response?.data?.items;
                setphoto(response?.data)
                setJobApplylist(jobApplicationList)
            }
        } catch (error) {
            console.log('Error fetching job data:', error);
            return [];
        } finally {
            setLoading(false);
        }
    };

    const buttonStyles = {
        border: '1px solid #E3ECFF',
        borderRadius: '5px',
        height: '44px',
        fontWeight: 500,
        fontSize: '16px',
        lineHeight: '20px',
        color: '#223385',
        backgroundColor: '#E3ECFF',
        textTransform: 'none',
        padding: '10px 40px',
        margin: '5px' // You can adjust the margin as needed
    };


    console.log("courseListcourseList", courseList);

    const redirectCreateCertificate = () => {
        navigate(`/createCertification`)
    }
    return (
        <div className="fullWidth position-relative">
            <div className="JobCardFilter">
                {/* <div className="JobCardSearchBar"> */}
                {/* <TextField
                        id="last_name"
                        label="Search here..."
                        className="form-control"
                        InputProps={{
                            type: 'text',
                        }}
                    />
                    <Button>
                        <SearchIcon />
                    </Button> */}
                <Button variant="contained" style={{ marginRight: '30px' }} className="postJobBtn" onClick={redirectCreateCertificate} >Create Certification</Button>
                {/* </div> */}
                <div className="listGridIconSet">
                    <span className={gridView === true ? 'listGridIconBtn Active' : 'listGridIconBtn'} onClick={showGridView}>
                        <ListIcon />
                    </span>
                    <span className={listView === true ? 'listGridIconBtn Active' : 'listGridIconBtn'} onClick={showListView}>
                        <GridIcon />
                    </span>
                </div>
            </div>



            {
                listView &&
                <div className="activeJobListView">
                    <div className="employerTabel">
                        <TableContainer component={Paper} className="tabelContainer" ref={ref => setContainerRef(ref)}
                            onScroll={handleScroll}>
                            <Table>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Course Name</TableCell>
                                        <TableCell align="center">Course ID</TableCell>
                                        <TableCell align="center">Posted on</TableCell>
                                        {/* <TableCell align="center">Total Applications</TableCell>
                                        <TableCell align="center">Rejected Applications</TableCell> */}
                                        <TableCell align="center">Status</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {courseList.length === 0 && !loading ? (
                                        <TableRow>
                                            <TableCell colSpan={6}>
                                                <div className="noData"><NoDataImg /></div>
                                            </TableCell>
                                        </TableRow>
                                    ) : (
                                        <>
                                            {courseList.map((data, index) => (
                                                <TableRow key={index}>
                                                    <TableCell>{data?.course_name}</TableCell>
                                                    <TableCell align="center">{data?.id}</TableCell>
                                                    <TableCell align="center">{dayjs(data?.created_at, "YYYY-MM-DD").format("DD-MM-YYYY")}</TableCell>
                                                    {/* <TableCell align="center">0</TableCell>
                                                    <TableCell align="center">0</TableCell> */}
                                                    <TableCell align="center">
                                                        <Select value={selectedValue[index] || "1"} onChange={(event) => handleChange(event, index, data.id, data)} className="tableSelectBtn selectActive">
                                                            <MenuItem value="1">Active</MenuItem>
                                                            <MenuItem value="0">Inactive</MenuItem>
                                                        </Select>
                                                    </TableCell>
                                                </TableRow>
                                            ))}
                                            {loading && (
                                                <TableRow>
                                                    <TableCell colSpan={6}>
                                                        <div className="tableLoader">
                                                            <Spin spinning={true} />
                                                        </div>
                                                    </TableCell>
                                                </TableRow>
                                            )}
                                        </>
                                    )}
                                </TableBody>

                            </Table>
                        </TableContainer>
                    </div>
                </div>
            }

            <div className="fullWidth">
                <Grid container spacing={5}>
                    <Grid item xs={12} sm={12}>
                        <div className="dashboardCardTitle">
                            <span>Applied candidates</span>
                            {/* <Link to="/view-candidates" className="viewAllLink">View all</Link> */}
                        </div>
                        <div className="employerTabel">
                            <TableContainer component={Paper}>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Name</TableCell>
                                            <TableCell>course title</TableCell>
                                            <TableCell>course ID</TableCell>
                                            <TableCell align="center">Outlook</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {/* <TableRow key={1}>
                                            <TableCell>
                                                <div className="d-flex align-center">
                                                    <img src={require('../../../assets/images/user1.png')} className="userImg" alt="" />
                                                    <span>William</span>
                                                </div>
                                            </TableCell>
                                            <TableCell>Digital Media Handling</TableCell>
                                            <TableCell>#66555</TableCell>
                                            <TableCell align="center">
                                                <Button variant="contained" className="viewBtn">View</Button>
                                            </TableCell>
                                        </TableRow> */}
                                        {jobApplyList.length === 0 && !loading ? (
                                            <TableRow>
                                                <TableCell colSpan={6}>
                                                    <div className="noData"><NoDataImg /></div>
                                                </TableCell>
                                            </TableRow>
                                        ) : (
                                            <>
                                                {jobApplyList.map((data, index) => (

                                                    <TableRow key={index}>
                                                        <TableCell>
                                                            <div className="d-flex align-center">
                                                                {/* <img src={require('../../../assets/images/user1.png')} className="userImg" alt="" /> */}
                                                                {data?.employee_profile?.employee_photo ?
                                                                    <img src={`${photo?.meta?.employee_photo?.path}/${photo?.meta?.employee_photo?.folder}/${photo?.meta?.employee_photo?.sizes?.medium}/${data?.employee_profile?.employee_photo}`} className="userImg" />
                                                                    : <img
                                                                        src={require('../../../assets/images/user.png')}
                                                                        className="userImg"
                                                                        alt="User Img"
                                                                    />}
                                                                <span>{data?.employee_profile?.first_name}</span>
                                                            </div>
                                                        </TableCell>
                                                        <TableCell>{data?.certification_course?.course_name}</TableCell>
                                                        <TableCell>{data?.certification_course?.id}</TableCell>
                                                        <TableCell align="center">
                                                            <Button variant="contained" onClick={() => handleViewClick(data?.certification_course?.id)} className="viewBtn">View</Button>
                                                        </TableCell>
                                                    </TableRow>
                                                ))}
                                                {loading && (
                                                    <TableRow>
                                                        <TableCell colSpan={6}>
                                                            <div className="tableLoader">
                                                                <Spin spinning={true} />
                                                            </div>
                                                        </TableCell>
                                                    </TableRow>
                                                )}
                                            </>
                                        )}
                                    </TableBody>
                                    {/* <TableFooter> */}
                                    {/* {console.log(photo?.meta?.count)} */}

                                    {/* </TableFooter> */}
                                </Table>
                                <Pagination count={Math.ceil(photo?.meta?.count / 10)} variant="outlined" shape="rounded" />
                            </TableContainer>
                        </div>
                    </Grid>
                </Grid>
            </div>
            {openSnackbar ? <CustomSnackBar
                openSnackbar={openSnackbar}
                snackBarMessage={customMsg?.msg}
                status={customMsg?.type}
                handleSnackbarClick={handleSnackbarClick}
            /> : null}

            <Loader loading={load} />
        </div>
    )
}
export default ActiveTabComponent;
