// Core
import React from 'react';
import { Navigate, Route, Routes, useNavigate, useLocation } from 'react-router-dom';

// Custom
// import PublicRoute from './publicRouteConfig';
// import ProtectedRoute from './privateRouteConfig';
import { lazyImport } from '../utils/lazyImport';
import EmployerLogin from '../pages/login';

import SignUP from '../pages/employer/signUp';
import PrivacyPolicyComponent from '../pages/home/privacy-policy';
import TermsServiceComponent from '../pages/home/terms-service';

// import ViewJobsPageComponent from '../pages/employer/jobs/view-jobs';
// import ExplorePageComponent from '../pages/parttimer/explore';
// import DescriptionPage from '../pages/parttimer/description';
// import PersonalInfo from '../pages/parttimer/registration-wizard/personal-info';
// import MyJobsComponent from '../pages/parttimer/my-jobs';
import { ROUTE_URL, PUBLIC_URL, ROLE } from '../constant/routeUrl'
import { removeItemFrmLocalStorage } from '../utils/genericUtilities';


// import RejectionSubscriptionComponent from '../pages/parttimer/rejection-subscription';

// import { useLocation } from 'react-router-dom';
// Lazy Route Layouts
const { EmployerLayout } = lazyImport(() => import('../components/layout/employerLayout'), 'EmployerLayout');
const { EmployeeLayoutHome } = lazyImport(() => import('../components/layout/index'), 'EmployeeLayoutHome');
const { EmployeeLayout } = lazyImport(() => import('../components/layout/jobSeekerLayout'), 'EmployeeLayout');

export default function RouteComponent(props) {
    const { roles } = props;
    const currentUser = localStorage.getItem("currentUser");
    const token = localStorage.getItem("token");
    let currentLocation = useLocation().pathname;
    const loginType = JSON.parse(localStorage.getItem('currentUser'));

    const publicValue = PUBLIC_URL.find((element) => element === currentLocation)

    if (publicValue && token) {
        loginType?.types[0] === 1 ? window.open(window.location.origin + '/dashboard', "_self") : window.open(window.location.origin + '/explorejobs?roles=employee', "_self")
    }


    if ((currentLocation.includes('/signup') || currentLocation.includes('/login')) && token) {

        loginType?.types[0] === 1 ? window.open(window.location.origin + '/dashboard', "_self") : window.open(window.location.origin + '/explorejobs?roles=employee', "_self")
    }

    if ((!currentLocation.includes('/signup')) && !token && ROUTE_URL.indexOf(currentLocation) >= 0) {

        window.open(window.location.origin + '/', "_self")
    }

    if (currentLocation == '/') {
        if (token) {
            // /explorejobs?roles=employee
            loginType?.types[0] === 1 ? window.open(window.location.origin + '/dashboard', "_self") : window.open(window.location.origin + '/explorejobs?roles=employee', "_self")
        }
    }
    if (loginType?.types[0] === 3) {
        removeItemFrmLocalStorage(['token', 'currentUser'])
    }


    return (
        <Routes>
            <Route path="/" element={<EmployeeLayoutHome pageTitle='home' />} />

        </Routes>
    )
}
