import React, { useEffect, useState } from 'react';
import { Grid, Button, FormControl, InputLabel, Select, MenuItem, TextareaAutosize } from '@mui/material';
// import { ReactComponent as PostJobBanner } from '../../assets/images/post-job-banner.svg';
// import EastIcon from '@mui/icons-material/East';
// import { DemoContainer } from '@mui/x-date-pickers/internals/demo';
// import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
// import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
// import { DatePicker } from '@mui/x-date-pickers/DatePicker';
// import { useNavigate } from "react-router-dom";
import { JobApplicationView, JobApplyStatus, ApplicationAccept, ApplicationPayment } from '../../sevices/job';
import { useNavigate } from "react-router-dom";
import './employerStyle.scss'
import { isEmptyObj } from '../../utils/genericUtilities';
import CustomSnackBar from '../../shared/snackBar';
import Loader from '../Loader';

const ViewCandidates = (props) => {
    const navigate = useNavigate();
    const [jobApplyList, setJobApplylist] = useState([]);
    const [jobApplyData, setJobApplydata] = useState('');
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});
    const [loading, setLoading] = useState(false);
    const [photo, setphoto] = useState('')


    // const navigate = useNavigate();
    const queryParams = new URLSearchParams(window.location.search);
    var id = queryParams.get('id');
    var empId = queryParams.get('empid');

    const sessionId = queryParams.get('session_id');
    const applicationID = queryParams.get('application_id')
    const postID = queryParams.get('post_id')
    // console.log("IDDDD", id);
    const handleAppliedJob = (id) => {
        // jobApplyList
        const filterJobApplied = jobApplyList.filter(job => job.id === id);
        console.log("filterJobApplied", filterJobApplied);
        setJobApplydata(filterJobApplied[0])
    }

    useEffect(() => {
        if (sessionId) {
            HandlePayment()
        }
    }, []);

    useEffect(() => {
        fetchJobApplicationView(id);
    }, [])

    const HandlePayment = async () => {
        setLoading(true);
        var body = {
            "payment_id": sessionId,
            "application_id": applicationID,
            "post_id": postID

        }

        const response = await ApplicationPayment(body);

        if (response?.status === 200) {
            setLoading(false);
            if (response?.data?.details) {
                handleSnackbarClick(true, { type: 'success', msg: "Job Application Accepted" })
                setTimeout(() => {
                    navigate(`/jobs`)
                }, 1000);

            }
        } else {
            setLoading(false);
            // navigate("/dashboard");
        }



    }
    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };

    const fetchJobApplicationView = async (id) => {

        console.log("CJCASCCS855", id);
        try {
            // setLoading(true);
            const response = await JobApplicationView(id)



            if (response?.status === 200) {
                const jobApplicationList = response?.data?.items;
                console.log("CJCASCCS", response, id);
                // if (jobApplicationList.length === 0) {
                //     // handleSnackbarClick(true, {type: 'error', msg: "No Applied Candidates"  })
                // }
                // const employee_profile = response?.data?.items.filter(employee_profile => employee_profile.id === id);
                console.log("sdghd",empId);

                // var employeeId = empId.split('?id=')
                // console.log("xdfgdfg", employeeId[0]);

                const profile_id = response?.data?.items.find(obj => obj.employee_profile.id == empId);
                console.log("sdgdfgsdf", profile_id);

                setphoto(response?.data)
                setJobApplylist(jobApplicationList)

                // setJobApplydata(jobApplicationList[0])
                setJobApplydata(profile_id)

            }
        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: error })
            return [];
        } finally {
            // setLoading(false);
        }
    };

    const statusFunction = async (status) => {
        let data = {
            "id": jobApplyData?.id,
            "status": status == "decline" ? 6 : status
        }
        try {
            // setLoading(true);
            const response = await JobApplyStatus(data)
            if (response?.status === 200) {
                // const jobApplicationList = response?.data?.items;
                // if(response?.data?.details?.status===5){
                //     handleSnackbarClick(true, {type: 'success', msg: "Job Application Accepted"  })
                //     setTimeout(() => {
                //         navigate(`/jobs`)
                //     }, 1000);

                // }
                if (response?.data?.details?.status === 6) {
                    handleSnackbarClick(true, { type: 'error', msg: "Job Application Decline" })
                    setTimeout(() => {
                        navigate(`/jobs`)
                    }, 1000);
                }

                // navigate(`/dashboard`);
            }
        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: "Something weng wrong" })
            return [];

        }

    }
    const paymentFunction = async (status) => {
        let data = {
            "id": jobApplyData?.id,
        }
        try {
            setLoading(true);
            const response = await ApplicationAccept(data)
            if (response?.status === 200) {
                setLoading(false);
                let paymentID = response.data?.details?.part_timer_payment_id;
                const URL = response.data?.details?.part_timer_payment_object?.url
                if (paymentID) {
                    setLoading(false)
                    window.location.href = URL;

                }
            }
        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: "Something weng wrong" })
            return [];

        }

    }
    return (
        <div className="dashboardPageContainer">
            <div className="mainTitle">Part Timer Profile for {jobApplyList[0]?.job_post?.title} - <span>ID: #{jobApplyData?.id}</span></div>
            <div className="fullWidth">
                <Grid container spacing={5}>
                    <Grid item xs={12} sm={4} lg={4}>
                        <div className="jobAppliedCandidatesContainer">
                            <div className="TotalCandidates">
                                {jobApplyList?.length} Applied candidates
                            </div>
                            <div className="jobAppliedCandidates">
                                {jobApplyList.map((data, index) => (

                                    // <TableRow key={index}>
                                    //     <TableCell>{data?.title}</TableCell>
                                    // </TableRow>
                                    <div className="jobAppliedCandidatesSet" onClick={() => handleAppliedJob(data?.id)} key={index}>
                                        <div className="jobAppliedCandidatesHeader">

                                            {data?.employee_profile?.employee_photo ? <img title={data?.employee_profile?.first_name} src={`${photo?.meta?.employee_photo?.path}/${photo?.meta?.employee_photo?.folder}/${photo?.meta?.employee_photo?.sizes?.medium}/${data?.employee_profile?.employee_photo}`} className="userImg" /> : <img title={data?.employee_profile?.first_name} src={require('../../assets/images/user.png')} className="userImg" alt="" />}


                                            <span>{data?.employee_profile?.first_name} {data?.employee_profile?.last_name}</span>
                                        </div>
                                        <div className="jobAppliedSetInfo">{data?.job_post?.city}</div>
                                        <div className="jobAppliedSetInfo">{data?.job_post?.skills} </div>
                                    </div>
                                ))}
                                {/* <div className="jobAppliedCandidatesSet">
                                    <div className="jobAppliedCandidatesHeader">
                                        <img src={require('../../assets/images/user1.png')} alt="" />
                                        <span>William</span>
                                    </div>
                                    <div className="jobAppliedSetInfo">Whitefiled</div>
                                    <div className="jobAppliedSetInfo">Data Entry </div>
                                </div>
                                <div className="jobAppliedCandidatesSet">
                                    <div className="jobAppliedCandidatesHeader">
                                        <img src={require('../../assets/images/user2.png')} alt="" />
                                        <span>Anjali</span>
                                    </div>
                                    <div className="jobAppliedSetInfo">Whitefiled</div>
                                    <div className="jobAppliedSetInfo">Data Entry </div>
                                </div>
                                <div className="jobAppliedCandidatesSet active">
                                    <div className="jobAppliedCandidatesHeader">
                                        <img src={require('../../assets/images/carousel-img1.jpg')} alt="" />
                                        <span>Meera</span>
                                    </div>
                                    <div className="jobAppliedSetInfo">Electronic city</div>
                                    <div className="jobAppliedSetInfo">Data Entry </div>
                                </div>
                                <div className="jobAppliedCandidatesSet">
                                    <div className="jobAppliedCandidatesHeader">
                                        <img src={require('../../assets/images/user4.png')} alt="" />
                                        <span>Rohit</span>
                                    </div>
                                    <div className="jobAppliedSetInfo">Yelahanka</div>
                                    <div className="jobAppliedSetInfo">Data Entry </div>
                                </div>
                                <div className="jobAppliedCandidatesSet">
                                    <div className="jobAppliedCandidatesHeader">
                                        <img src={require('../../assets/images/user3.png')} alt="" />
                                        <span>Diya</span>
                                    </div>
                                    <div className="jobAppliedSetInfo">Electronic city</div>
                                    <div className="jobAppliedSetInfo">Data Entry </div>
                                </div>
                                <div className="jobAppliedCandidatesSet">
                                    <div className="jobAppliedCandidatesHeader">
                                        <img src={require('../../assets/images/user4.png')} alt="" />
                                        <span>Siddharth</span>
                                    </div>
                                    <div className="jobAppliedSetInfo">Yelahanka</div>
                                    <div className="jobAppliedSetInfo">Data Entry </div>
                                </div> */}
                            </div>
                        </div>
                    </Grid>
                    <Grid item xs={12} sm={8} lg={8}>
                        <div className="candidatesDetailsCard">
                            <div className="candidatesDetailsCardHeader">
                                {jobApplyData?.employee_profile?.employee_photo ? <img title={jobApplyData?.employee_profile?.first_name} src={`${photo?.meta?.employee_photo?.path}/${photo?.meta?.employee_photo?.folder}/${photo?.meta?.employee_photo?.sizes?.medium}/${jobApplyData?.employee_profile?.employee_photo}`} className="userImg" /> : <img title={jobApplyData?.employee_profile?.first_name} src={require('../../assets/images/user.png')} className="userImg" alt="" />}
                                {/* <img src={`https://s3.ap-south-1.amazonaws.com/parttimer/public/images/employee_photo/512/${ jobApplyData?.employee_profile?.employee_photo}`} className="userImg" /> */}

                                {/* <img src={require('../../assets/images/carousel-img1.jpg')} className="userImg" alt="" /> */}
                                <div className="CardHeaderNameInfo">
                                    <div className="CardHeaderName">{jobApplyData?.employee_profile?.first_name} {jobApplyData?.employee_profile?.last_name}</div>
                                    <div className="qualificationSet">
                                        <img src={require('../../assets/images/location-icon1.png')} alt="" />
                                        <span>{jobApplyData?.employee_profile?.city}</span>
                                    </div>
                                    <div className="qualificationSet">
                                        <img src={require('../../assets/images/graduation.png')} alt="" />
                                        <span>{jobApplyData?.employee_profile?.education_qualification}</span>
                                    </div>
                                </div>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">Gender :</span>
                                <span className="CardInfoValue">{jobApplyData?.employee_profile?.gender}</span>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">Language known :</span>
                                <span className="CardInfoValue">{jobApplyData?.employee_profile?.language_known}</span>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">City :</span>
                                <span className="CardInfoValue">{jobApplyData?.employee_profile?.city}</span>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">State :</span>
                                <span className="CardInfoValue">{jobApplyData?.employee_profile?.state}</span>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">Education :</span>
                                <span className="CardInfoValue">{jobApplyData?.employee_profile?.education_qualification}</span>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">Certification :</span>
                                <span className="CardInfoValue">{jobApplyData?.employee_profile?.certification}</span>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">Experience :</span>
                                <span className="CardInfoValue">{jobApplyData?.employee_profile?.experience_category}</span>
                            </div>
                            <div className="candidatesCardInfoSet">
                                <span className="CardInfoLabel">Skills :</span>
                                <span className="CardInfoValue">{jobApplyData?.job_post?.skills}</span>
                            </div>
                            <div className="skillMatchCard boxNone">
                                {/* <div className="skillMatchCardHeader">
                                <div className="skillMatchCardHeaderCard">
                                    <span>Skill match</span>
                                    <strong>7/10</strong>
                                </div>
                            </div> */}
                                <form className="ferrorullWidth">
                                    <div className="WizardFormContainer">
                                        {/* <TextareaAutosize
                                        minRows={5}
                                        maxRows={5}
                                        className="form-control"
                                        placeholder="Write your note"
                                    /> */}
                                        {jobApplyList[0]?.isAccepted === 0 ? <div className="acceptRejectBtnContainer">
                                            <Button className="acceptRejectBtn declineBtn" onClick={() => statusFunction("decline")}>
                                                <img src={require('../../assets/images/reject-icon.png')} alt="" />
                                                <span>Decline</span>
                                            </Button>
                                            <Button className="acceptRejectBtn acceptBtn" onClick={() => paymentFunction()} >
                                                <img src={require('../../assets/images/accept-icon.png')} alt="" />
                                                <span>Accept</span>
                                            </Button>
                                        </div> : null}
                                    </div>
                                </form>
                            </div>
                        </div>
                    </Grid>
                    <Grid item xs={12} sm={3} lg={3}>

                    </Grid>
                </Grid>
            </div>
            {openSnackbar ? <CustomSnackBar
                openSnackbar={openSnackbar}
                snackBarMessage={customMsg?.msg}
                status={customMsg?.type}
                handleSnackbarClick={handleSnackbarClick}
            /> : null}
            <Loader loading={loading} />
        </div>
    );
}

export default ViewCandidates;
