import React, { useState, useEffect } from 'react';
import { Checkbox, FormControlLabel, Tab, Tabs, Box, Collapse, InputBase, IconButton, Paper, Button, FormControl, InputLabel } from '@mui/material';
import JobFilterComponent from '../home/jobFilter';
import ExploreCardComponent from '../home/exploreCard';
