  useEffect(() => {
    // if (locationValue) {
    // console.log("fewfwefwerfewrfwefwefrewerfeffefefee1111111111122222222222222222");

    ApplyFilter()
    getEmployeeProfile()
    // }

  }, [])

  const ValidatePayment = async () => {
    const body = {}
    body.checkout = SessionId
    try {
      const response = await EmployeeSubscriptionVerify(body);

    } catch (error) {
      console.error('CHEckoutResponse error:', error);

    }

  }
  const getEmployeeProfile = async () => {
    // const body = {}
    // body.checkout = SessionId
    try {
      const response = await employeeProfile();
      console.log("profileeee", response?.data?.details?.is_rejected_count);
      if (response?.data?.details?.is_rejected_count >= 3) {
        // alert("reject 3 three time")
        // rejectionPaymentPage()
        navigate('/rejection-subscription')
      }

    } catch (error) {
      console.error('CHEckoutResponse error:', error);

    }

  }








  const city = [
    // { id: 1, name: "Bangalore" },
    { id: 1, name: "Chennai" },
    { id: 2, name: "Puducherry" },
  ];

  useEffect(() => {
    // ApplyFilter();
    getCategory();
    getCertification();
    getEducation();
    // locationIp();
    // if(!SessionId){
    //   SubscriptionView()
    // }
    // if (navigator.geolocation) {
    //   navigator.geolocation.getCurrentPosition(success, error);


    // } else {
    // }

  }, []);
  useEffect(() => {
    if (filter) {
      // debugger
      ApplyFilter();
    }

  }, [page, filter]);

  // const locationIp = () => {
  //   try {
  //     fetch(
