  //         .then((data) => {
  //           const addressComponents = data.results[0].address_components;
  //           const cityName = getAddressComponentValue(addressComponents, 'city');
  //           setLocationValue(cityName);

  //         })

  //     } catch (error) {
  //     }
  //   }
  // }


  // function getAddressComponentValue(components, type) {
  //   for (const component of components) {
  //     // if (component.types.includes(type)) {
  //       if (component.types.includes('locality')) {
  //       return component.long_name;
  //     }
  //   }
  //   return null;
  // }


  // const SubscriptionView = async () => {
  //   try {
  //     const View = await EmployeeSubscriptionView({})
  //   } catch (error) {
  //     console.error('BCKCN225454:', error);
  //     navigate("/employee-subscription?roles=employee")
  //   }
  // }



  // const fetchJobData = async (page = 1) => {
  //   setIsLoading(true);
  //   try {
  //     let body = {};
  //     body.page = page;
  //     body.limit = 8;

  //     const response = await PostJobListFilter(
  //       body
  //     );
  //     if (response?.status === 200) {
  //       const jobItems = response?.data?.items;
  //       const jobMeta = response?.data?.meta;
  //       const nextPage = page + 1;
  //       setPage(nextPage);
  //       jobItems?.length && setJoblist(prevJobList => [...prevJobList, ...jobItems]);
  //       setMetaData({ ...jobMeta });
  //     }

  //   } catch (error) {
  //     return [];
  //   } finally {
  //     setIsLoading(false);
  //   }
  // };

  const handleFetchJobsForScroll = () => {
    const totalJobCount = metaData?.total ? metaData?.total : 0;
    if (totalJobCount !== joblist?.length) {
      ApplyFilter(page);
    }
  }

  const handleScroll = () => {
    const scrollTop = (document.documentElement && document.documentElement.scrollTop) || document.body.scrollTop;
    const scrollHeight = (document.documentElement && document.documentElement.scrollHeight) || document.body.scrollHeight;
    const clientHeight = document.documentElement.clientHeight || window.innerHeight;

    if (scrollTop + clientHeight >= scrollHeight) {
      setIsBottom(true);
    } else {
      setIsBottom(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    if (isBottom) {
      handleFetchJobsForScroll()
    }
  }, [isBottom]);



  const handlesubscription = async (id) => {

    try {
      const View = await EmployeeSubscriptionView({})
      if (View.status === 200) {
        let timestamp = View.data.details.subscription_data.current_period_end
        const targetDate = new Date(timestamp * 1000)
        const currentDate = new Date();
        const timeDiff = targetDate.getTime() - currentDate.getTime();
        const daysRemaining = Math.floor(timeDiff / (1000 * 3600 * 24));

        // setTimeout(() => {
        //   if (targetDate < currentDate) {
        //     navigate("/employee-subscription?roles=employee&data=renewal")
        //   } else {
        //     navigate(`/descriptionjob?id=${id}`)
        //   }
        // }, 500);
      }
    } catch (error) {
      console.error('BCKCN225454:', error);
      setTimeout(() => {
        navigate("/employee-subscription?roles=employee")
      }, 2000);
    }


  }

  const getCategory = async () => {
    const body = {
      status: 1
    }
    const response = await JobCategoryTypes(body);
    const categories = response?.data?.items.map(item => item.name);
    setCategory(categories)
  };
  const getCertification = async () => {
    const body = {
      status: 1
    }
    const response = await JobCertification(body);
    const certificationData = response?.data?.items.map(item => item.name);
    setCertification(certificationData)
  };
  const getEducation = async () => {
    const body = {
      status: 1
    }
    const response = await JobQualification(body);
    const educationdata = response?.data?.items.map(item => item.name);
    setEducation(educationdata)
  };
  const HandleChangeOne = async () => {
    if(inputValue.trim()||locationValue?.length>0){
      navigate('', { replace: true })
      setJoblist([]);
      setPage(1)
      setFilter(true)
    }

  };
  const HandleChangeTwo = async () => {
    setJoblist([]);
    setPage(1)
    setFilter(true)
    filterToggle();
  };

  const ApplyFilter = async () => {
    setFilter(false)
    setIsLoading(true);
    try {
      // let body = {};

      const body = {
        category: checkedCategory,
        education: checkedEducation,
        certification: checkedCertification,
        city:  locationValue?.length>0? locationValue : [],
        search: inputValue,
        page: page,
        limit: 12
      }
      const response = await PostJobListFilter(
        body
      );
      if (response?.status === 200) {
        const jobItems = response?.data?.items;
        const jobMeta = response?.data?.meta;
        const nextPage = page + 1;
        setPage(nextPage);
        setJoblist(prevJobList => [...prevJobList, ...jobItems]);
        setMetaData({ ...jobMeta });
      }

    } catch (error) {
      console.log('Error fetching job data:', error);
      return [];
    } finally {
      setIsLoading(false);
    }
  };

  const filterToggle = () => {
    setExpanded(!expanded);
  };
  const tabEvent = (event, newValue) => {
    setTabValue(newValue);
  };

  const TabPanel = ({ value, index, children }) => {
    return (
      <div role="tabpanel" hidden={value !== index}>
        {value === index && <Box p={3}>{children}</Box>}
      </div>
    );
  };


  const handleCheckboxCategory = (event) => {
    const itemName = event.target.name;
    const isCheckedCategory = event.target.checked;

    if (isCheckedCategory) {
      setCheckedCategoryItems(prevItems => [...prevItems, itemName]);

    } else {
      setCheckedCategoryItems(prevItems => prevItems.filter(item => item !== itemName));
    }

  };
  const handleCheckboxEducation = (event) => {
    const itemName = event.target.name;
    const isCheckedEducation = event.target.checked;

    if (isCheckedEducation) {
      setCheckedEducationItems(prevItems => [...prevItems, itemName]);

    } else {
      setCheckedEducationItems(prevItems => prevItems.filter(item => item !== itemName));
    }

  };

  const handleCheckboxCertification = (event) => {
    const itemName = event.target.name;
    const isCheckedCertification = event.target.checked;

    if (isCheckedCertification) {
      setCheckedCertificationItems(prevItems => [...prevItems, itemName]);

    } else {
      setCheckedCertificationItems(prevItems => prevItems.filter(item => item !== itemName));
    }

  };
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      HandleChangeOne();
    }
  };
