  // const handleCheckboxChange = (event) => {
  //   setCheckedItems((prevCheckedItems) => ({
  //     ...prevCheckedItems,
  //     [event.target.name]: event.target.checked,
  //   }));
  // };

  // const categoriesItems =['Accountant','Event planner','Content Reviewer','Fashion Faculty','Shop keeper','Content Writer','Catering','Tutor']
  // const educationItems =['10th completed','12th completed','Perusing','Any degree']
  // const certificationItems =['Mc Donnells Certified','KFC Certified','Certified Personal Traine','Certified Personal Traine','TEFL/TESOL','Google Analytics','Microsoft Office','Food Handler','Public Accountant']
