    <div className="ExploreFilterPage" id="container">
      <div className="jobFilterContainer">
        <div className="jobFilterSearchCard">
          <div className="title">
            Find Your Perfect Part-Time Job
          </div>
          <div className="filterInputContainer">
            <Paper
              component="form"
              sx={{ p: '2px 4px', display: 'flex', alignItems: 'center', width: 400 }}
            >
              <div className="searchLoaction">
                <FilterLocation />
                <FormControl>
                  {/* <InputLabel id="choose-location-label">Choose your location</InputLabel> */}
                  <Select
                    style={{ width: '175px' }}
                    labelId="choose-location-label"
                    id="choose-location"
                    className="form-control home-location-field"
                    defaultValue={locationValue}
                    value={locationValue}
                    label="Choose your location"
                    onChange={(e) => setLocationValue(e.target.value)}
                  >
                    {city.map((cities) => (
                      <MenuItem key={cities.id} value={cities.name}>{cities.name}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </div>
              <InputBase
                className="searchSkills"
                sx={{ flex: 5 }}
                placeholder="Job title, Skills or Company"
                value={inputValue}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
              />
              <IconButton onClick={() => { HandleChangeOne(); }} type="button" aria-label="search">
                <FilterSearch />
              </IconButton>
              <IconButton color="primary" aria-label="directions" onClick={filterToggle}>
                <FilterIcon />
              </IconButton>
            </Paper>
          </div>
        </div>
        <div>
          <Collapse in={expanded}>
            <div className="filterCollapseContainer">
              <div className="title">
