              <div className="tabContainer">
                <Tabs value={tabValue} onChange={tabEvent} orientation="vertical">
                  <Tab label="Categories" />
                  <Tab label="Education" />
                  <Tab label="Certification" />
                </Tabs>

                <TabPanel value={tabValue} index={0}>
                  {category.map((item) => (

                    <FormControlLabel
                      key={item}
                      control={
