                          checked={checkedCategory.includes(item)}
                          onChange={handleCheckboxCategory}
                          name={item}
                        />
                      }
                      label={item}
                    />
                  ))}
                </TabPanel>

                <TabPanel value={tabValue} index={1}>
                  {education.map((item) => (

                    <FormControlLabel
                      key={item}
                      control={
                        <Checkbox
                          checked={checkedEducation.includes(item)}
                          onChange={handleCheckboxEducation}
                          name={item}
                        />
                      }
                      label={item}
                    />
                  ))}
                </TabPanel>

                <TabPanel value={tabValue} index={2}>
                  {certification.map((item) => (

                    <FormControlLabel
                      key={item}
                      control={
                        <Checkbox
                          checked={checkedCertification.includes(item)}
                          onChange={handleCheckboxCertification}
                          name={item}
                        />
                      }
                      label={item}
                    />
                  ))}
                </TabPanel>
              </div>
              <div className="fullWidth">
                <Button onClick={() => { HandleChangeTwo(); }} variant="outlined" className="applyFilterBtn">Apply filter</Button>
              </div>
            </div>
          </Collapse>
        </div>
      </div>
      {/* <div className="jobsFilterContainer">
        <JobFilterComponent />
      </div> */}
      <div className="exploreCardContainer" style={{ marginTop: '40px' }}>
        <Grid container spacing={2.5}>
          {isLoading ? (
            <ExploreCardLoader />
          ) : joblist.length === 0 ? (
            <div className="noData">
              <NoDataImg />
            </div>
          ) : (
            <>
              {joblist.map((ele) => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={ele.id}>
                  <Link className="exploreCard" 
                   href={ele.applications_count >= 3 ? `/descriptionjob?id=${ele.id}&job=filled` : `/descriptionjob?id=${ele.id}`}

                  >
                    <div className="exploreCardHeader">
                      {ele?.employer_profile?.company_logo ? (

                        <img className="exploreCardHeaderImg"
                          alt="Company Logo"
                          src={`${metaData?.company_logo?.path}/${metaData?.company_logo?.folder}/${metaData?.company_logo?.sizes?.medium}/${ele?.employer_profile?.company_logo}`}  />
                      ) : (
                        <img
                          src={require('../../assets/images/default-logo1.png')}
                          className="exploreCardHeaderImg"
                          alt="Default Logo"
                        />
                      )}
                      <div className="exploreCardHeaderNameDomain">
                        <span className="exploreCardHeaderName">
                          {ele?.employer_profile?.company_name}
                        </span>
                        <span className="exploreCardHeaderDomain">{ele.city}</span>
                      </div>
                    </div>
                    <div className="exploreCardBody">
                      <div className="exploreCardBodyTitle">{ele.title}</div>
                      <Typography variant="body1">{ele?.job_description}</Typography>
                    </div>
                    <div className="exploreCardFooter">
                      <span className="exploreCardFooterAmount">
                        {ele?.job_salary}/<small>/{moment(ele?.job_timing_slot, 'HH:mm a').format('HH')} hrs</small>
                      </span>
                      {ele.applications_count >= 3 ? (
                        <Link className="exploreCardFooterLink exploreFilled">

                          <span>Filled</span>
                        </Link>
                      ) : (
                        <Link onClick={() => handlesubscription(ele.id)} className="exploreCardFooterLink">
                          <span>Apply Now</span>
                          <EastIcon />
                        </Link>
                      )}
                    </div>
                  </Link>
                </Grid>
              ))}
            </>
          )}
        </Grid>
      </div>
    </div>
  )
}
export default ExplorePageComponent;
