import React, { useEffect, useState } from 'react';
import { Button, Link, Menu, Select, MenuItem, InputLabel, FormControl, TextField, Fade, Badge } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { useNavigate, useLocation } from 'react-router-dom';
import { EmployerNotificationsList, EmployerProfile } from '../../sevices/account';
import { Spin } from 'antd';
import { removeItemFrmLocalStorage } from '../../utils/genericUtilities';
import NotificationsIcon from '@mui/icons-material/Notifications';
import '../header/style.scss';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime'
import OneSignal from 'react-onesignal';
dayjs.extend(relativeTime)

function EmployerHeader() {
  const [menuToggled, setMenuToggled] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [profileInfo, setProfileInfo] = React.useState(null);
  const [firstName, setFirstName] = React.useState(null);
  const [companyName, setCompanyName] = React.useState(null);

  const [anchorElNotifi, setAnchorElNotifi] = React.useState(null);
  const [logo, setLogo] = React.useState(null);
  const [meta, setMeta] = React.useState(null);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = React.useState(false);
  const [employerID, setEmployerID] = React.useState(null);

  useEffect(() => {
if (employerID) {
setTimeout(() => {
    OneSignal.login(`employer-${employerID}`);
  }, 5000);
}
getEmployerProfile()
}, [employerID])

useEffect(() => {
  if (employerID) {

    setTimeout(() => {
      fetchNotificationsData();
    }, 5000);
  }


  }, [employerID,notifications])

  const getEmployerProfile = async () => {
    // setLoading(true);
    try {
      const response = await EmployerProfile({});
      setEmployerID(response?.data?.details?.id)
      // setFirstName(response?.data?.details?.first_name)
      // setCompanyName(response?.data?.details?.company_name)
      // setLogo(response?.data?.details?.company_logo)
      // setMeta(response?.data?.meta)
      // setLoading(false)
      console.log("eewefeff",response?.data?.details?.id);
    } catch (error) {
      console.error('error:', error);
    }
  }
  const open = Boolean(anchorEl);
  const openNotifi = Boolean(anchorElNotifi);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  const fetchNotificationsData = async () => {
    let body ={
        status:1
    }
        // if (userAuth) {
            const response = await EmployerNotificationsList(body);
            console.log("response?.data?.details===>",response?.data?.details);
            setNotifications(response?.data?.details)
        // }
    }
  const handleNotification = (event) => {
      setAnchorElNotifi(event.currentTarget);

  };
  const handleRedirectNotifi = (item) => {
    console.log("fcefeeeee",item?.job_post);
      setAnchorElNotifi(null);
      navigate(`/viewCandidate?id=${item?.job_post}`);

  };
  const handleCloseNotifi = () => {
      setAnchorElNotifi(null);
  };

  const redirectPostJob = () => {
    navigate("/postjob")
  };
  const redirectProfile = () => {
    navigate("/userProfile")
    setAnchorEl(null);
  }
  const handleLogout = () => {
    removeItemFrmLocalStorage(['token', 'currentUser'])
    navigate("/")
  }
  useEffect(() => {
    if (menuToggled) {
      document.body.classList.add('menuOpen');
    } else {
      document.body.classList.remove('menuOpen');
    }
  }, [menuToggled]);

  const menuToggle = () => {
    setMenuToggled(!menuToggled);
  };
  return (
    <div className="fullWidth">
      <Spin spinning={loading}>
        {/* Employeer Before Login Header Start */}
        {(location.pathname === '/company-registration') &&
          <div className="EmployeerHeaderContainer">
            <Link href="javascript:;">
              <img src={require("../../assets/images/Logo Blue TM.png")} className="logo" />
            </Link>
            <div className="EmployeerHeaderMenu">
              <div className="menuDropdown">
              </div>
              <div className="menuDropdown">
                {/* <Button
                  id="fade-button"
                  aria-controls={open ? 'fade-menu' : undefined}
                  aria-haspopup="true"
                  aria-expanded={open ? 'true' : undefined}
                  onClick={handleClick}
                >
                  <span>Employer</span>
                  <img src={require("../../assets/images/menu-down.png")} className="downArrow" />
                </Button> */}
                <Button
                  id="fade-menu"
                  MenuListProps={{
                    'aria-labelledby': 'fade-button',
                  }}
                  anchorEl={anchorEl}
                  open={open}
                  onClose={handleClose}
                  TransitionComponent={Fade}
                >
                </Button>
              </div>

              <div className="menuDropdown">
                {/* <Button
                  id="fade-button"
                  aria-controls={open ? 'fade-menu' : undefined}
                  aria-haspopup="true"
                  aria-expanded={open ? 'true' : undefined}
                  onClick={handleClick}
                >
                  <img src={require("../../assets/images/menu-user.png")} className="menuUser" />
                  <span>Welcome</span>
                </Button> */}
              </div>
            </div>
          </div>
        }

        {/* Employeer Before Login Header End */}
        {!(location.pathname === '/company-registration'|| location.pathname === '/employersubscription') &&
        <>
          <div className="DashboardHeaderContainer">
            <div className="companyLogoConatainer d-flex align-center" display={{ xs: "block", md: "none" }}>
            <Button className="mobileMenuBtn hideWeb" onClick={menuToggle}>
