              <Button variant="contained" style={{ marginRight: '30px' }} className="postJobBtn" onClick={redirectPostJob}>Post a job</Button>
              {/* <Button className="notificationBtn">
                <img src={require("../../assets/images/bell-icon.png")} />
                <span className="dot"></span>
              </Button> */}
              <div className="menuDropdown">
                <Button
                  id="fade-button"
                  aria-controls={open ? 'fade-menu' : undefined}
                  aria-haspopup="true"
                  aria-expanded={open ? 'true' : undefined}
                  onClick={handleClick}
                >
                  <img src={require("../../assets/images/menu-user1.png")} className="menuUser" />
                  {firstName ? <span>{firstName}</span> : <span></span>}
                  <img src={require("../../assets/images/menu-down.png")} className="downArrow" />
                </Button>
                <Menu
                  id="fade-menu"
                  MenuListProps={{
                    'aria-labelledby': 'fade-button',
                  }}
                  anchorEl={anchorEl}
                  open={open}
                  onClose={handleClose}
                  TransitionComponent={Fade}
                >
                  <MenuItem onClick={redirectProfile}>Profile</MenuItem>
                  {/* <MenuItem onClick={handleClose}>My account</MenuItem> */}
                  <MenuItem onClick={handleLogout}>Logout </MenuItem>
                </Menu>
              </div>
            </div>
          </div>
          <div className="menuOverlay" display={{ xs: "block", lg: "none" }} onClick={menuToggle} />
          </>
        }
        {(location.pathname === '/employersubscription') &&
          <div className="DashboardHeaderContainer">
          <div className="companyLogoConatainer">
            {/* <img src={require("../../assets/images/default_logo.png")} className="logo" />
            {companyName ? <span>{companyName}</span> : <span></span>} */}
          </div>
          <div className="MenuContainer">
            <div className="menuDropdown">
              <Button
                id="fade-button"
                aria-controls={open ? 'fade-menu' : undefined}
                aria-haspopup="true"
                aria-expanded={open ? 'true' : undefined}
                onClick={handleClick}
              >
                <img src={require("../../assets/images/menu-user1.png")} className="menuUser" />
                {firstName ? <span>{firstName}</span> : <span></span>}
                <img src={require("../../assets/images/menu-down.png")} className="downArrow" />
              </Button>
              <Menu
                id="fade-menu"
                MenuListProps={{
                  'aria-labelledby': 'fade-button',
                }}
                anchorEl={anchorEl}
                open={open}
                onClose={handleClose}
                TransitionComponent={Fade}
              >
                <MenuItem onClick={redirectProfile}>Profile</MenuItem>

                <MenuItem onClick={handleLogout}>Logout </MenuItem>
              </Menu>
            </div>
          </div>
        </div>
        }
      </Spin>
    </div>

