import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from '@mui/material';

import { ReactComponent as LoginBack } from '../assets/images/left-nav.svg';
import { useNavigate } from 'react-router-dom';
function JobView() {
  const navigate = useNavigate();

  const job = {
    companyLogo: require('../assets/images/com-logo-3.png'), // Ensure you have the correct path to your logo
    companyName: 'Example Company',
    jobTitle: 'Software Engineer',
    skills: 'JavaScript, React, Node.js',
    requirements: '2+ years of experience, Bachelor\'s degree in Computer Science',
    description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Blanditiis, ex doloremque dignissimos excepturi quisquam fugit aperiam sit molestias similique architecto, odit velit labore! Accusamus incidunt sapiente delectus iusto atque nisi!",
  };
  const handleGoBack = () => {
    navigate(-1);
  };
  return (
    <><Button   className='loginBackBtn' onClick={handleGoBack} style={{ backgroundColor: 'rgb(245, 250, 255)',display:"inline-block",padding:50 }}>
      <LoginBack />
      <span>Back</span>
    </Button><div className="container">

        <div className="row justify-content-center">
          <div className="col-lg-8">

            <div className="card job-card">
              <div className="card-body text-center">

                <img src={job.companyLogo} className="company-logo" alt="Company Logo" />
                <h2 className="ca3rd-title">{job.companyName}</h2>
                <h4 className="card-subtitle mb-2 text-muted">{job.jobTitle}</h4>
                <hr />
                <h5 className="card-subtitle">Skills</h5>
                <p className="card-text">{job.skills}</p>
                <hr />
                <h5 className="card-subtitle mb-2">Requirements</h5>
                <p className="card-text">{job.requirements}</p>
                <hr />
                <h5 className="card-subtitle mb-2">Job Description</h5>
                <p className="card-text">{job.description}</p>
                <a href="#" className="btn btn-primary">Apply Now</a>
              </div>
            </div>
          </div>
        </div>
      </div></>
  );
}

export default JobView;
