import React, { useEffect, useState } from 'react';
import { Grid, Tab, Tabs, Button, Select, MenuItem, } from '@mui/material';
// import EastIcon from '@mui/icons-material/East';
import '../../parttimer/part-timer-style.scss';
import { PostJobView, PostJobStatus, PostJobDelete, EmployerTag, JobApplicationView } from '../../../sevices/job';
import { useLocation, useNavigate } from 'react-router-dom';
import { isEmptyObj } from '../../../utils/genericUtilities';
import CustomSnackBar from '../../../shared/snackBar';
import Loader from '../../Loader';
import _ from 'lodash';
import dayjs from 'dayjs';

const ViewJobsPageComponent = (props) => {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const value = searchParams.get('jobId');
    const navigate = useNavigate();

    const [jobView, setJobView] = useState('');
    const [job, setJob] = useState('');

    const [workingTiming, setWorkingTiming] = useState('');
    const [selectedValue, setSelectedValue] = useState('');
    const [daysRemaining, setDaysRemaining] = useState(null);
    const [meta, setmeta] = useState('')
    const [loading, setLoading] = useState(true);
    const [hidebutton, setHidebutton] = useState(false);

    const [workingDateReached, setWorkingDateReached] = useState(false);

    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});

    useEffect(() => {
        HandleViewjob()
    }, [])

    useEffect(() => {
        const currentDate = new Date();
        const inputDate = new Date(jobView?.created_at);
        const timeDifference = currentDate.getTime() - inputDate.getTime();
        const days = Math.ceil(timeDifference / (1000 * 3600 * 24));
        setDaysRemaining(days);
    }, [jobView?.created_at]);

    const handleChange = async (event) => {
        if (jobView?.payment_status != 9) {
            navigate(`/jobPostPayment?value=${jobView.id}`);
        } else {
            setLoading(true)
            setSelectedValue(event.target.value);
            let data = event.target.value
            // const params = { id: value }
            const body = {
                id: value,
                status: data
            }

            try {
                const response = await PostJobStatus(body);
                if (response?.status == 200) {
                    handleSnackbarClick(true, { type: 'success', msg: `${response?.data?.details?.title} is now inactive` })
                    HandleViewjob()
                    setLoading(false)
                }

                // fetchJobData();
            } catch (error) {
                setLoading(false)
                handleSnackbarClick(true, { type: 'error', msg: "Something Went wrong" })
            }
        }



        // const response = await PostJobStatus(body);
        // HandleViewjob()

    };

    const handleDelete = async () => {
        const params = { id: value };
        const response = await PostJobDelete(params);
        navigate('/jobs')

    }

    const HandleViewjob = async () => {
        setLoading(true)
        const params = { id: value }
        try {
            const response = await PostJobView({ params: params });
            setJob(response?.data)
            setmeta(response?.data)
            setSelectedValue(response?.data?.details?.status)
            let ViewData = response?.data?.details
            convertAndAddHours(ViewData?.working_timing, ViewData?.job_timing_slot)
            setJobView(ViewData)
            setWorkingDateReached(response?.data?.details?.Working_date_reached)
            setLoading(false)

        } catch (error) {
            setLoading(false)
            handleSnackbarClick(true, { type: 'error', msg: "somthing went wrong" })

        }

    }


    const EmployerTagSubmit = async () => {
        setLoading(true)
        const body = { id: value, employer_tag: 9 }
        try {
            const response = await EmployerTag(body);
            setLoading(false)
            HandleViewjob()
        } catch (error) {
            setLoading(false)
        }

    }

    const handleEditchange = async () => {
        navigate(`/postjob?viewid=${jobView?.id}`)
    }
    const convertAndAddHours = (workingTiming24, jobTimingSlot) => {
        const [startHour, startMinute] = workingTiming24.split(':');
        const startHourInt = parseInt(startHour, 10);
        const endHourInt = startHourInt + parseInt(jobTimingSlot, 10);
        const startAmPm = startHourInt >= 12 ? 'PM' : 'AM';
        const endAmPm = endHourInt >= 12 ? 'PM' : 'AM';
        const startHour12 = startHourInt === 0 ? 12 : startHourInt > 12 ? startHourInt - 12 : startHourInt;
        const endHour12 = endHourInt === 0 ? 12 : endHourInt > 12 ? endHourInt - 12 : endHourInt;
        const formattedStartTime = `${startHour12.toString().padStart(2, '0')}:${startMinute} ${startAmPm}`;
        const formattedEndTime = `${endHour12.toString().padStart(2, '0')}:${startMinute} ${endAmPm}`;
        setWorkingTiming(`${formattedStartTime} - ${formattedEndTime}`)

        return `${formattedStartTime} - ${formattedEndTime}`;
    };



    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };

    const handleViewClick = async (id) => {
        setLoading(true);

        try {
            // setLoading(true);
            const response = await JobApplicationView(id)
            if (response?.status === 200) {
                const jobApplicationList = response?.data?.items;
                setLoading(false);
                if (jobApplicationList.length === 0) {
                    handleSnackbarClick(true, { type: 'error', msg: "No applicants have applied for this job yet" })
                } else {
                    // navigate(`/viewCandidate?id=${id}`);
                    try {
                        const params = { id: id }
                        const response = await PostJobView({ params: params });

                        if (response?.data?.details?.part_timer_payment_status === 9) {
                            navigate(`/employeeview?id=${id}`);
                        } else {
                            navigate(`/viewCandidate?id=${id}`);
                        }

                    } catch (error) {
                setLoading(false);
                        handleSnackbarClick(true, { type: 'error', msg: "Error fetching job data" })
                    }
                }

            }
        } catch (error) {
            setLoading(false);
            handleSnackbarClick(true, { type: 'error', msg: "Error fetching job data" })
        } finally {
            // setLoading(false);
        }

    }

    const buttonStyles = {
        border: '1px solid #E3ECFF',
        borderRadius: '5px',
        height: '44px',
        fontWeight: 500,
        fontSize: '16px',
        lineHeight: '20px',
        color: '#223385',
        backgroundColor: '#E3ECFF',
        textTransform: 'none',
        padding: '10px 40px',
        margin: '5px' // You can adjust the margin as needed
    };
    return (
        <div className="discriptionPage viewJobPage">
            <Grid container spacing={5}>
                <Grid item xs={12} sm={10}>
                    <div className="discriptionLeftCard">
                        <div className="companyCard">
                            <div className="cardHeaderSet">
                                <div className="cardHeaderLogoSet">


                                    {jobView?.employer_profile?.company_logo != null ? <img title={jobView?.employer_profile?.company_name} src={`${job?.meta?.company_logo?.path}/${job?.meta?.company_logo?.folder}/${job?.meta?.company_logo?.sizes?.medium}/${jobView?.employer_profile?.company_logo}`} /> : <img title={jobView?.employer_profile?.company_name} src={require("../../../assets/images/default-logo1.png")} className="logo" />}
                                    {/* <img src={require("../../../assets/images/com-logo-2.png")} /> */}
                                    <div className="nameInfo">
                                        <strong>{jobView?.employer_profile?.company_name} </strong>
                                        <span>{jobView?.employer_profile?.state}</span>
                                    </div>
                                </div>
                                <div className="amountBtnSet">
                                    <div className="amount">
                                        <span>â‚¹{jobView?.job_salary}</span>
                                        <small>/hr</small>
                                    </div>
                                    <Button className="actionBtn" onClick={handleDelete} >
                                        <img title='Delete' src={require("../../../assets/images/delete-icon.png")} />
                                    </Button>
                                    {/* <Button onClick={handleEditchange} className="actionBtn">
                                        <img title='Edit' src={require("../../../assets/images/edit-icon3.png")} />
                                    </Button> */}
                                </div>
                            </div>

                            <div className="cardBody">
                                <div className="title">{jobView?.title}</div>
                                <div className="content">
                                    {jobView?.job_description}

                                </div>
                                <div className="qualificationSetContainer">
                                    <div className="qualificationSet">
                                        <img src={require('../../../assets/images/work-icon.png')} alt="" />
                                        <span>{jobView?.experience}</span>
                                    </div>
                                    <div className="qualificationSet">
                                        <img src={require('../../../assets/images/location-icon1.png')} alt="" />
                                        <span>{jobView?.city}</span>
                                    </div>
                                    <div className="qualificationSet">
                                        <img src={require('../../../assets/images/graduation.png')} alt="" />
                                        <span>{jobView?.required_qualifications}</span>
                                    </div>
                                </div>
                                <div className="viewJobsCardFooter">
                                    {console.log("JOBBBBSBSBBSSB", jobView)}
                                    {/* <Button onClick={() => handleViewClick(`${jobView?.id}`)}  className="viewApplicantBtn" >View Applicants</Button> */}
                                    <Button className="viewApplicantBtn" onClick={() => handleViewClick(`${jobView?.id}`)} >{jobView?.part_timer_payment_status === 9 ? 'Part-timer Details' : 'View Applicants'}</Button>
{/*                                     
                                    {workingDateReached && !hidebutton ? <> <span>Job Completed ?</span>
                                        <Button onClick={() => EmployerTagSubmit()} style={buttonStyles} >Yes</Button>
                                        <Button onClick={() => setHidebutton(true)} style={buttonStyles} >No</Button></> : null} */}


                                    <Select value={selectedValue} className={selectedValue === 0 ? "inActiveBtn" : null} onChange={handleChange}>
                                        <MenuItem value="0">Inactive</MenuItem>
                                        <MenuItem value="1">Active</MenuItem>
                                    </Select>
                                </div>
                            </div>
                            <div className="cardFooter">
                                <div className="footerInfo">
                                    Posted:
                                    <span> {daysRemaining} days ago</span>
                                </div>
                                <div className="footerInfoRight">
                                    <div className="footerInfo">
                                        Job ID:
                                        <span>{jobView?.id}</span>
                                    </div>
                                    <div className="footerInfo leftRightBorder">
                                        <img src={require('../../../assets/images/date-icon2.png')} />

                                        <span>
                                            {dayjs(jobView?.created_at, "YYYY-MM-DD").format("DD-MM-YYYY")}
                                        </span>
                                    </div>
                                    <div className="footerInfo">
                                        <img src={require('../../../assets/images/time-icon.png')} />
                                        <span>{workingTiming}</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="discriptionCard">
                            <div className="title">
                                Certification Required
                            </div>
                            <div className="content">
                                {/* Type Writing Junior Grade */}
                                {jobView?.certification_required}

                            </div>
                        </div>
                        <div className="discriptionCard">
                            <div className="title">
                                Full Job Description
                            </div>
                            <ul className="contentList">
                                <li>{jobView?.job_description}</li>

                            </ul>
                            {/* <Button className="shareBtn">
                                <img src={require('../../../assets/images/share-icon.png')} />
                            </Button> */}
                        </div>
                        <div className="discriptionCard">
                            <div className="title">
                                Required Skills/Abilities
                            </div>
                            <ul className="contentList">

                                <li>{jobView?.skills}</li>
                            </ul>
                        </div>
                    </div>
                </Grid>
            </Grid>
            <Loader loading={loading} />
            {openSnackbar ? <CustomSnackBar
                openSnackbar={openSnackbar}
                snackBarMessage={customMsg?.msg}
                status={customMsg?.type}
                handleSnackbarClick={handleSnackbarClick}
            /> : null}
        </div>
    )
}
export default ViewJobsPageComponent;
