import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './JobListing.css'; // Import your custom CSS

function JobListing() {
  const [job, setJob] = useState({ title: '', description: '' });

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call API to save job listing
  };

  return (
    <div className="container my-5">
      <div className="row mb-4">
        <div className="col">
          <input type="text" className="form-control" placeholder="Search Job Listings" />
        </div>
      </div>
      <div className="card">
        <div className="card-body">
          <h1 className="card-title">Create/Edit Job Listing</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="title">Job Title</label>
              <input
                id="title"
                name="title"
                value={job.title}
                onChange={handleChange}
                className="form-control"
                placeholder="Job Title"
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Job Description</label>
              <input
                id="description"
                name="description"
                value={job.description}
                onChange={handleChange}
                className="form-control"
                placeholder="Job Description"
              />
            </div>
            <button type="submit" className="btn btn-primary">Save</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default JobListing;
