import React,{ useState,useEffect } from 'react';
import { Grid, Button} from '@mui/material';
import { ReactComponent as JobPaymentBanner } from '../../../assets/images/payment-banner.svg';
import EastIcon from '@mui/icons-material/East';
import './../employerStyle.scss';
import { useLocation } from "react-router-dom";
import { ENV_CONFIG } from '../../../constant';
import { PostJobView ,PaymentValidate} from '../../../sevices/job';
import { useNavigate } from "react-router-dom";
import moment from 'moment';
import Loader from '../../Loader';
import dayjs from 'dayjs';
import _ from 'lodash';

function JobPaymentComponent() {
    const [loading, setLoading] = useState(false);
    const location = useLocation();
    const [meta, setmeta] = useState('')
    const [jobPost, setJobPost] = useState('');
    const navigate = useNavigate();
    const queryParams = new URLSearchParams(window.location.search);
    const sessionId = queryParams.get('session_id');
    const sessionValue = queryParams.get('value')

    const HandlePayment = async () => {
        setLoading(true);
        var body = {
            "payment_id": sessionId,
            "id": sessionValue
        }

        const response = await PaymentValidate(body);

        if (response?.status === 200) {
            setLoading(false);
            // setPopupOpen(true);
            navigate("/dashboard");
        }else{
            setLoading(false);
            // navigate("/dashboard");
        }



    }
    useEffect(() => {
        if (sessionId) {
            HandlePayment()
        }else{
            HandleViewjob()
        }
    }, []);
    const HandleViewjob = async () => {
        setLoading(true);
        const params = { id: sessionValue }
        const response = await PostJobView({ params: params });
        setJobPost(response?.data?.details)
        setmeta(response?.data?.meta)
        setLoading(false);

    }
    const Onsubmit = async () => {
        setLoading(true);
        try {


            let paymentID = jobPost?.payment_id;
            const URL = jobPost?.payment_object?.url
            if (paymentID) {
                setLoading(false)
                window.location.href = URL;

            } else {
                // console.error(data.error);
            }
        } catch (error) {
            setLoading(false)
            console.error('error:', error);
        }
    }
    return (
        <div className="dashboardPageContainer">
            <Grid container alignItems="center" justifyContent="center" spacing={0}>
                <Grid item xs={12} sm={6}>
                    <div className="jobPaymentContainer">
                        <div className="jobPaymentBannerImg">
                            <JobPaymentBanner />
                        </div>
                        <div className="payBenefits">For posting a job pay <strong>₹100</strong> per job the benefits are</div>
                        <div className="sortTitle">Short Preview of your job</div>
                        <div className="jobPaymentCard">
                            <div className="jobPaymentCardImg">
                            {jobPost?.employer_profile?.company_logo ? <img src={`${_.get(meta, 'company_logo.path')}/${_.get(meta, 'company_logo.folder')}/${_.get(meta, 'company_logo.sizes.medium')}/${jobPost?.employer_profile?.company_logo}`} /> : <img src={require("../../../assets/images/default-logo1.png")} />}
                            </div>
                            <div className="jobPaymentCardRight">
                                <div className="jobPaymentCardHeader">
                                    <span className="CardHeaderTitle">
                                        {jobPost?.employer_profile?.company_name}
                                    </span>
                                    <div className="jobIdRight">
                                        <span className="jobId">
                                            Job ID: <span>#{jobPost?.id}</span>
                                        </span>
                                        <span>₹{jobPost?.job_salary}</span>
                                                            <small>/{moment(jobPost?.job_timing_slot, "HH:mm a").format("HH")}hrs</small>
                                    </div>
                                </div>
                                <div className="CardMainTitle">
                                {jobPost?.category_type}
                                </div>
                                <div className="CardMainContant">
                                    {jobPost?.job_description}
                                </div>
                                <div className="qualificationSetContainer">
                                    <div className="qualificationSet">
                                        <img src={require('../../../assets/images/work-icon.png')} alt="" />
                                        <span>0 - {jobPost?.experience}</span>
                                    </div>
                                    <div className="qualificationSet">
                                        <img src={require('../../../assets/images/location-icon1.png')} alt="" />
                                        <span>{jobPost?.city}</span>
                                    </div>
                                    <div className="qualificationSet">
                                        <img src={require('../../../assets/images/graduation.png')} alt="" />
                                        <span>{jobPost?.required_qualifications}</span>
                                    </div>
                                </div>
                                <div className="payDateTimeBtnContainer">
                                    <span className="payDateTimeBtn">
                                        <img src={require('../../../assets/images/date-icon1.png')} alt="" />
                                        <span>{dayjs(jobPost?.working_date, "YYYY-MM-DD").format("DD-MM-YYYY")}</span>
                                    </span>
                                    <span className="payDateTimeBtn">
                                        <img src={require('../../../assets/images/clock-icon.png')} alt="" />
                                        <span>{jobPost?.job_timing_slot}</span>
                                    </span>
                                </div>
                            </div>
                        </div>
                        <Button onClick={Onsubmit} variant="contained" className="JobPayNowBtn">
                            <span>Pay now</span>
                            <EastIcon />
                        </Button>
                    </div>
                </Grid>
            </Grid>
            <Loader loading={loading} />
        </div>
    );
  }

  export default JobPaymentComponent;
