sourcesContent:
import React, { useState, useEffect } from 'react';
import { Button, TextField, Grid, FormControl, Select, MenuItem, InputLabel, InputAdornment } from '@mui/material';
import EastIcon from '@mui/icons-material/East';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import { useLocation, useNavigate } from 'react-router-dom';
import { Spin } from 'antd';
import { EmployerSignup, CompanyLogo, GovtDoc } from '../../../sevices/account';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import { Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from '@mui/material';
// import DeleteIcon from '@mui/icons-material/Delete';
import { removeElement, isEmptyObj, setCurrentUser, removeItemFrmLocalStorage } from '../../../utils/genericUtilities';
import { ENV_CONFIG } from '../../../constant';
import Loader from '../../Loader';
import axios from 'axios';
import DoneIcon from '@mui/icons-material/Done';
import { invalid } from 'moment';

const CompanyDetails = (props) => {
    const { onNext, onBack, companyDetails, handleSnackbarClick = f => f } = props;
    const [companyType, setCompanyType] = useState('');
    const [companyWebsite, setCompanyWebsite] = useState('');
    const [companyName, setCompanyName] = useState('');
    const [companyAddress, setCompanyAddress] = useState('');
    const [state, setState] = useState('');
    const [city, setCity] = useState('');
    const [aboutCompany, setAboutCompany] = useState('');
    const [documentType, setDocumentType] = useState('');
    const [verificationData, setVerificationData] = useState('');
    const [validationError, setValidationError] = useState('');
    const [documentVerified, setDocumentVerified] = useState(false);

    const [zipCode, setZipCode] = useState('');
    const [pan, setPan] = useState('');
    const [gst, setGst] = useState('');
    const [aadhar, setAadhar] = useState('');

    const [addSocialMediaLinks, setAddSocialMediaLinks] = useState([{ value: '' }]);
    const [errorPopupOpen, setErrorPopupOpen] = useState(false);
    const [selectedFile, setSelectedFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [selectedGovtDoc, setSelectedGovtDoc] = useState([]);
    const [previewImage, setPreviewImage] = useState([]);

    var companyDetail = {};

    const handleChange = (event) => {
        setCompanyType(event.target.value);
        companyDetail.company_type = event.target.value;
        localStorage.setItem("companyRegister", JSON.stringify(companyDetail))


    };
    const handleChangedocumentType = (event) => {
        setDocumentType(event.target.value);
        companyDetail.company_type = event.target.value;
        setVerificationData('');
        setGst('')
        setAadhar('')
        setValidationError('');
        setDocumentVerified(false)


    };
    const handleVerificationDataChange = (event) => {
        if (!documentType) {
            return handleSnackbarClick(true, { type: 'error', msg: "Please select document for verification" })
        }
        const data = event.target.value;
        setVerificationData(data);
        const isValid = validateData(data, documentType);
        if (!isValid) {
            setValidationError(`Invalid ${documentTypeLabels[documentType]}`);
        } else {
            setValidationError('');
        }
    };
    const validateData = (data, documentType) => {
        if (documentType === 'Udyog Aadhar') {
            // return data.match(/^UDYAM-[a-zA-Z]*-[0-9]*-[0-9]{2,10}$/) !== null;
        } else if (documentType === 'Udyam') {
            return data.match(/^UDYAM-[a-zA-Z]*-[0-9]*-[0-9]{2,10}$/) !== null;
        } else if (documentType === 'GSTIN') {
            return data.match(/^[A-Z0-9]{15}$/) !== null;
        }
        return true;
    };

    const DocumentVerify = async () => {
        if (documentType === 'GSTIN') {
            const requestBody = {
                id_number: verificationData,
                "filing_status_get": true
            };

            axios.post('https://kyc-api.surepass.io/api/v1/corporate/gstin', requestBody, {
                headers: {
                    'Authorization': `Bearer ${ENV_CONFIG.AUTH_TOKEN}`,
                    'Content-Type': 'application/json',
                },
            })
                .then(response => {
                    if (response?.data?.success) {
                        handleSnackbarClick(true, { type: 'success', msg: "Verification Success" })
                        setDocumentVerified(true)
                        setGst(verificationData)
                    }
                })
                .catch(error => {
                    handleSnackbarClick(true, { type: 'error', msg: error?.response?.data?.message })

                    console.error('Error:', error?.response?.data?.message);
                });
        } else {
            const requestBody = {
                id_number: verificationData,
            };

            axios.post('https://kyc-api.surepass.io/api/v1/corporate/udyog-aadhaar', requestBody, {
                headers: {
                    'Authorization': `Bearer ${ENV_CONFIG.AUTH_TOKEN}`,
                    'Content-Type': 'application/json',
                },
            })
                .then(response => {

                    if (response?.data?.success) {
                        handleSnackbarClick(true, { type: 'success', msg: "Verification Success" })
                        setAadhar(verificationData)
                        setDocumentVerified(true)
                    }
                })
                .catch(error => {
                    handleSnackbarClick(true, { type: 'error', msg: error?.response?.data?.message })

                    console.error('Error:', error?.response?.data?.message);
                });
        }

    }

    const handleCompanyName = (event) => {
        setCompanyName(event.target.value);

        const updatedCompanyDetail = {
            ...companyDetail,
            company_name: event.target.value,
        };


        localStorage.setItem("companyRegister", JSON.stringify(updatedCompanyDetail));
    };

    const handleCompanyWebsite = (event) => {
        setCompanyWebsite(event.target.value);
        const updatedCompanyDetail = {
            ...companyDetail,
            company_website: event.target.value,
        };
        localStorage.setItem("companyRegister", JSON.stringify(updatedCompanyDetail));

    }

    const handleCompanyAddress = (event) => {
        setCompanyAddress(event.target.value);
        companyDetail.company_address = event.target.value;
        localStorage.setItem("companyRegister", JSON.stringify(companyDetail))

    }

    const handleCity = (event) => {
        setCity(event.target.value);
        companyDetail.city = event.target.value;
        localStorage.setItem("companyRegister", JSON.stringify(companyDetail))

    }
    const handleChangestate = (event) => {
        setState(event.target.value);
        companyDetail.state = event.target.value;
        localStorage.setItem("companyRegister", JSON.stringify(companyDetail))
    }

    const handleAboutCompany = (event) => {
        setAboutCompany(event.target.value);
        companyDetail.about_company = event.target.value;
        localStorage.setItem("companyRegister", JSON.stringify(companyDetail))
    }

    const handleLogoChange = async (event) => {
        setSelectedFile(event.target.files[0]);

    };

    // const handleGovtChange = (event) => {
    //     const selectedFiles = Array.from(event.target.files);
    //     setSelectedGovtDoc(selectedFiles);


    //     // const imagePreviews = selectedFiles.map((file) => URL.createObjectURL(file));
    //     const fileNames = selectedFiles.map((file) => file.name);

    //     setPreviewImage(fileNames);
    // }

    const handleGovtChange = (event) => {
        const selectedFiles = Array.from(event.target.files);

        // Filter out video files
        const validFiles = selectedFiles.filter((file) => {
            const fileType = file.type;
            const fileName = file.name.toLowerCase();
            if (!fileType.startsWith('image/') || fileType === 'application/pdf') {
                // setErrorMessage('Please upload only image files.');
                handleSnackbarClick(true, { type: 'error', msg: 'Please upload only image or document files.' })
                event.target.value = null;
                setSelectedGovtDoc(null)
                setPreviewImage(null)
            }
            return (
                (fileType.startsWith('image/') || fileType === 'application/pdf') &&
                !fileName.endsWith('.mp4') // Exclude MP4 video files

            );
        });

        // if (!fileName.endsWith('.mp4')) {
        //     // setErrorMessage('Please upload only image files.');
        //     handleSnackbarClick(true, { type: 'error', msg: 'Please upload only image files.' })
        //     event.target.value = null;
        //     setPreviewImage(null)
        // } else {
        //     setSelectedFile(file);
        //     setPreviewImage(URL.createObjectURL(file));
        // }

        setSelectedGovtDoc(validFiles);

        const fileNames = validFiles.map((file) => file.name);
        setPreviewImage(fileNames);
    };

    const handleImageDelete = (index) => {
        const updatedSelectedDocs = selectedGovtDoc.filter((_, i) => i !== index);
        setSelectedGovtDoc(updatedSelectedDocs);

        const updatedPreviews = previewImage.filter((_, i) => i !== index);
        setPreviewImage(updatedPreviews);
    };

    useEffect(() => {
        let loaclInfo = localStorage.getItem("companyDetails")
        let infoData = JSON.parse(loaclInfo)
        if (infoData) {
            setCompanyName(infoData?.companyName);
            setCompanyWebsite(infoData?.companyWebsite);
            setCompanyAddress(infoData?.companyAddress);
            setState(infoData?.state);
            setCity(infoData?.city);
            setZipCode(infoData?.zipCode);
            setCompanyType(infoData?.companyType);
            setAboutCompany(infoData?.aboutCompany);
            setAddSocialMediaLinks(infoData?.addSocialMediaLinks);


        }
        return () => {
            setSelectedFile({})
        }
    }, [])
    const documentTypeLabels = {
        'Udyog Aadhar': 'Udyog Aadhar Number',
        'Udyam': 'Udyam Number',
        'GSTIN': 'GSTIN Number',
    };
    const Onsubmit = async () => {
        if (!documentVerified) {
            handleSnackbarClick(true, { type: 'error', msg: "Please verify any document" })
        } else {
            setLoading(true);
            const convertedArray = addSocialMediaLinks.map(obj => obj.value);
            let body = {};
            body.company_website = companyWebsite;
            body.company_name = companyName;
            body.company_type = companyType;
            body.about_company = aboutCompany
            body.company_address = companyAddress
            body.state = state
            body.city = city
            body.zip_code = zipCode
            body.email = companyDetails.email
            body.phone = companyDetails.phone
            body.password = companyDetails.password
            body.social_media_link = convertedArray;
            body.first_name = companyDetails.firstName
            body.last_name = companyDetails.lastName
            body.username = companyDetails.email
            body.password = companyDetails.password
            body.client_id = ENV_CONFIG.CLIENT_ID;
            body.client_secret = ENV_CONFIG.CLIENT_SECRET;
            body.grant_type = "password";
            body.login_type = "password";
            body.gst = gst;
            body.aadhar = aadhar;
            body.verified_document = documentType;
            try {
                const response = await EmployerSignup(body);
                let date = new Date();
                let currentTime = date.getTime();
                const expires_in = currentTime + response?.data?.token?.expires_in * 1000;
                setCurrentUser({ ...response?.data?.token, expires_in });
                if (response?.status == 200 && response?.data?.token) {
                    let employerId = response?.data?.details?.id;
                    console.log("selectedGovtDoc",selectedGovtDoc);
                    // if (selectedFile && selectedGovtDoc) {
                    if (selectedFile && !isEmptyObj(selectedFile)) {
                        uploadLogo(employerId)
                    }

                    if (selectedGovtDoc && !isEmptyObj(selectedGovtDoc)) {
                        uploadGovtDoc(employerId)
                    }

                    removeItemFrmLocalStorage(['generalInfo', 'companyDetails'])
                    setLoading(false);
                    onNext(companyDetails)

                } else {

                }
            } catch (err) {

                handleSnackbarClick(true, { type: 'error', msg: err?.response?.data?.errors?.message ? err?.response?.data?.errors?.message : err?.response?.data?.errors[0].rules[0].message })
                // setErrorPopupOpen(true);
                setLoading(false);
            }
        }

    };
    const ErrorPopup = () => {
        return (
            <Dialog open={errorPopupOpen} onClose={() => setErrorPopupOpen(false)}>
                <DialogTitle>Error</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        please provide valid details
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setErrorPopupOpen(false)}>OK</Button>
                </DialogActions>
            </Dialog>
        );
    };
    const handleAddMore = () => {
        const values = [...addSocialMediaLinks];
        values.push({ value: '' });
        setAddSocialMediaLinks(values);
    }

    const handleDeleteMore = (index) => {
        if (index > -1) {
            const array = removeElement(addSocialMediaLinks, index);
            setAddSocialMediaLinks(array)
        }
    }

    const handleSocialMediaChange = (index, event) => {
        const values = [...addSocialMediaLinks];
        values[index].value = event.target.value;
        setAddSocialMediaLinks(values);
    };


    //   for upload image

    const uploadLogo = async (id) => {
        setLoading(true);

        const formData = new FormData();
        formData.append('photo', selectedFile);
        try {
            const response = await CompanyLogo(formData, id);
            if (response?.status == 200) {
                setLoading(false);
            } else {
            }
        } catch (err) {
            console.log('error', err)
            setLoading(false);
        }

    }

    const uploadGovtDoc = async (id) => {
        setLoading(true);

        const formData = new FormData();
        selectedGovtDoc.map((file) => {
            formData.append('photo', file);
        });
        // formData.append('photo', selectedGovtDoc);
        try {
            const response = await GovtDoc(formData, id);

            if (response?.status == 200) {
                setLoading(false);

                onNext(companyDetails)

            } else {
            }
        } catch (err) {
            setLoading(false);

            console.log('error', err)
        }

    }

    // const newData = {
    //     email,
    //     phone,
    //     password,
    //     firstName,
    //     lastName,
    //   };
    //   let genInfo ={
    //     "email":email,
    //     "phone":phone,
    //     "password":password,
    //     "firstName":firstName,
    //     "lastName":lastName,
    //   }
    //   localStorage.setItem("generalInfo",JSON.stringify(genInfo));

    const backGeneral = () => {
        const companyDetails = {
            companyName,
            companyWebsite,
            companyAddress,
            state,
            city,
            zipCode,
            companyType,
            aboutCompany,
            addSocialMediaLinks

        };
        localStorage.setItem('companyDetails', JSON.stringify(companyDetails))
        onBack()
    }
    function getFileExtension(filename) {
        return filename.split('.').pop();
    }
    return (
        <div className="WizardFormContainer">
            <div className="title">Company Details</div>
            <form>
                <Grid container spacing={5}>
                    <Grid item xs={12} sm={6}>
                        {/* <TextField
                            id="company_name"
                            label="Company name which registerd with government"
                            className="form-control"
                            value={companyName}
                            onChange={handleCompanyName}
                            // onChange={(event) => setCompanyName(event.target.value)}

                            InputProps={{
                                type: 'text',
                            }}
                        /> */}
                        <TextField
                            id="company_name"
                            label="Company name which registerd with government"
                            className="form-control"
                            value={companyName}
                            // onChange={(event) => setCompanyName(event.target.value)}
                            onChange={(e) => {
                                const regex = /^[A-Za-z ]+$/;
                                if (regex.test(e.target.value)) {
                                    setCompanyName(e.target.value);
                                }

                            }}
                            InputProps={{
                                type: 'text',
                            }}
                            inputProps={{
                                maxLength: 100
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            id="company_website"
                            label="Company website"
                            className="form-control"
                            value={companyWebsite}
                            // onChange={handleCompanyWebsite}
                            onChange={(e) => {
                                const regex = /^[A-Za-z./@:]+$/;
                                if (regex.test(e.target.value) || e.target.value === '') {
                                    handleCompanyWebsite(e);
                                }
                            }}
                            InputProps={{
                                type: 'text',
                            }}
                            inputProps={{
                                maxLength: 60
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            id="company_address"
                            label="Company address"
                            className="form-control"
                            value={companyAddress}
                            // onChange={handleCompanyAddress}
                            onChange={(e) => {
                                const regex = /^[A-Za-z 0-9 .,]+$/;
                                if (regex.test(e.target.value) || e.target.value === '') {
                                    handleCompanyAddress(e);
                                }
                            }}
                            InputProps={{
                                type: 'text',
                            }}
                            inputProps={{
                                maxLength: 100
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        {/* <TextField
                            id="state"
                            label="State"
                            className="form-control"
                            value={state}
                            onChange={handleState}
                            InputProps={{
                                type: 'text',
                            }}
                        /> */}
                        <FormControl fullWidth>
                            <InputLabel id="type_of_company">State</InputLabel>
                            <Select
                                labelId="state"
                                id="demo-simple-select"
                                className="form-control"
                                value={state}
                                label="State"
                                onChange={handleChangestate}
                            >
                                <MenuItem value={'Andhra Pradesh'}>Andhra Pradesh</MenuItem>
                                <MenuItem value={"Arunachal Pradesh"}>Arunachal Pradesh</MenuItem>
                                <MenuItem value={"Assam"}>Assam</MenuItem>
                                <MenuItem value={'Bihar'}>Bihar</MenuItem>
                                <MenuItem value={'Chhattisgarh'}>Chhattisgarh</MenuItem>
                                <MenuItem value={'Goa'}>Goa</MenuItem>
                                <MenuItem value={"Haryana"}>Haryana</MenuItem>
                                <MenuItem value={"Himachal Pradesh"}>Himachal Pradesh</MenuItem>
                                <MenuItem value={"Jharkhand"}>Jharkhand</MenuItem>
                                <MenuItem value={"Karnataka"}>Karnataka</MenuItem>
                                <MenuItem value={"Kerala"}>Kerala</MenuItem>
                                <MenuItem value={"Madhya Pradesh"}>Madhya Pradesh</MenuItem>
                                <MenuItem value={"Maharashtra"}>Maharashtra</MenuItem>
                                <MenuItem value={"Manipur"}>Manipur</MenuItem>
                                <MenuItem value={"Meghalaya"}>Meghalaya</MenuItem>
                                <MenuItem value={"Mizoram"}>Mizoram</MenuItem>
                                <MenuItem value={"Nagaland"}>Nagaland</MenuItem>
                                <MenuItem value={"Puducherry"}>Puducherry</MenuItem>
                                <MenuItem value={"Punjab"}>Punjab</MenuItem>
                                <MenuItem value={"Rajasthan"}>Rajasthan</MenuItem>
                                <MenuItem value={"Odisha"}>Odisha</MenuItem>
                                <MenuItem value={"Sikkim"}>Sikkim</MenuItem>
                                <MenuItem value={"Tamil Nadu"}>Tamil Nadu</MenuItem>
                                <MenuItem value={"Telangana"}>Telangana</MenuItem>
                                <MenuItem value={"Tripura"}>Tripura</MenuItem>
                                <MenuItem value={"Uttar Pradesh"}>Uttar Pradesh</MenuItem>
                                <MenuItem value={"Uttarakhand"}>Uttarakhand</MenuItem>
                                <MenuItem value={"West Bengal"}>West Bengal</MenuItem>
                                <MenuItem value={"Andaman and Nicobar Islands"}>Andaman and Nicobar Islands</MenuItem>
                                <MenuItem value={"Chandigarh"}>Chandigarh</MenuItem>
                                <MenuItem value={"Dadra and Nagar Haveli and Daman and Diu"}>Dadra and Nagar Haveli and Daman and Diu</MenuItem>
                                <MenuItem value={"Lakshadweep"}>Lakshadweep</MenuItem>
                                <MenuItem value={"Delhi"}>Delhi</MenuItem>
                                <MenuItem value={"Ladakh"}>Ladakh</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            id="city"
                            label="City"
                            className="form-control"
                            value={city}
                            // onChange={handleCity}
                            onChange={(e) => {
                                const regex = /^[A-Za-z]+$/;
                                if (regex.test(e.target.value) || e.target.value === '') {
                                    handleCity(e);
                                }
                            }}
                            InputProps={{
                                type: 'text',
                            }}
                            inputProps={{
                                maxLength: 100
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            id="pin_code"
                            label="Pin code"
                            className="form-control"
                            value={zipCode}
                            // onChange={(event) => setZipCode(event.target.value)}
                            onChange={(e) => {
                                const value = e.target.value;
                                const sanitizedInput = value.replace(/[^0-9]/g, '').slice(0, 10);
                                if (sanitizedInput.length <= 6) {
                                    setZipCode(sanitizedInput);
                                }
                            }}
                            inputProps={{
                                type: 'tel',
                                maxLength: 6
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel id="type_of_document"> Select Document For Verification </InputLabel>
                            <Select
                                labelId="type_of_document"
                                id="demo-simple-select"
                                className="form-control"
                                value={documentType}
                                label="select document for verification"
                                onChange={handleChangedocumentType}
                            >
                                <MenuItem value={'Udyog Aadhar'}>Udyog Aadhar</MenuItem>
                                <MenuItem value={"Udyam"}>Udyam</MenuItem>
                                <MenuItem value={"GSTIN"}>GSTIN</MenuItem>

                            </Select>
                        </FormControl>
                    </Grid>
                    {/* <Grid item xs={12} sm={6}>
                        <TextField
                            id="document"
                            label={documentTypeLabels[documentType]}
                            className="form-control"
                            value={verificationData}
                            onChange={handleVerificationDataChange}
                            error={!!validationError}
                            helperText={validationError}
                            InputProps={{
                                type: 'text',
                            }}
                        />
                    </Grid> */}
                    <Grid item xs={12} sm={6}>
                        {
                            documentVerified ? (
                                <TextField
                                    id="document"
                                    label={documentTypeLabels[documentType]}
                                    className="form-control"
                                    value={verificationData}
                                    InputProps={{
                                        type: 'text',
                                        maxLength: 15,

                                        endAdornment: (
                                            <InputAdornment position="end">
                                                <DoneIcon className="phoneVerifyIcon" />
                                            </InputAdornment>
                                        ),
                                    }}
                                    InputLabelProps={{ shrink: true }}
                                />
                            ) : (
                                <TextField
                                    id="document"
                                    label={documentTypeLabels[documentType]}
                                    className="form-control"
                                    value={verificationData}
                                    onChange={handleVerificationDataChange}
                                    error={!!validationError}
                                    helperText={validationError}
                                    InputProps={{
                                        type: 'text',
                                        maxLength: 15,
                                        endAdornment: (
                                            <InputAdornment position="end">
                                                {verificationData ? (
                                                    <Button className="phoneVerifyBtn" onClick={DocumentVerify}>
                                                        Verify
                                                    </Button>
                                                ) : null}
                                            </InputAdornment>
                                        ),
                                    }}
                                />
                            )
                        }
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel id="type_of_company">Type of company</InputLabel>
                            <Select
                                labelId="type_of_company"
                                id="demo-simple-select"
                                className="form-control"
                                value={companyType}
                                label="Type of company"
                                onChange={handleChange}
                            >
                                <MenuItem value={'Private Limited Company'}>Private Limited Company</MenuItem>
                                <MenuItem value={"Public Limited Company"}>Public Limited Company</MenuItem>
                                <MenuItem value={"Partnerships Company"}>Partnerships Company</MenuItem>
                                <MenuItem value={'Limited Liability Partnership'}>Limited Liability Partnership</MenuItem>
                                <MenuItem value={'One Person Company'}>One Person Company</MenuItem>
                                <MenuItem value={'Sole Proprietorship'}>Sole Proprietorship</MenuItem>
                                <MenuItem value={"Section 8 Company"}>Section 8 Company</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} sm={6}>

                        <TextField
                            id="outlined-uncontrolled"
                            label="Company logo"
                            className="form-control fileAttachmentInput"
                            InputLabelProps={{ shrink: true }}
                            inputProps={{ accept: "image/*" }}
                            InputProps={{
                                type: 'file',
                                accept: 'image/*', // Accept only JPEG, PNG, and GIF
                                onChange: handleLogoChange,
                                endAdornment: (
                                    <InputAdornment position="end">
                                        <AttachFileIcon />
                                    </InputAdornment>
                                ),
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={12}>
                        <TextField
                            id="about_company"
                            label="About company "
                            className="form-control"
                            value={aboutCompany}
                            onChange={handleAboutCompany}
                            InputProps={{
                                type: 'text',
                            }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        {/* <TextField
                            id="government-document"
                            label="Any government authorized document "
                            className="form-control fileAttachmentInput"
                            InputLabelProps={{ shrink: true }}
                            // inputProps={{ accept: "file/*", multiple: true }}
                            inputProps={{ accept: 'image/*,application/pdf', multiple: true }}

                            InputProps={{
                                type: 'file',
                                onChange: handleGovtChange,
                                accept: 'image/*,application/pdf',
                                // accept: 'file/*', // Accept only JPEG, PNG, and GIF
                                endAdornment: (
                                    <InputAdornment position="end">
                                        <AttachFileIcon />
                                    </InputAdornment>
                                ),
                            }}
                        /> */}

                        <TextField
                            id="government-document"
                            label="Any government authorized document"
                            className="form-control fileAttachmentInput"
                            InputLabelProps={{ shrink: true }}
                            inputProps={{ accept: 'image/*,application/pdf', multiple: true }}
                            InputProps={{
                                type: 'file',
                                onChange: handleGovtChange,
                                accept: 'image/*,application/pdf',
                                endAdornment: (
                                    <InputAdornment position="end">
                                        <AttachFileIcon />
                                    </InputAdornment>
                                ),
                            }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                        {addSocialMediaLinks.map((val, index) => {
                            return <div style={{ display: 'flex', flexDirection: 'column' }}>
                                <TextField
                                    sx={{ marginTop: '10px' }}
                                    id="social_media_link"
                                    label="Social media link"
                                    className="form-control"
                                    value={val.value}
                                    onChange={(event) => handleSocialMediaChange(index, event)}
                                    InputProps={{
                                        type: 'text',
                                        endAdornment: (
                                            index > 0 ? <InputAdornment position="end">
                                                <DeleteOutlineOutlinedIcon className='cursor-pointer' onClick={() => handleDeleteMore(index)} />
                                            </InputAdornment> : null
                                        ),
                                    }}
                                    inputProps={{
                                        maxLength: 80
                                    }}
                                />
                            </div>
                        })}
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <div className="uploadedImage imageViewContainer">
                            {previewImage.map((previewImage, index) => (
                                <div key={index} className="uploadedImage imageViewChild">
                                    <div>
                                        {/* Extract the file name and type from the URL */}
                                        {previewImage.split('/').pop()}
                                    </div>
                                    <div className='deleteImgIcon' onClick={() => handleImageDelete(index)}>
                                        <CancelOutlinedIcon />
                                    </div>
                                </div>
                            ))}
                        </div>
                    </Grid>

                    <Grid item xs={12} sm={12} className="addMoreItemBtn">
                        <Button onClick={handleAddMore}>Add more +</Button>
                    </Grid>
                    <Grid item xs={12} sm={12}>
                        <div className="WizardNextStep">
                            <Button onClick={() => Onsubmit()} variant="contained" className="WizardNextBtn">
                                <span>Next</span>
                                <EastIcon />
                            </Button>
                            <Button variant="outlined" className="WizardBackBtn" onClick={() => backGeneral()}>
                                <EastIcon />
                                <span>Back</span>
                            </Button>
                        </div>
                    </Grid>
                </Grid>
            </form>
            {errorPopupOpen ? <ErrorPopup /> : null}
            <Loader loading={loading} />
        </div>
    );

}

export default CompanyDetails;
