import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './login/style.scss'; // Import your custom CSS for additional styling
import { Navigate } from 'react-router-dom';
import { Button } from 'bootstrap';

function JobListing() {
  const [job, setJob] = useState({ title: '', description: '' });
  const imagePath = require('../assets/images/com-logo-3.png'); // Ensure you have the correct path to your image

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call API to save job listing
  };
  const jobs = [
    { title: 'Job 1', description: 'Description 1' },
    { title: 'Job 2', description: 'Description 2' },
    { title: 'Job 3', description: 'Description 3' },
    { title: 'Job 4', description: 'Description 4' },
    { title: 'Job 5', description: 'Description 5' },
    { title: 'Job 5', description: 'Description 5' },
    { title: 'Job 5', description: 'Description 5' },
    { title: 'Job 5', description: 'Description 5' },
    { title: 'Job 5', description: 'Description 5' },
    { title: 'Job 5', description: 'Description 5' },
  ];

  const handleNavigate = () => {
    Navigate("/jobview")
  }

  return (
    <div className="container my-5">
      <div className="row mb-4">
        <div className="col">
          <input height={30} width={25} type="text" className="form-control" placeholder="Search Job Listings" />
        </div>
      </div>
      <div className="row">
        {jobs.map((job, index) => (
          <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={index}>
            <div className="card custom-card">
              <img
                style={{ width: '20%', height: 'auto' }}
                src={imagePath}
                className="card-img-top"
                alt="..."
              />
              <div className="card-body">
                <h5 className="card-title">{job.title}</h5>
                <p className="card-text">Lorem ipsum dolor sit amet consectetur adipisicing elit. deserunt quasi!</p>
                <button onClick={handleNavigate}  className="btn btn-primary">Apply for this job</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default JobListing;
