import React, { useEffect, useState } from 'react';
import { Button, Link, Menu, Select, MenuItem, InputLabel, FormControl, TextField, Fade, Badge } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import NotificationsIcon from '@mui/icons-material/Notifications';

import { useNavigate, useLocation } from 'react-router-dom';
import { EmployerProfile, NotificationsList, employeeProfile } from '../../sevices/account';
import { Spin } from 'antd';
import _ from 'lodash';
import '../header/style.scss';
import { getCurrentUserData, removeItemFrmLocalStorage } from '../../utils/genericUtilities';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime'
import OneSignal from 'react-onesignal';
dayjs.extend(relativeTime)

function EmployeeHeader() {
    const [menuToggled, setMenuToggled] = useState(false);
    const navigate = useNavigate();
    const location = useLocation();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [anchorElNotifi, setAnchorElNotifi] = React.useState(null);
    const [profileInfo, setProfileInfo] = React.useState(null);
    const [firstName, setFirstName] = React.useState(null);
    const [companyName, setCompanyName] = React.useState(null);
    const [logo, setLogo] = React.useState(null);
    const [meta, setMeta] = React.useState(null);
    const [loading, setLoading] = React.useState(true);
    const currentPage = location.pathname;
    const hideSearchBar = currentPage === '/explorejobs';
    const [name, setname] = useState('')
    const [photo, setphoto] = useState('')
    const [isModalVisible, setModalVisible] = useState(false);
    const [dataList, setDataList] = useState([]);
    const [notifications, setNotifications] = useState([]);
    const [notificationsCount, setNotificationsCount] = useState(0);
    const [employeeID, setEmployeeID] = React.useState(null);

      useEffect(() => {
if (employeeID) {
    setTimeout(() => {
        console.log("drdefsdfsdf44");
        OneSignal.login(`partTimer-${employeeID}`);
      }, 5000);
}
    }, [employeeID])
    const userAuth = getCurrentUserData()
    const open = Boolean(anchorEl);
    const openNotifi = Boolean(anchorElNotifi);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };
    const handleNotification = (event) => {
        setAnchorElNotifi(event.currentTarget);

    };
    const handleRedirectNotifi = (item) => {
        setAnchorElNotifi(null);
        navigate(`/my-jobs?roles=employee&key=${item?.notification_type}`)

    };
    const handleCloseNotifi = () => {
        setAnchorElNotifi(null);
    };
    const handleProfileNavigate = (data) => {

        navigate("/parttimer-profile?roles=employee")
    };
    const handleAccountNavigate = (data) => {

        navigate("/my-jobs?roles=employee")
    };
    const redirectPostJob = () => {
        navigate("/signup?roles=employee")
    };
    const handleLogout = () => {
        removeItemFrmLocalStorage(['token', 'currentUser'])
        navigate("/")
    }

    const fetchNotificationsData = async () => {
    let body ={
        status:1
    }
        if (userAuth) {
            const response = await NotificationsList(body);
            console.log("response?.data?.details===>",response?.data?.details);
            setNotifications(response?.data?.details)
            setNotificationsCount(response?.data?.unreaded)
        }
    }

    useEffect(() => {
        fetchJobData();
        fetchNotificationsData();
    }, [loading,isModalVisible])

    const fetchJobData = async () => {

        if (userAuth) {
            const employee = await employeeProfile({});
            let photos = employee?.data

            setphoto(photos)
            setEmployeeID(employee?.data?.details?.id)
            let Name = employee?.data?.details?.first_name
            setname(Name)
        }
    }
    useEffect(() => {
        if (menuToggled) {
            document.body.classList.add('menuOpen');
        } else {
            document.body.classList.remove('menuOpen');
        }
    }, [menuToggled]);

    const menuToggle = () => {
        setMenuToggled(!menuToggled);
    };
    return (
        <div className="fullWidth">
            {/* <Spin spinning={loading}> */}
            {/* Employeer Before Login Header Start */}
            {(location.pathname === '/explore-certifications' || location.pathname === '/parttimer-profile' || location.pathname === '/employeesignin' || location.pathname === '/descriptionjob' || location.pathname === '/my-jobs' || location.pathname === '/explorejobs' || location.pathname === '/descriptionCertificate') &&
                <div className="EmployeerHeaderContainer">
                    <div className="d-flex align-center">
                        <Button className="mobileMenuBtn hideWeb" display={{ xs: "block", lg: "none" }} onClick={menuToggle}>
                            <img src={require("../../assets/images/menu.png")} className="mobileMenuImg" />
                        </Button>
                        <Link href="/">
                            <img src={require("../../assets/images/Logo Blue TM.png")} className="logo" />
                        </Link>
                    </div>
                    <div className="EmployeerHeaderMenu">
                        <Link href="javascript:;" display={{ xs: "block", md: "none" }} className="mobileLogo hideWeb">
                            <img src={require("../../assets/images/Logo White TM.png")} className="logo" />
                        </Link>
                        {userAuth ? (<>
                            <div className="menuDropdown">
                                <Button
                                    id="fade-button"
                                    href="/explore-certifications"

                                >
                                    <span>Certification Course</span>

                                </Button>

                            </div>
                            <div className="menuDropdown">
                                <Button
                                    id="fade-button"
                                    href="/"

                                >
                                    <span>Part Timer</span>

                                </Button>

                            </div><div className="menuDropdown">
                                <Button
                                    id="fade-button"
                                    href="/my-jobs?roles=employee"
                                >
                                    <span>My jobs</span>
                                </Button>
                                <Menu
                                    id="fade-menu"
                                    MenuListProps={{
                                        'aria-labelledby': 'fade-button',
                                    }}
                                    anchorEl={anchorEl}
                                    open={open}
                                    onClose={handleClose}
                                    TransitionComponent={Fade}
                                >
                                    <MenuItem onClick={() => {
                                        handleProfileNavigate();
                                        handleClose();
                                    }}>Profile</MenuItem>
                                    <MenuItem onClick={handleClose}>Logout</MenuItem>
                                </Menu>
                            </div>
                            {location.pathname !== '/explore-certifications' || location.pathname !== '/explorejobs' || location.pathname !== '/descriptionjob' || location.pathname !== '/employeesignin' && <form>
