import React, { useState, useEffect } from 'react';
import { Select, MenuItem, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import { Spin } from 'antd';
import { PostJobList, PostJobStatus } from '../../../sevices/job';
import { useNavigate, useLocation, Navigate } from 'react-router-dom';
import { ReactComponent as NoDataImg } from '../../../assets/images/no-data-employer.svg';
import '../employerStyle.scss'
import { isEmptyObj } from '../../../utils/genericUtilities';
import CustomSnackBar from '../../../shared/snackBar';
const InactiveTab = (props) => {
    const [jobList, setJoblist] = useState([]);
    const [loading, setLoading] = useState(true);
    const [page, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [metadata, setMetaData] = useState(null);
    const [containerRef, setContainerRef] = useState(null);
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const value = searchParams.get('jobId');
    const [selectedValue, setSelectedValue] = useState(jobList.map(() => "0"));
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        fetchJobData();
    }, []);


    const fetchJobData = async (page = 1) => {
        setLoading(true);
        try {

            const response = await PostJobList({ page, status: 0, limit: 10 });
            if (response?.status === 200) {
                // setJoblist([])
                const jobItems = response?.data?.items;
                const jobMeta = response?.data?.meta;
                const nextPage = page + 1;
                setPage(nextPage);
                // setJoblist(prevState => [...prevState, ...jobItems]);
                setJoblist(jobItems);

                setMetaData({ ...jobMeta });
                setLoading(false);
            }

        } catch (error) {
            console.log('Error fetching job data:', error);
            return [];
        } finally {
            setLoading(false);
        }
    };

    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };


    const handleChange = async (event, index, rowId, data) => {
        setLoading(true)
        // debugger
        if (data?.payment_status != 9) {
            navigate(`/jobPostPayment?value=${rowId}`);
        } else {
            setLoading(true)
            const newSelectedValues = [...selectedValue];
            newSelectedValues[index] = event.target.value;
            // setSelectedValue(newSelectedValues);
            const body = {
                id: rowId,
                status: event.target.value
            }
            // const response = await PostJobStatus(body);

            // await fetchJobData(page);


            try {
                const response = await PostJobStatus(body);
                if (response?.status == 200) {
                    // await fetchJobData(page);
                    // fetchJobData()
                    await fetchJobData();
                    setLoading(false)
                    handleSnackbarClick(true, { type: 'success', msg: `${response?.data?.details?.title} is now ${response?.data?.details?.status === 1 ? 'active' : 'inactive'} ` })

                }

                // fetchJobData();
            } catch (error) {
                handleSnackbarClick(true, { type: 'error', msg: "Something Went wrong" })
            }

        }
    };

    const handleScroll = () => {
        if (
            containerRef &&
            containerRef.scrollTop + containerRef.clientHeight >= containerRef.scrollHeight
        ) {
            handleFetchJobsForScroll();
        }
    };

    const handleFetchJobsForScroll = () => {
        const totalJobCount = metadata?.total ? metadata?.total : 0;
        if (!loading && totalJobCount !== jobList?.length) {
            fetchJobData(page);
        }
    }

    return (

        <div className="activeJobListView position-relative">
            {/* <div className="JobCardFilter">
                <div className="JobCardSearchBar">
                    <TextField
                        id="last_name"
                        label="Search here..."
                        className="form-control"
                        InputProps={{
                            type: 'text',
                        }}
                    />
                    <Button>
                        <SearchIcon />
                    </Button>
                </div>
            </div> */}
            <div className="employerTabel" >
                <TableContainer component={Paper} ref={ref => setContainerRef(ref)}
                    onScroll={handleScroll} className="tabelContainer">
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Job title</TableCell>
                                <TableCell align="center">Job ID</TableCell>
                                <TableCell align="center">Posted on</TableCell>
                                {/* <TableCell align="center">New Applications</TableCell>
                                <TableCell align="center">Rejected Applications</TableCell> */}
                                <TableCell align="center">Status</TableCell>
                            </TableRow>
                        </TableHead>


                        <TableBody>
                            {jobList.length === 0 && !loading ? (
                                <TableRow>
                                    <TableCell colSpan={6}>
                                        {/* <div className="noData">
                                            No data available

                                        </div> */}
                                        <div className="noData"><NoDataImg /></div>
                                    </TableCell>
                                </TableRow>
                            ) : (
                                <>
                                    {jobList.map((data, index) => (
                                        <TableRow key={index}>
                                            <TableCell>{data?.title}</TableCell>
                                            <TableCell align="center">{data?.id}</TableCell>
                                            <TableCell align="center">{data?.created_at.split("T")[0]}</TableCell>
                                            {/* <TableCell align="center">0</TableCell>
                                            <TableCell align="center">0</TableCell> */}
                                            <TableCell align="center">
                                                <Select
                                                    value={selectedValue[index] || "0"} onChange={(event) => handleChange(event, index, data.id, data)}
                                                    className="tableSelectBtn selectInactive"
                                                // className={data?.status === 0 ? 'tableSelectBtn selectInactive' : ''}
                                                >
                                                    <MenuItem value="1">Active</MenuItem>
                                                    <MenuItem value="0">Inactive</MenuItem>
                                                </Select>
                                            </TableCell>
                                        </TableRow>

                                    ))}

                                </>
                            )}

                        </TableBody>
                        {loading && (
                            <TableBody>
                                <TableCell colSpan={6}>
                                    <div className="tableLoader">
                                        <Spin spinning={true} />
                                    </div>
                                </TableCell>
                            </TableBody>
                        )}




                    </Table>
                </TableContainer>
            </div>
            {/* {loading && (

                <div className="inactiveloader">
                    <Spin spinning={true} />
                </div>

            )} */}
            {openSnackbar ? <CustomSnackBar
                openSnackbar={openSnackbar}
                snackBarMessage={customMsg?.msg}
                status={customMsg?.type}
                handleSnackbarClick={handleSnackbarClick}
            /> : null}
        </div>
    )
}
export default InactiveTab;
