import React, { useEffect, useState } from 'react';
import Grid from '@mui/material/Grid';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Link } from '@mui/material';
import '../employer/employerStyle.scss'
import { Spin } from 'antd';
import { ReactComponent as MailBanner } from '../../assets/images/subscriptionBg1.svg';
import { PostJobList, JobApplicationList, JobApplicationView, PostJobView } from '../../sevices/job';
import { getCurrentUserData, isEmptyObj } from '../../utils/genericUtilities';
import { useNavigate } from "react-router-dom";
import _ from 'lodash';
import { EmployerSubscriptionVerify, EmployerSubscriptionView, EmployersubscribeVerify } from '../../sevices/account';
import { ReactComponent as NoDataImg } from '../../assets/images/no-data-employer.svg';
import CustomSnackBar from '../../shared/snackBar';
import Loader from '../Loader';
function DashboardPage() {
    const navigate = useNavigate();
    const [jobList, setJoblist] = useState([]);
    const [loading, setLoading] = useState(true);
    const [page, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [metadata, setMetaData] = useState(null);
    const [containerRef, setContainerRef] = useState(null);
    const [jobApplyList, setJobApplylist] = useState([]);
    const [validity, setvalidity] = useState('')
    const [photo, setphoto] = useState('')
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});
    const [load, setload] = useState(false)
    const queryParams = new URLSearchParams(window.location.search);
    const SessionId = queryParams.get('session_id');


    const userAuth = getCurrentUserData()


    useEffect(() => {
        if (SessionId) {
            ValidatePayment()
        }
    }, [SessionId])

    const ValidatePayment = async () => {
        const body = {}
        body.checkout = SessionId
        try {
            const response = await EmployerSubscriptionVerify(body);
            if (response?.status == 200) {
                SubscriptionView();
            }

        } catch (error) {
            console.error('CHEckoutResponse error:', error);
        }
    }

    useEffect(() => {
        fetchJobData();
        fetchJobApplication();
        setTimeout(() => {
            if (!SessionId) {
                SubscriptionView();
            }
        }, 500);
    }, [])


    const handleNavigate = (id) => {
        navigate(`/viewjob?jobId=${id}`)
    }
    const SubscriptionView = async () => {
        setLoading(true)
        try {
            const View = await EmployerSubscriptionView({})
            if (View.status === 200) {
                let timestamp = View.data.details.current_period_end
                const targetDate = new Date(timestamp * 1000)
                const currentDate = new Date();
                const timeDiff = targetDate.getTime() - currentDate.getTime();
                const daysRemaining = Math.floor(timeDiff / (1000 * 3600 * 24));
                console.log("PAYAMCUCNC", View?.data?.details);
                setvalidity(daysRemaining)
                if (View?.data?.details?.status === 'canceled') {
                    console.log("bcadcd", View?.data?.details);
                    navigate("/employersubscription?roles=employer&data=renewal")
                }
            }
        } catch (error) {
            console.error('BCKCN2:', error);
            setLoading(false)
            console.error('BCKCN256:', error?.response?.status);
            if (error?.response?.status === 500) {
                console.error('SUBSCRIPTION NNOT FOUND:');
                navigate("/employersubscription?roles=employer")
            }
        }
    }


    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };

    const handleViewClick = async (id) => {
        setload(true);

        try {
            // setLoading(true);
            const response = await JobApplicationView(id)
            if (response?.status === 200) {
                const jobApplicationList = response?.data?.items;
                setload(false);
                if (jobApplicationList.length === 0) {
                    handleSnackbarClick(true, { type: 'error', msg: "No applicants have applied for this job yet" })
                } else {
                    // navigate(`/viewCandidate?id=${id}`);
                    try {
                        const params = { id: id }
                        const response = await PostJobView({ params: params });
                        if (response?.data?.details?.part_timer_payment_status === 9) {
                            navigate(`/employeeview?id=${id}`);
                        } else {
                            navigate(`/viewCandidate?id=${id}`);
                        }

                    } catch (error) {
                        handleSnackbarClick(true, { type: 'error', msg: "Error fetching job data" })
                    }
                }

            }
        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: "Error fetching job data" })
        } finally {
            // setLoading(false);
        }

    }

    const fetchJobData = async (page = 1) => {
        try {
            setLoading(true);
            const response = await PostJobList({ page, limit: 10 });
            if (response?.status === 200) {
                const jobItems = response?.data?.items;
                const jobMeta = response?.data?.meta;
                const nextPage = page + 1;

                setPage(nextPage);
                jobItems?.length && setJoblist(prevJobList => [...prevJobList, ...jobItems]);
                setMetaData({ ...jobMeta });
                const activeJobLength = jobItems.filter(job => job.status === 1);
            }
        } catch (error) {
            return [];
        } finally {
            setLoading(false);
        }
    };

    const fetchJobApplication = async () => {
        try {
            setLoading(true);
            const response = await JobApplicationList()
            // const response = await PostJobList({ page, limit: 10 });
            if (response?.status === 200) {
                console.log("METTYVAVAC", response);
                setphoto(response?.data?.meta)
                const jobApplicationList = response?.data?.items;
                setJobApplylist(jobApplicationList)
            }
        } catch (error) {
            console.log('Error fetching job data:', error);
            return [];
        } finally {
            setLoading(false);
        }
    };


    const handleScroll = () => {
        if (
            containerRef &&
            containerRef.scrollTop + containerRef.clientHeight >= containerRef.scrollHeight
        ) {
            handleFetchJobsForScroll();
        }
    };

    const handleFetchJobsForScroll = () => {
        const totalJobCount = metadata?.total ? metadata?.total : 0;
        if (!loading && totalJobCount !== jobList?.length) {
            fetchJobData(page);
        }
    }


    return (
        <>
            {jobList ? <div className="dashboardPageContainer">
                <div className="mainTitle">Dashboard</div>
                <div className="fullWidth">
                    <Grid container spacing={5}>
                        <Grid item xs={12} sm={5.5}>
                            <div className="postedJobsCard">
                                <span>Total Jobs Posted </span>
                                <strong>{metadata?.total}</strong>
                            </div>
                        </Grid>
                        <Grid item xs={12} sm={5.5}>
                            <div className="postedJobsCard" style={{ backgroundColor: '#EFFFFF' }}>
                                <span>Active jobs</span>
                                <strong style={{ color: '#00702D' }}>{jobList && jobList.filter(job => job?.status === 1).length}</strong>
                            </div>
                        </Grid>
                    </Grid>
                </div>
                <div className="fullWidth">
                    <Grid container spacing={5}>
                        <Grid item xs={12} sm={6}>
                            <div className="dashboardCardTitle">Jobs list</div>
                            <div className="employerTabel">
                                <TableContainer component={Paper} ref={ref => setContainerRef(ref)}
                                    onScroll={handleScroll} className="tabelContainer" style={{ maxHeight: '55vh' }}>
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>Job title</TableCell>
                                                {/* <TableCell align="center">Applications</TableCell> */}
                                                <TableCell align="center">Status</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {jobList.length === 0 && !loading ? (
                                                <TableRow>
                                                    <TableCell colSpan={6}>
                                                        <div className="noData">
                                                            No data available
                                                        </div>
                                                    </TableCell>
                                                </TableRow>
                                            ) : (
                                                <>
                                                    {jobList.map((data, index) => (
                                                        <Link style={{ cursor: 'pointer', textDecoration: 'none', width: '100%', display: 'table-row' }} onClick={() => handleNavigate(data?.id)}>
                                                            <TableRow style={{ minWidth: '100%', display: 'contents' }} key={index}>
                                                                <TableCell>{data?.title}</TableCell>
                                                                <TableCell align='center' >
                                                                    {data?.status === 0 ? <Button variant="contained" className="inActiveBtn">Inactive</Button> : <Button variant="contained" className="activeBtn">Active</Button>}
                                                                </TableCell>
                                                            </TableRow>
                                                        </Link>
                                                    ))}
                                                    {loading && (
                                                        <TableRow>
                                                            <TableCell colSpan={6}>
                                                                <div className="tableLoader">
                                                                    <Spin spinning={true} />
                                                                </div>
                                                            </TableCell>
                                                        </TableRow>
                                                    )}
                                                </>
                                            )}
                                        </TableBody>

                                    </Table>
                                </TableContainer>
                            </div>
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <div className="dashboardCardTitle">Subscription Details</div>
                            <div className="subscriptionvalidityCard">
                                <div className="cardHeader">
                                    <img src={require('../../assets/images/date-icon.png')} className="ScardHeaderImg" alt="" />
                                    <div className="cardHeaderContent">
                                        <span>Your Subscription Pack Ends Within</span>
                                        {/* <Spin spinning={true} /> */}

                                        <strong>
                                            {loading ? <Spin spinning={true} /> :
                                                validity} {" "}
                                            Days</strong>
                                    </div>
                                </div>
                                <div className="cardBody">
                                    <div className="cardBodyTitle">
                                        Gets subscribe and gets our benefits like:
                                    </div>
                                    <div className="cardPlanSet">
                                        <img src={require('../../assets/images/check-icon.png')} alt="" />
                                        <div className="cardPlanContent">
                                            <div className="cardPlanContentTitle">Unlimited Resumes and Social profile search</div>
                                            <span>Search and Contact over 80 thousand candidates in Part timer Resume Database</span>
                                        </div>
                                    </div>
                                    <div className="cardPlanSet">
                                        <img src={require('../../assets/images/check-icon.png')} alt="" />
                                        <div className="cardPlanContent">
                                            <div className="cardPlanContentTitle">Unlimited Resume match notification</div>
                                            <span>Receive instant notifications when new resumes are added that match your search criteria.</span>
                                        </div>
                                    </div>
                                    <div className="cardPlanSet">
                                        <img src={require('../../assets/images/check-icon.png')} alt="" />
                                        <div className="cardPlanContent">
                                            <div className="cardPlanContentTitle">Job post Email</div>
                                            <span></span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Grid>
                    </Grid>
                </div>
                <div className="fullWidth">
                    <Grid container spacing={5}>
                        <Grid item xs={12} sm={12}>
                            <div className="dashboardCardTitle">
                                <span>Recently applied candidates</span>

                            </div>
                            <div className="employerTabel">
                                <TableContainer component={Paper} className="tabelContainer">
                                    <Table
                                    >
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>Name</TableCell>
                                                <TableCell>Job title</TableCell>
                                                <TableCell>Job ID</TableCell>
                                                <TableCell align="center">Action</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>


                                            {jobApplyList.length === 0 && !loading ? (
                                                <TableRow>
                                                    <TableCell colSpan={6}>
                                                        <div className="noData"><NoDataImg /></div>
                                                    </TableCell>
                                                </TableRow>
                                            ) : (
                                                <>
                                                    {jobApplyList.map((data, index) => (

                                                        <TableRow key={index}>

                                                            <TableCell>
                                                                <div className="d-flex align-center">
                                                                    {data?.employee_profile?.employee_photo ? <img
                                                                        title={data?.employee_profile?.first_name}
                                                                        src={`${_.get(photo, 'employee_photo.path')}/${_.get(photo, 'employee_photo.folder')}/${_.get(photo, 'employee_photo.sizes.medium')}/${_.get(data?.employee_profile, 'employee_photo')}`}

                                                                        className="userImg"
                                                                        alt="User Img"
                                                                    /> : <img
                                                                        title={data?.employee_profile?.first_name}
                                                                        src={require('../../assets/images/user.png')}
                                                                        className="userImg"
                                                                        alt="User Img"
                                                                    />}
                                                                    <span>{data?.employee_profile?.first_name}</span>
                                                                </div>
                                                            </TableCell>
                                                            <TableCell>{data?.job_post.title}</TableCell>
                                                            <TableCell>{data?.job_post.id}</TableCell>
                                                            <TableCell align="center">
                                                                <Button variant="contained" onClick={() => handleViewClick(data?.job_post.id)} className="viewBtn">View</Button>
                                                            </TableCell>
                                                        </TableRow>
                                                    ))}
                                                    {loading && (
                                                        <TableRow>
                                                            <TableCell colSpan={6}>
                                                                <div className="tableLoader">
                                                                    <Spin spinning={true} />
                                                                </div>
                                                            </TableCell>
                                                        </TableRow>
                                                    )}
                                                </>
                                            )}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            </div>
                        </Grid>
                    </Grid>
                </div>
            </div> : <div className="applicationStatusPage emptyDashBoardPage">
                <Grid container alignItems="center" justifyContent="center" spacing={2.5}>
                    <Grid item xs={12} sm={6}>
                        <div className="applicationStatusContainer">
                            <div className="applicationStatusBanner">
                                <MailBanner />
                            </div>
                            <div className="noDataText">No Data's found, Post a job and gets our benifits</div>
                            <div className="PostJobBannerCheckListSet">
                                <img src={require('../../assets/images/check-icon.png')} alt="" />
                                <span>Part-Timer have a large user base and attract a diverse range of job seekers.</span>
                            </div>
                            <div className="PostJobBannerCheckListSet">
                                <img src={require('../../assets/images/check-icon.png')} alt="" />
                                <span>Part-Timer allow you to target specific industries, job roles, and skill sets.
                                    You can customize your job posting to ensure it reaches the most relevant
                                    candidates, increasing the likelihood of finding qualified individuals for your
                                    specific requirements.</span>
                            </div>
                            <div className="PostJobBannerCheckListSet">
                                <img src={require('../../assets/images/check-icon.png')} alt="" />
                                <span>Posting a job on a Part-Timer is often more cost-effective compared to other
                                    advertising options. Many job portals offer affordable pricing plans, allowing
                                    employers with different budgets to access their services.</span>
                            </div>
                            <div className="PostJobBannerCheckListSet">
                                <img src={require('../../assets/images/check-icon.png')} alt="" />
                                <span>Part-Timer provide analytics and reporting tools that offer insights into your
                                    job posting's performance.</span>
                            </div>

                            <div className="buttonContainer">
                                <Button className="backBtn">Post a job</Button>
                            </div>
                        </div>
                    </Grid>
                </Grid>
                {openSnackbar ? <CustomSnackBar
                    openSnackbar={openSnackbar}
                    snackBarMessage={customMsg?.msg}
                    status={customMsg?.type}
                    handleSnackbarClick={handleSnackbarClick}
                /> : null}

                <Loader loading={load} />
            </div>}
        </>



    );
}

export default DashboardPage;
