import React, { useState, useEffect } from 'react';
import { Grid, Tab, Tabs, Button, TextField } from '@mui/material';
import { Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from '@mui/material';
import EastIcon from '@mui/icons-material/East';
import { ReactComponent as SocialMedia } from '../../assets/images/social-media.svg';
import { ReactComponent as Catering } from '../../assets/images/catering.svg';
import { ReactComponent as TranslatorIcon } from '../../assets/images/translator-icon.svg';
import { ReactComponent as AccountanIocn } from '../../assets/images/accountan-icon.svg';
import { ReactComponent as WritingIcon } from '../../assets/images/writing-icon.svg';
import { ReactComponent as ShopIcon } from '../../assets/images/shop-icon.svg';
import { ReactComponent as SignUpBanner } from '../../assets/images/post-job-banner.svg';
import { CertificationView, PostJobView } from '../../sevices/job';
import { CertificationAplicationCreate, EmployeeSubscriptionView, JobAplicationCreate } from '../../sevices/account';
import { useNavigate, useLocation } from 'react-router-dom';

import './part-timer-style.scss';
import { getCurrentUserData, isEmptyObj } from '../../utils/genericUtilities';
import moment from 'moment';
import Loader from '../Loader';
import dayjs from 'dayjs';
import { VALIDATION_MSG } from '../../constant';
import { DiscriptionCardLoader1 } from '../loaders/loaders';
import CustomSnackBar from '../../shared/snackBar';
import SEO from '../../utils/SEO'


const DescriptionCertificate = () => {
    const [loading, setLoading] = useState(false);
    const [selectedTab, setSelectedTab] = React.useState(0);
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const navigate = useNavigate();
    const userData = getCurrentUserData();
    const [jobView, setJobView] = useState('');
    const [job, setJob] = useState('');
    const [daysRemaining, setDaysRemaining] = useState('')
    const [workingTiming, setWorkingTiming] = useState('')
    const [errorPopupOpen, setErrorPopupOpen] = useState(false);
    const [errorMessage, setSrrorMessage] = useState('');
    const [createdDate, setcreatedDate] = useState('');
    const [phoneError, setPhoneError] = useState('');
    const [emailError, setEmailError] = useState('');

    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [customMsg, setCustomMsg] = useState({});

    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const [isLoading, setIsLoading] = useState(false);
    const jobid = searchParams.get('id');
    const ErrorPopup = () => {
        return (
            <Dialog open={errorPopupOpen} onClose={() => setErrorPopupOpen(false)}>
                <DialogTitle>Oops!</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        {errorMessage}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setErrorPopupOpen(false)}>OK</Button>
                </DialogActions>
            </Dialog>
        );
    };

    useEffect(() => {
        jobDescriptionData()
    }, [])

    useEffect(() => {
        const currentDate = new Date();
        const inputDate = new Date(jobView?.created_at);
        const timeDifference = currentDate.getTime() - inputDate.getTime();
        const days = Math.ceil(timeDifference / (1000 * 3600 * 24));
        setDaysRemaining(days);
    }, [jobView?.created_at]);



    const handleSnackbarClick = (status, msgInfo) => {
        setOpenSnackbar(status);
        !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
    };




    const jobDescriptionData = async () => {
        const params = { id: jobid }
        setIsLoading(true)
        try {
            const response = await CertificationView({ params: params });
            setIsLoading(false)

            console.log("resssssss", response?.data?.details);
            setJob(response?.data)
            let ViewData = response?.data?.details;
            setJobView(ViewData)
            convertAndAddHours(ViewData?.working_timing, ViewData?.job_timing_slot)

            const utcDate = new Date(ViewData?.created_at);
            const indianStandardTime = new Date(utcDate.getTime() + 5.5 * 60 * 60 * 1000);
            // Extract day, month, and year from the Indian Standard Time date object
            const day = indianStandardTime.getDate().toString().padStart(2, "0");
            const month = (indianStandardTime.getMonth() + 1).toString().padStart(2, "0"); // Month is zero-indexed, so add 1
            const year = indianStandardTime.getFullYear().toString().slice(-4);
            const formattedDate = `${day}-${month}-${year}`;
            setcreatedDate(formattedDate)
            setIsLoading(false)
        } catch (error) {
            handleSnackbarClick(true, { type: 'error', msg: error?.response?.data?.message })
        }

    }

    const convertAndAddHours = (workingTiming24, jobTimingSlot) => {
        const [startHour, startMinute] = workingTiming24.split(':');
        const startHourInt = parseInt(startHour, 10);
        const endHourInt = startHourInt + parseInt(jobTimingSlot, 10);
        const startAmPm = startHourInt >= 12 ? 'PM' : 'AM';
        const endAmPm = endHourInt >= 12 ? 'PM' : 'AM';
        const startHour12 = startHourInt === 0 ? 12 : startHourInt > 12 ? startHourInt - 12 : startHourInt;
        const endHour12 = endHourInt === 0 ? 12 : endHourInt > 12 ? endHourInt - 12 : endHourInt;
        const formattedStartTime = `${startHour12.toString().padStart(2, '0')}:${startMinute} ${startAmPm}`;
        const formattedEndTime = `${endHour12.toString().padStart(2, '0')}:${startMinute} ${endAmPm}`;
        setWorkingTiming(`${formattedStartTime} - ${formattedEndTime}`)

        return `${formattedStartTime} - ${formattedEndTime}`;
    };


    const Onsubmit = async () => {
        setLoading(true);
        let body = {
            "course_id": jobView?.id
        }


        try {
            const View = await EmployeeSubscriptionView({})
            if (View.status === 200) {
                let timestamp = View?.data?.details?.current_period_end
                const targetDate = new Date(timestamp * 1000)
                const currentDate = new Date();
                const timeDiff = targetDate.getTime() - currentDate.getTime();
                const daysRemaining = Math.floor(timeDiff / (1000 * 3600 * 24));

                if (View?.data?.details?.status != 'active') {
                    navigate("/employee-subscription?roles=employee&data=renewal")
                } else {
                    try {
                        // const response = await JobAplicationCreate(body);
                        const response = await CertificationAplicationCreate(body);

                        if (response?.status === 200) {
                            // setPopupOpen(true);
                            handleSnackbarClick(true, { type: 'success', msg: "Certification Course Applied Successfully " })
                            setTimeout(() => {
                                navigate("/explore-certifications");

                            }, 3000);
                            setLoading(false)
                        }
                    } catch (error) {
                        if (error?.response?.data?.message) {
                            // setPopupOpen(true);
                            // navigate("/explorejobs?roles=employee");
                            if (error?.response?.data?.message === "already applyed") {


                                handleSnackbarClick(true, { type: 'error', msg: "Oops! You've already applied for this job." })
                                // setSrrorMessage("already applyed for this job");
                                // setErrorPopupOpen(true);
                                // setSrrorMessage("You've already applied for this job.");
                                // setErrorPopupOpen(true);
                                setLoading(false)

                            } else {
                                handleSnackbarClick(true, { type: 'error', msg: error?.response?.data?.message })
                                // setSrrorMessage("somthing went wrong");
                                // setErrorPopupOpen(true);
                                setLoading(false)
                            }
                        }
                    }
                }

            }
        } catch (error) {
            console.error('BCKCN225454:', error);
            if (error?.response?.status === 500) {
                console.error('SUBSCRIPTION NNOT FOUND:');
                navigate("/employee-subscription?roles=employee")
            }
        }




    }

    const handleTabChange = (event, newValue) => {
        setSelectedTab(newValue);
    };

    const handlelogin = () => {
        navigate("/login?roles=employee")
    }
    const handlesignup = () => {

        if (!email || !phone) {
            setEmailError(VALIDATION_MSG.emailRequired);
            setPhoneError(VALIDATION_MSG.phoneRequired);
            return;
        }
        // if (!phone) {
        //     setPhoneError(VALIDATION_MSG.phoneRequired);
        //     return;
        // }

        if (email && phone?.length >= 10) {

            const data = {
                email: email,
                phone: phone
            }
            navigate("/employeesignin", { state: data })
        } else {

        }

    }

    const handlePhoneBlur = () => {
        const phoneRegex = /^\d{10}$/;
        const isValid = phoneRegex.test(phone);

        if (!phone) {
            setPhoneError(VALIDATION_MSG.phoneRequired);
        } else if (!isValid) {
            setPhoneError(VALIDATION_MSG.phoneLength);
        } else {
            setPhoneError('');
        }

        return isValid;
    };
    const validateEmail = () => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
        const isValid = emailRegex.test(email);

        if (!email) {
            setEmailError(VALIDATION_MSG.emailRequired);
        } else if (!isValid) {
            setEmailError(VALIDATION_MSG.invalidEmailAddress);
        } else {
            const afterCom = email.split('.com')[1];
            const containsNumbersAfterCom = /\d/.test(afterCom);
            if (containsNumbersAfterCom) {
                setEmailError('The email after ".com" should not contain numbers.');
                return false;
            }
            setEmailError('');
        }
        return isValid;
    };

    return (
        <div className="discriptionPage">
            {/* <SEO
                title={jobView?.title}
                description={jobView?.job_description}
                name={jobView?.employer_profile?.company_name}
                image={`${job?.meta?.company_logo?.path}/${job?.meta?.company_logo?.folder}/${job?.meta?.company_logo?.sizes?.medium}/${jobView?.employer_profile?.company_logo}`}
                type='website' /> */}
            <Grid container spacing={5}>
                <Grid item xs={12} sm={12} lg={7.5}>
                    <div className="discriptionLeftCard">
                        {isLoading ? (
                            <DiscriptionCardLoader1 />
                        ) : (
                            <>
                                <div className="companyCard">
                                    <div className="cardHeaderSet">
                                        {/* <div className="cardHeaderLogoSet">

                                            {jobView?.employer_profile?.company_logo != null ? <img src={`${job?.meta?.company_logo?.path}/${job?.meta?.company_logo?.folder}/${job?.meta?.company_logo?.sizes?.medium}/${jobView?.employer_profile?.company_logo}`} /> : <img src={require("../../assets/images/default-logo1.png")} className="logo" />}
                                            <div className="nameInfo">
                                                <strong>{jobView?.employer_profile?.company_name} </strong>
                                                <span>{jobView?.employer_profile?.state}</span>
                                            </div>
                                        </div> */}
                                        <div className="amountBtnSet">
                                            {/* <div className="amount">
                                                <span>₹ {jobView?.job_salary}</span>
                                                <small>/{moment(jobView?.job_timing_slot, "HH:mm a").format("HH")} hr</small>
                                            </div> */}
                                            {/* <Button onClick={Onsubmit} variant="contained" className="applyBtn">
                                                <span>Apply now</span>
                                                <EastIcon />
                                            </Button> */}
                                        </div>
                                    </div>
                                    <div className="cardBody cardContainerMain">
                                    <div className="subCont">
                                        <div className="title">{jobView?.course_name}</div>
                                        <div className="content">
                                            {jobView?.description}
                                        </div>
                                        </div>
                                        <div className="subContBtn">
                                            <Button onClick={Onsubmit} variant="contained" className="applyBtn">
                                                <span>Apply now</span>
                                                <EastIcon />
                                            </Button>
                                        </div>
                                        {/* <div className="qualificationSetContainer">
                                            <div className="qualificationSet">
                                                <img src={require('../../assets/images/work-icon.png')} alt="" />
                                                <span>{jobView?.experience}</span>
                                            </div>
                                            <div className="qualificationSet">
                                                <img src={require('../../assets/images/location-icon1.png')} alt="" />
                                                <span>{jobView?.city}</span>
                                            </div>
                                            <div className="qualificationSet">
                                                <img src={require('../../assets/images/graduation.png')} alt="" />
                                                <span>{jobView?.required_qualifications}</span>
                                            </div>
                                        </div> */}
                                    </div>
                                    <div className="cardFooter">
                                        <div className="footerInfo">
                                            Posted:
                                            <span>  {dayjs(jobView?.created_at, "YYYY-MM-DD").format("DD-MM-YYYY")} </span>
                                            {/* <span> {daysRemaining} days ago</span> */}
                                        </div>
                                        <div className="footerInfoRight">
                                            <div className="footerInfo">
                                                Job ID:
                                                <span>{jobView?.id}</span>
                                            </div>
                                            <div className="footerInfo leftRightBorder">
                                                <img src={require('../../assets/images/date-icon2.png')} />
                                                <span>{dayjs(jobView?.created_at, "YYYY-MM-DD").format("DD-MM-YYYY")}</span>
                                            </div>
                                            <div className="footerInfo">
                                                <img src={require('../../assets/images/time-icon.png')} />
                                                {/* <span>{workingTiming}</span> */}
                                                <span>{jobView?.time}</span>
                                            </div>
                                        </div>
                                    </div>
                                </div>



                                {jobView?.dress_code ? <div className="discriptionCard">
                                    <div className="title">
                                        Required Dress Code
                                    </div>
                                    <ul className="contentList">
                                        <li>{jobView?.dress_code}</li>
                                    </ul>
                                </div> : null}
                            </>)}
                    </div>
                </Grid>
                <Grid item xs={12} sm={12} lg={4.5}>
                    <div className="discriptionTabCard">
                        <div className="headerContainer">
                            {jobView?.employer_profile?.company_logo != null ? <img src={`${job?.meta?.company_logo?.path}/${job?.meta?.company_logo?.folder}/${job?.meta?.company_logo?.sizes?.medium}/${jobView?.employer_profile?.company_logo}`} /> : <img src={require("../../assets/images/default-logo1.png")} className="logo" />}
                        </div>
                        <div className="discriptionTab">
                            <Tabs value={selectedTab} onChange={handleTabChange}>
                                <Tab label="About company" />
                                <Tab label="Overview" />

                            </Tabs>
                            {selectedTab === 0 && (
                                <div className="tabContnet">
                                    <p>{jobView?.employer_profile?.about_company}</p>
                                </div>
                            )}
                            {selectedTab === 1 && (
                                <div className="tabContnet">
                                    <p>An event management company is a business that specializes in organizing, planning, and executing events. These events can range from small gatherings to large conferences, trade shows, and festivals. Overall, an event management company plays a critical role in creating successful and impactful events, and can help clients save time, reduce stress, and achieve their goals.</p>
                                </div>
                            )}
                            {selectedTab === 2 && (
                                <div className="tabContnet">
                                    <p>An event management company is a business that specializes in organizing, planning, and executing events. These events can range from small gatherings to large conferences, trade shows, and festivals. Overall, an event management company plays a critical role in creating successful and impactful events, and can help clients save time, reduce stress, and achieve their goals.</p>
                                </div>
                            )}
                        </div>
                    </div>
                    <div className="topCategoriesCard">
                        <div className="title">
                            Top categories
                        </div>
                        <div className="topCategoriesContainer">
                            <div className="topCategoriesSet">
                                <ShopIcon />
                                <span>Shop keeper</span>
                            </div>
                            <div className="topCategoriesSet">
                                <AccountanIocn />
                                <span>Accountant</span>
                            </div>
                            <div className="topCategoriesSet">
                                <WritingIcon />
                                <span>Content writer</span>
                            </div>
                            <div className="topCategoriesSet">
                                <Catering />
                                <span>Catering</span>
                            </div>
                            <div className="topCategoriesSet">
                                <SocialMedia />
                                <span>Social media assistant</span>
                            </div>
                            <div className="topCategoriesSet">
                                <TranslatorIcon />
                                <span>Translator</span>
                            </div>
                        </div>
                        <Button className="viewAllBtn">View all</Button>
                    </div>
                </Grid>
            </Grid>
            <div style={{ display: userData?.access_token ? 'none' : 'block' }} className="discriptionSignUpModal">
                <div className="signUpModalInner">
                    <Grid container spacing={5} rowSpacing={2}>
                        <Grid item xs={12} sm={4}>
                            <SignUpBanner />
                        </Grid>
                        <Grid item xs={12} sm={6.5}>
                            <div className="WizardFormContainer">
                                <div className="title">Please Sign In to apply</div>
                                <form>
                                    <Grid container spacing={5} rowSpacing={2.5}>
                                        <Grid item xs={12} sm={6}>
                                            <TextField
                                                id="first_name"
                                                label="Email ID"
                                                className="form-control"
                                                error={!!emailError}
                                                helperText={emailError}
                                                onBlur={validateEmail}
                                                onChange={(event) => setEmail(event.target.value)}
                                                InputProps={{
                                                    type: 'email',
                                                }}
                                            />
                                        </Grid>
                                        <Grid item xs={12} sm={6}>
                                            <TextField
                                                id="first_name"
                                                label="Mobile number"
                                                className="form-control"
                                                onBlur={handlePhoneBlur}
                                                error={!!phoneError}
                                                helperText={phoneError}
                                                value={phone}
                                                onChange={(event) => {
                                                    const phoneNumber = event.target.value;
                                                    if (phoneNumber.length <= 10) {
                                                        const numericInput = phoneNumber.replace(/\D/g, '')
                                                        setPhone(numericInput);
                                                    }
                                                }}
                                                inputProps={{ inputMode: 'numeric' }}
                                            />
                                        </Grid>
                                        <Grid item xs={12} sm={12}>
                                            <div className="WizardNextStep">
                                                <Button onClick={handlesignup} variant="contained" className="WizardNextBtn">
                                                    <span>Sign Up</span>
                                                </Button>
                                            </div>
                                        </Grid>
                                        <Grid item xs={12} sm={12}>
                                            <Button onClick={handlelogin} variant="outlined" className="WizardBackBtn">
                                                <span>Have an account Login</span>
                                            </Button>
                                        </Grid>
                                    </Grid>
                                </form>
                            </div>
                        </Grid>
                    </Grid>
                </div>
            </div>
            <Loader loading={loading} />
            <ErrorPopup />
            {openSnackbar ? <CustomSnackBar
                openSnackbar={openSnackbar}
                snackBarMessage={customMsg?.msg}
                status={customMsg?.type}
                handleSnackbarClick={handleSnackbarClick}
            /> : null}
        </div>
    )
}
