import React, { useEffect, useState } from 'react';
import { Grid, TextField, InputLabel, FormControl, InputAdornment, Button, MenuItem, Menu, Popover, Typography, LinearProgress, Stack } from '@mui/material';
import IconButton from '@mui/material/IconButton';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
// import { NewPassword, RequestsPassword, loginWithOuth, subscribe } from '../../sevices/account'
import './style.scss'
// import { ENV_CONFIG, REGEX, ROLES, VALIDATION_MSG } from '../../constant';
import { isEmptyObj, removeItemFrmLocalStorage, setCurrentUser } from '../../utils/genericUtilities';
import { Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from '@mui/material';
import { Spin } from 'antd';
// import Razorpay from "razorpay";
import { useLocation, useNavigate } from 'react-router-dom';
// import { useValidation } from '../../constant/index'
import Loader from '../Loader';
import CustomSnackBar from '../../shared/snackBar';
import { Key } from '@mui/icons-material';


const LoginComponent = (props) => {
  const [showPassword, setShowPassword] = useState(false);
  const [errorPopupOpen, setErrorPopupOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [customMsg, setCustomMsg] = useState({});
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [request, setrequest] = useState(false)
  const [strength, setStrength] = useState([])

  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);

  const location = useLocation();
  const navigate = useNavigate();
  const handleCloseAnchor = () => {
    setAnchorEl(null);
  };


  const { email,
    setEmail,
    emailError,
    setEmailError,
    validateEmail,
    validatefield,
    password,
    setPassword,
    passwordError,
    confirmPassword,
    setConfirmPassword,
    setPasswordError,
    setConfirmPasswordError,
    confirmPasswordError,
    handleConfirmPasswordBlur,
    handlePasswordBlur } = useValidation();


  const queryParams = new URLSearchParams(window.location.search);
  const roles = queryParams.get('roles');
  const forget = queryParams.get('reset');
  const ResetToken = queryParams.get('data');

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };
  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const Signup = () => {
    navigate(`/signup?roles=${roles}`)
    // if(roles==='employer'){
    //   navigate(`/signup?roles=${roles}`)
    // }else{
    //   navigate(`/signup?roles=${roles}`)
    // }

  }
  const token = localStorage.getItem("token")


  const handleSnackbarClick = (status, msgInfo) => {
    setOpenSnackbar(status);
    !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
  };


  const ErrorPopup = () => {
    return (
      <Dialog open={errorPopupOpen} onClose={() => setErrorPopupOpen(false)}>
        <DialogTitle>Error</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Incorrect username or password. Please try again.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setErrorPopupOpen(false)}>OK</Button>
        </DialogActions>
      </Dialog>
    );
  };


  const HandlePasswordReset = async () => {
    if (!email) {
      return setEmailError(VALIDATION_MSG.emailRequired);
    }
    setLoading(true);
    let body = {};
    body.email = email
    body.roles = roles
    try {
      const response = await RequestsPassword(body);
      if (response?.status === 200) {
        setrequest(true)
        handleSnackbarClick(true, { type: 'success', msg: "We've emailed you a password reset link. Please check your inbox." })
        setLoading(false);

        setTimeout(() => {
          navigate("/")
        }, 3500);
      }

    } catch (error) {
      setLoading(false);
      handleSnackbarClick(true, { type: 'error', msg: error?.response?.data?.errors })
    }
  }

  const handleChangeEmail = async (e) => {
    let email = e.target.value;

    if (email.length > 50) {
      email = email.slice(0, 50);
      setEmailError('Email is too long');
    } else {
      setEmailError('');
    }



    setEmail(email);
  }

  const handleFocusPassword = async (e) => {
    // alert("3333")
    setAnchorEl(e.currentTarget)
  }


  const handleChangePassword = async (e) => {
    // setAnchorEl(e.currentTarget)
    let newPassword = e.target.value;
    // if (!newPassword) {
    //   setPasswordError('Password is required');
    // } else if (newPassword.length > 32) {
    //   // Truncate the input to the maximum length
    //   newPassword = newPassword.slice(0, 32);
    //   setPasswordError('Password is too long');
    // } else if (newPassword.length < 6) {
    //   setPasswordError('Password must be at least 6 characters long');
    // } else if (!/[A-Z]/.test(newPassword)) {
    //   setPasswordError('Password must contain at least one uppercase letter');
    // } else if (!/[a-z]/.test(newPassword)) {
    //   setPasswordError('Password must contain at least one lowercase letter');
    // } else if (!/\d/.test(newPassword)) {
    //   setPasswordError('Password must contain at least one numeric digit');
    // } else if (!/[!@#$%^&*]/.test(newPassword)) {
    //   setPasswordError('Password must contain at least one special symbol (!@#$%^&*)');
    // } else {
    //   setPasswordError('');
    // }
    // if (newPassword.length < 6) {
    //   const isValuePresent = strength.some((item) => item?.passwordLength === true);
    //   if (!isValuePresent) {
    //     setStrength([...strength, {passwordLength:true}])
    //   }
    // } else if (/[A-Z]/.test(newPassword)) {
    //   const isValuePresent = strength.some((item) => item?.Uppercase === true);
    //   if (!isValuePresent) {
    //     setStrength([...strength, {Uppercase:true}])
    //   }
    // } else if (/[a-z]/.test(newPassword)) {
    //   const isValuePresent = strength.some((item) => item?.Lowercase === true);
    //   if (!isValuePresent) {
    //     setStrength([...strength, {Lowercase:true}])
    //   }
    // } else if (/\d/.test(newPassword)) {
    //   const isValuePresent = strength.some((item) => item?.number === true);
    //   if (!isValuePresent) {
    //     setStrength([...strength, {number:true}])
    //   }
    // } else if (/[!@#$%^&*]/.test(newPassword)) {
    //   const isValuePresent = strength.some((item) => item?.symbol === true);
    //   if (!isValuePresent) {
    //     setStrength([...strength, {symbol:true}])
    //   }
    // }
    updateStrength('passwordLength', newPassword.length < 6);
    updateStrength('Uppercase', /[A-Z]/.test(newPassword));
    updateStrength('Lowercase', /[a-z]/.test(newPassword));
    updateStrength('number', /\d/.test(newPassword));
    updateStrength('symbol', /[!@#$%^&*]/.test(newPassword));
    setPassword(newPassword);

    if (!newPassword) {
      setStrength([]);
    }
  }
  const updateStrength = (criteriaName, condition) => {
    const isValuePresent = strength.some((item) => item?.[criteriaName] === true);
    if (!isValuePresent && condition) {
      setStrength([...strength, { [criteriaName]: true }]);
    }
  };
  const HandleNewPassword = async () => {
    if (!password) {
      return setPasswordError(VALIDATION_MSG.passwordRequired);
    }
    let body = {};
    setLoading(true);
    body.token = ResetToken
    body.password = password


    try {
      const response = await NewPassword(body)
      if (response?.status === 200) {
        setLoading(false);
        navigate(`/login?roles=${roles}`)
      }
    } catch (error) {
      setLoading(false);
      console.log("ERROR", error)
      setErrorPopupOpen(true);
    }
  }

  const Onsubmit = async (event) => {

    event.preventDefault()
    const erros = {}

    if (!email || !password) {
      setEmailError(VALIDATION_MSG.emailRequired);
      setPasswordError(VALIDATION_MSG.passwordRequired);
      return
    }
    setLoading(true);
    let requestParams = {};
    requestParams.username = email;
    requestParams.password = password;
    requestParams.client_id = ENV_CONFIG.CLIENT_ID;
    requestParams.client_secret = ENV_CONFIG.CLIENT_SECRET;
    requestParams.grant_type = "password";
    requestParams.login_type = "password";
    try {
      const response = await loginWithOuth(requestParams);
      if (response?.status === 200) {

        let date = new Date();
        let currentTime = date.getTime();

        let type = response?.data?.types;
        // if(type[0] === 3){
        //   handleSnackbarClick(true, {type: 'error', msg: "Invalid login" })
        //   removeItemFrmLocalStorage(['currentUser','token'])
        // }
        // if(type[0] === 3){
        //   handleSnackbarClick(true, {type: 'error', msg: "Invalid login" })     
        // }
        if (type[0] === 3) {
          handleSnackbarClick(true, { type: 'error', msg: "Invalid login" })
          removeItemFrmLocalStorage(['currentUser', 'token'])
        }

        if (roles === 'employee') {
          if (type[0] === 2) {
            const expires_in = currentTime + response?.data?.expires_in * 1000;
            setCurrentUser({ ...response?.data, expires_in });
            navigate(`/explorejobs?roles=${roles}`)
          }else{
            handleSnackbarClick(true, { type: 'error', msg: "Invalid login" })
          }

        } else {
          if (type[0] === 1) {
            const expires_in = currentTime + response?.data?.expires_in * 1000;
            setCurrentUser({ ...response?.data, expires_in });
            navigate("/dashboard");
          }else{
            handleSnackbarClick(true, { type: 'error', msg: "Invalid login" })
          }
        }
      }
    } catch (err) {
      console.log("ERRR", err)
      setErrorPopupOpen(true);
    }
    finally {
      setLoading(false);
    }
  };
  const handleGoBack = () => {
    navigate(-1);
  };


  return (

    <div className="SinginFormRight">


      {loading && (
        <div className="SiginLoader">
          <Spin spinning={true} />
        </div>
      )}
      <a onClick={handleGoBack}>
        <img src={require('../../assets/images/Logo Blue TM.png')} className="SinginFormLogo" alt="" />
      </a>
      <div className="SinginFormTitleContainer">
        <span className="SinginFormTitle">

          {ResetToken ? 'Set Password' : forget ? 'Password Reset' : roles == 'employee' ? 'Welcome Back' : 'Employer Sign In'}
        </span>
        <span>{forget || ResetToken ? null : "Sign in to your account"}</span>
      </div>
      {ResetToken ? <div className="SinginFormPageContainer">
        <FormControl className="form-control">
          <InputLabel htmlFor="name-input">New Password</InputLabel>
          {/* <TextField
            variant="outlined"
            type={showPassword ? 'text' : 'password'}
            error={!!passwordError}
            helperText={passwordError}
            value={password}
            name='password'
            onBlur={handlePasswordBlur}
            onChange={(e) => setPassword(e.target.value)}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          /> */}

          <Stack
            spacing={0.5}
            sx={{
              '--hue': Math.min(strength?.length * 10, 120),
            }}
          >
            <TextField
              variant="outlined"
              type={showPassword ? 'text' : 'password'}
              error={!!passwordError}
              helperText={passwordError}
              value={password}
              name='password'
              onBlur={handlePasswordBlur}
              // onChangeCapture={handleFocusPassword}
              // onChange={(e) => setPassword(e.target.value)}
              onChange={handleChangePassword}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      onClick={handleClickShowPassword}
                      onMouseDown={handleMouseDownPassword}
                      edge="end"
                    >
                      {showPassword ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
              maxLength={10}
            />
            {console.log("strength?.length", strength?.length)}
            {password.length > 0 ? <LinearProgress variant="determinate"
              size="sm"
              value={Math.min((strength?.length * 100) / 5, 100)}
              sx={{
                bgcolor: 'background.level3',
                color: 'hsl(var(--hue) 80% 40%)',
              }}
            /> : null}
            <Typography
              level="body-xs"
              sx={{ alignSelf: 'flex-end', color: 'hsl(var(--hue) 80% 30%)' }}
            >
              {strength?.length <= 2 && password && 'Very weak'}
              {strength?.length == 3 && 'weak'}
              {strength?.length > 3 && strength?.length < 5 && 'strong'}
              {strength?.length == 5 && ' Very strong'}

              {/* {value.length < 3 && 'Very weak'}
                {value.length >= 3 && value.length < 6 && 'Weak'}
                {value.length >= 6 && value.length < 10 && 'Strong'}
                {value.length >= 10 && 'Very strong'} */}
            </Typography>
          </Stack>
        </FormControl>
        <FormControl className="form-control">
          <InputLabel htmlFor="name-input">Confirm Password</InputLabel>
          <TextField
            variant="outlined"
            type={showPassword ? 'text' : 'password'}
            error={!!confirmPasswordError}
            helperText={confirmPasswordError}
            onBlur={handleConfirmPasswordBlur}
            value={confirmPassword}
            name='password'
            // onChange={(e) => setConfirmPassword(e.target.value)}
            onChange={(e) => {
              const newValue = e.target.value;
              if (newValue.length <= 32) {
                setConfirmPassword(newValue);
              }
            }}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        </FormControl>
        <Button type='submit' onClick={HandleNewPassword} variant="contained" className="SinginFormBtn">Submit</Button>

      </div> : forget ? <div className="SinginFormPageContainer">
        <FormControl className="form-control">
          <InputLabel htmlFor="name-input">Email id</InputLabel>
          <TextField
            id="name-input"
            name='email'
            variant="outlined"
            value={email}
            onBlur={validateEmail}
            error={!!emailError}
            helperText={emailError}
            // onChange={(e) => setEmail(e.target.value)}
            onChange={handleChangeEmail}
            InputProps={{
              type: 'text',
            }}

          />
        </FormControl>

        {/* 

        <Grid container spacing={2} direction="row">
          <Grid item padding='0 0 0.5 10px' xs={8}>
            <span style={{}}>Link sent to the registered mail</span>
          </Grid>
          <Grid item xs={4} display="flex" justifyContent="end" >
            <a href=''onClick={HandlePasswordReset}  style={{}} >resend email?</a>
          </Grid>

        </Grid> */}

        <Button type='submit' onClick={HandlePasswordReset} variant="contained" className="SinginFormBtn">Reset</Button>
      </div> : <div className="SinginFormPageContainer">
        <form>
          <FormControl className="form-control">
            <InputLabel htmlFor="name-input">Email id or Mobile number</InputLabel>
            <TextField
              id="name-input"
              name='email'
              variant="outlined"
              value={email}
              onBlur={validateEmail}
              error={!!emailError}
              helperText={emailError}
              // onChange={(e) => setEmail(e.target.value)}
              onChange={handleChangeEmail}
              startDecorator={<Key />}
              InputProps={{
                type: 'text',
                maxLength: 50
              }}

            />

          </FormControl>
          <FormControl className="form-control">
            <InputLabel htmlFor="name-input">Password</InputLabel>
            <Stack
              spacing={0.5}
              sx={{
                '--hue': Math.min(strength?.length * 10, 120),
              }}
            >
              <TextField
                variant="outlined"
                type={showPassword ? 'text' : 'password'}
                error={!!passwordError}
                helperText={passwordError}
                value={password}
                name='password'
                onBlur={handlePasswordBlur}
                // onChangeCapture={handleFocusPassword}
                // onChange={(e) => setPassword(e.target.value)}
                onChange={handleChangePassword}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        onClick={handleClickShowPassword}
                        onMouseDown={handleMouseDownPassword}
                        edge="end"
                      >
                        {showPassword ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
                maxLength={10}
              />
              {console.log("strength?.length", strength?.length)}
              {/* {password.length > 0 ? <LinearProgress variant="determinate"
                size="sm"
                value={Math.min((strength?.length * 100) / 5, 100)}
                sx={{
                  bgcolor: 'background.level3',
                  color: 'hsl(var(--hue) 80% 40%)',
                }}
              /> : null} */}
              {/* <Typography
                level="body-xs"
                sx={{ alignSelf: 'flex-end', color: 'hsl(var(--hue) 80% 30%)' }}
              > */}
              {/* {strength?.length <= 2 && password && 'Very weak'}
                {strength?.length == 3 && 'weak'}
                {strength?.length > 3 && strength?.length < 5 && 'strong'}
                {strength?.length == 5 && ' Very strong'} */}

              {/* {value.length < 3 && 'Very weak'}
                {value.length >= 3 && value.length < 6 && 'Weak'}
                {value.length >= 6 && value.length < 10 && 'Strong'}
                {value.length >= 10 && 'Very strong'} */}
              {/* </Typography> */}
            </Stack>
          </FormControl>
          {/* <Menu
              id="header_profile_dropdown"
              className="header-profile-dropdown"
              aria-labelledby="demo-positioned-button"
              anchorEl={anchorEl}
              open={open}
              onClose={handleCloseAnchor}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
            >
             <MenuItem>dddddd </MenuItem>
             <MenuItem>adfhszdfgzdf </MenuItem>
            </Menu> */}

          {/* <Popover
            // id={id}
            open={open}
            anchorEl={anchorEl}
            onClose={handleCloseAnchor}
            anchorOrigin={{
              vertical: 'bottom',
              horizontal: 'left',
            }}
          >
            <Typography sx={{ p: 2 }}>The content of the Popover.</Typography>
          </Popover> */}
          <a href={`/login?reset=password&roles=${roles}`}>Forgot Password ?</a>
          <Button type='submit' onClick={Onsubmit} variant="contained" className="SinginFormBtn">Sign in</Button>
          <Button type='button' onClick={Signup} variant="outlined" className="SinginFormLoginBtn">Don't have an account? Sign up here</Button>
        </form>
      </div>}
      <ErrorPopup />
      {openSnackbar ? <CustomSnackBar
        openSnackbar={openSnackbar}
        snackBarMessage={customMsg?.msg}
        status={customMsg?.type}
        handleSnackbarClick={handleSnackbarClick}
      /> : null}
    </div>
  );
}

export default LoginComponent;
