import { useState } from 'react';
import { Button, Stepper, Step, StepLabel, StepIcon } from '@mui/material';
import { ReactComponent as User } from './../../../assets/images/wizard-user.svg';
import { ReactComponent as Device } from './../../../assets/images/wizard-device.svg';
import { ReactComponent as Rupee } from './../../../assets/images/wizard-rupee.svg';
import { ReactComponent as Tick } from './../../../assets/images/wizard-tick.svg';
import { ReactComponent as BankIcon } from './../../../assets/images/bank-icon.svg';
import PersonalInfo from './personal-info';
import EducationInfo from './education-info';
import SubscriptionPlans from './subscription-plans';
import BankDetails from './bank-details'

import CustomSnackBar from '../../../shared/snackBar';
import { isEmptyObj } from '../../../utils/genericUtilities';

function CustomStepIcon(props) {
  const { completed, icon } = props;
  return (
    <div className="WizardStepperIcon">
      {completed ? (
        <span className="WizardCompletedIcon"><Tick /></span>
      ) : (
        <span>{props.icon}</span>
      )}
    </div>
  );
}
const RegistrationWizard = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [genericData, setGenericData] = useState({
    personalInfo: {},
    educationInfo: {},
    bankDetails: {},

  });
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [customMsg, setCustomMsg] = useState({});

  const steps = [
    { label: <span>General <br /> information</span>, icon: <User /> },
    // { label: <span>Education <br /> details</span>, icon: <Device /> },
    // { label: <span>Bank <br /> details</span>, icon: <BankIcon /> },
    { label: <span>Subscription <br /> plans</span>, icon: <Rupee /> },
  ];

  const handleNext = (data) => {
    if (data.tabKey == 1) {
      setGenericData(prevState => ({ ...prevState, personalInfo: data?.personalInfo }))
    } 
    // else if (data.tabKey == 2) {
    //   setGenericData(prevState => ({ ...prevState, educationDetails: data?.educationDetails }))
    // }
    //  else if (data.tabKey == 2) {
    //   setGenericData(prevState => ({ ...prevState, userdata: data?.UserData, bankData:data?.bankDetailsData}))
    // }
    setActiveStep(activeStep + 1);
  };

  const handleBack = (event) => {
    event?.preventDefault();
    setActiveStep(activeStep - 1);
  };

  const handleSnackbarClick = (status, msgInfo) => {
    setOpenSnackbar(status);
    !isEmptyObj(msgInfo) && setCustomMsg(msgInfo);
  };

  // useEffect(() => {
  //   if (activeStep === 1 && !isEmptyObj(genericData)) {
  //     setCompanyDetails(genericData); 
  //   }
  // }, [activeStep, genericData])

  return (
    <div className="registrationWizard">
      <div className="WizardStepper">
        <Stepper activeStep={activeStep}>
          {steps.map(({ label, icon }, index) => {
            const stepProps = {};
            const labelProps = {};
            return (
              <Step key={label} {...stepProps}>
                <StepLabel
                  StepIconComponent={() => <CustomStepIcon completed={index < activeStep} icon={icon} />}
                  {...labelProps}
                >
                  {/* <img src={icon}/> */}
                  {label}
                </StepLabel>
              </Step>
            );
          })}
        </Stepper>
      </div>
      {activeStep === 0 && <PersonalInfo handleSnackbarClick={handleSnackbarClick} onNext={handleNext} employeeDetails={genericData} />}
      {/* {activeStep === 1 && <EducationInfo onNext={handleNext} employeeDetails={genericData} onBack={handleBack} />} */}
      {/* {activeStep === 1 && <BankDetails handleSnackbarClick={handleSnackbarClick} onNext={handleNext} onBack={handleBack} employeeDetails={genericData} />} */}
      {activeStep === 1 && <SubscriptionPlans userData={genericData} onBack={handleBack} />}

      {/* <div>
        {activeStep !== 0 && (
          <Button variant="contained" onClick={handleBack}>
            Back
          </Button>
        )}
        {activeStep !== steps.length - 1 && (
          <Button variant="contained" color="primary" onClick={handleNext}>
            Next
          </Button>
        )}
        {activeStep === steps.length - 1 && (
          <Button variant="contained" color="primary">
            Finish
          </Button>
        )}
      </div> */}
      {openSnackbar ? <CustomSnackBar
        openSnackbar={openSnackbar}
        snackBarMessage={customMsg?.msg}
        status={customMsg?.type}
        handleSnackbarClick={handleSnackbarClick}
      /> : null}
    </div>
  );
}

export default RegistrationWizard;
