import React, { useEffect, useState } from 'react';
import { Grid, TextField, InputLabel, FormControl, Button } from '@mui/material';
import SignInCarosel from './signInCarousel'
import './employerStyle.scss'
import { useNavigate } from "react-router-dom";
import { Spin } from 'antd';
import { ENV_CONFIG, REGEX, ROLES, VALIDATION_MSG, useValidation } from '../../constant';
import { ReactComponent as LoginBg } from '../../assets/images/sign-in-bg.svg';
import { ReactComponent as LoginBack } from '../../assets/images/left-nav.svg';

const SignUP = () => {

    const [showPassword, setShowPassword] = useState(false);
    const [errorPopupOpen, setErrorPopupOpen] = useState(false);

    const queryParams = new URLSearchParams(window.location.search);
    const [loading, setLoading] = useState(false);
    const rolesEmployee = queryParams.get('roles');
    // const navigate = useNavigate();
    const [locationValue,setLocationValue] = useState('');
    const handleGoBack = () => {
      navigate(-1); 
    };
    useEffect(() => {
      locationIp();
  }, []);
  const locationIp = () =>{
    try {
      fetch(
        `https://ipapi.co/json/`)
        .then((res) => res.json())
        .then((data) => {
          setLocationValue(data?.city)     
        })

    } catch (error) {
      console.log("Error fetching city name:", error);
    }
  }
    const {
        email,
        setEmail,
        emailError,
        handleEmailBlur,
        setPhone,
        phoneError,
        phone,
        setEmailError,
        handlePhoneBlur,
        setPhoneError
    } = useValidation();

    const navigate = useNavigate();


    const login = () => {
        if (rolesEmployee === 'employee') {
            navigate("/login?roles=employee")
        } else {
            navigate("/login?roles=employer")
        }

    }
    const signup = (event) => {
        event?.preventDefault()

        if (!email) {
            setEmailError(VALIDATION_MSG.emailRequired);
        }
        if (!phone) {
            setPhoneError(VALIDATION_MSG.phoneRequired);
        }

        if (rolesEmployee === 'employee') {
            if (email && phone?.length >= 10 && !emailError) {
                setLoading(true);
                const data = {
                    email: email,
                    phone: phone
                }
                setTimeout(() => {
                    navigate("/employeesignin", { state: data })
                }, 1000);
            }
        } else {

            if (email && phone?.length >= 10 && !emailError) {
                setLoading(true);
                const data = {
                    email: email,
                    phone: phone
                }
                setTimeout(() => {
                    navigate("/company-registration", { state: data })
                }, 1000);
            }
        }
    }

    // const handleKeyPressForModal = (event) => {
    //     const regexp = /^\S*$/;// Remove Whitespace
    //     if (!regexp.test(event.key)) {
    //         event.preventDefault();
    //     } else if (event.key === 'Enter') {
    //         handleSubmit(event)
    //     }
    // }
    return (
        <div className="SinginFormPage">
            {loading && (
                <div className="SiginLoader">
                    <Spin spinning={true} />
                </div>
            )}

            <Grid container spacing={0}>
                {rolesEmployee == 'employer' ? <Grid item xs={12} sm={6}>
                    <div className="SinginFormLeft">
                    <Button className='loginBackBtn'  onClick={handleGoBack}>
                        <LoginBack/>
                        <span>Back</span>
                    </Button>
                    <LoginBg className="SinginFormBgImg BannerSVG"/>
                        {/* <img src={require('../../assets/images/sign-in-bg.png')} className="SinginFormBgImg" alt="" /> */}
                        <div className="SinginFormCarouselContainer">
                            <div className="SinginFormCarouselTitle">
                                Join over 15,000+ <br />
                                users around {locationValue}
                            </div>
                            <SignInCarosel />
                        </div>
                    </div>
                </Grid> : <Grid item xs={12} sm={6}>
                    <div className="SinginFormLeft">
                    <Button className='loginBackBtn'  onClick={handleGoBack}>
                        <LoginBack/>
                        <span>Back</span>
                    </Button>
                        <img src={require('../../assets/images/jobseek.jpg')} className="SinginFormBgImg" style={{ padding: '50px 120px' }} alt="" />
                        <div className="SinginFormCarouselContainer" style={{ top: 0, paddingBottom: '30px' }}>
                            <div className="SinginFormCarouselTitle">
                                Join over 15,000+ <br />
                                users around {locationValue}
                            </div>
                            <SignInCarosel />
                        </div>
                    </div>
                </Grid>}

                <Grid item xs={12} sm={5}>
                    <div className="SinginFormRight">
                        <a  onClick={handleGoBack}>
                            <img src={require('../../assets/images/Logo Blue TM.png')} className="SinginFormLogo" alt="" />
                        </a>
                        <div className="SinginFormTitleContainer">
                            <span className="SinginFormTitle">
                                {rolesEmployee == 'employee' ? 'Part-Time Power, in Your Pocket!' : 'Employer Sign Up'}
                            </span>
                            <span>{  rolesEmployee === 'employee' ? 'Sign up now and start earning on your terms.' :"Unlock Your Next Hire – Sign Up as an Employer"}</span>
                        </div>
                        <div className="SinginFormPageContainer">
                            <form on >
                                <FormControl className="form-control">
                                    <InputLabel htmlFor="name-input">Email id</InputLabel>
                                    <TextField
                                        id="name-input"
                                        variant="outlined"
                                        value={email}
                                        onBlur={handleEmailBlur}
                                        error={!!emailError}
                                        helperText={emailError}

                                        onChange={(event) => setEmail(event.target.value)}
                                        InputProps={{
                                            type: 'email',
                                        }}
                                        inputProps={{
                                            maxLength:50                                        }}
                                    />
                                </FormControl>
                                <FormControl className="form-control">
                                    <InputLabel htmlFor="name-input">Mobile number</InputLabel>
                                    <TextField
                                        id="name-input"
                                        variant="outlined"
                                        onBlur={handlePhoneBlur}
                                        error={!!phoneError}
                                        helperText={phoneError}
                                        value={phone}
                                        onChange={(event) => {
                                            const phoneNumber = event.target.value;

                                            if (phoneNumber.length <= 10) {
                                                const numericInput = phoneNumber.replace(/\D/g, '')
                                                setPhone(numericInput);
                                            }
                                        }}
                                        inputProps={{ inputMode: 'numeric' }}
                                    />
                                </FormControl>
                                <Button type='submit' onClick={signup} variant="contained" className="SinginFormBtn">Sign up</Button>
                                <Button  type='button' onClick={login} variant="outlined" className="SinginFormLoginBtn">Already signed up? Sign in here</Button>
                            </form>
                        </div>
                    </div>
                </Grid>
            </Grid>
            <img src={require('../../assets/images/geo.png')} className="SignInGeoTop" alt="" />
            <img src={require('../../assets/images/geo1.png')} className="SignInGeoBottom" alt="" />
        </div>
    );
}

export default SignUP;
