import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './login/style.scss'; // Import your custom CSS for additional styling
import { Link, Navigate, useNavigate } from 'react-router-dom';

import { Button, createTheme, ThemeProvider } from '@mui/material'

function CandidateSearch() {
  const [job, setJob] = useState({ title: '', description: '' });
  const imagePath = require('../assets/images/user3.png');

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const navigate = useNavigate();

  const handleSubmit = (e) => {

    // Call API to save job listing
  };

  const jobs = [
    { title: 'Job 1', description: 'Description 1' },
    { title: 'Job 2', description: 'Description 2' },
    { title: 'Job 3', description: 'Description 3' },
    { title: 'Job 4', description: 'Description 4' },
    { title: 'Job 4', description: 'Description 4' },
    { title: 'Job 4', description: 'Description 4' },


  ];

  const handleNavigate = (e) => {
    // e.preventDefault();
    navigate("/candidatesview")
  }



  return (
    <>

    <header className="home-header">
      <nav>
        <ul>
          <li><a href="/EmployerLogin?roles=jobseeker">Job Seekers</a></li>
          <li><Link to="/EmployerLogin?roles=emp">Employers</Link></li>
        </ul>
      </nav>
    </header>


    <div  style={{margin:50}}  className="home-container">
        <div className="container my-5">
          <div className="row mb-4">
            <div style={{ width: 500 }}>
              <input style={{ height: 50 }} type="text" className="form-control" placeholder="Search Skills,title....." />
            </div>
          </div>
          <div className="row">
            {jobs?.map((job, index) => (
              <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={index}>
                <div className="card custom-card">
                  <img

                    style={{ width: '20%', height: 'auto' }}
                    src={imagePath}
                    className="card-img-top"
                    alt="..." />
                  <div className="card-body">
                    <h5 className="card-title">{job.title}</h5>
                    <p className="card-text">Lorem ipsum dolor sit amet consectetur adipisicing elit. deserunt quasi!</p>
                    <Button style={{ outlineColor: "black" }} variant="outlined" onClick={(e) => handleNavigate(e)} className="btn btn-primary">Apply for this job</Button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div></>
  );
}

export default CandidateSearch;
