  const [institute, setInstitute] = useState('');
  const [university, setUniversity] = useState('');
  const [yearOfPassing, setYearOfPassing] = useState('');
  const [fieldOfEducation, setFieldOfEducation] = useState('');
  const [aboutYou, setAboutYou] = useState('');
  const [fieldOfExperience, setFieldOfExperience] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [certificate, setCertificate] = useState('');
  const [educationError, seteducationError] = useState('')
  const [collegeError, setcollegeError] = useState('')
  const [universityError, setuniversityError] = useState('')
  const [fielldError, setfieldError] = useState('')
  const [yearError, setyearError] = useState('')


  const handleCheckboxChange = (event) => {
    const { name, checked } = event.target;
    if (checked) {
      setCheckedItems([...checkedItems, name]);
    } else {
      setCheckedItems(checkedItems.filter((item) => item !== name));
    }
  };


  const handleRadioChange = (event) => {
    setExperience(event.target.value);
  };

  const checkboxData = [
    { id: 'communication', label: 'Communication' },
    { id: 'technical', label: 'Technical' },
    { id: 'marketing', label: 'Marketing' },
    { id: 'leadership', label: 'Leadership' },
    { id: 'interpersonal', label: 'Interpersonal' },
    { id: 'sales', label: 'Sales' },
    { id: 'teamwork', label: 'Teamwork' },
    { id: 'customer_service', label: 'Customer service' },
    { id: 'analytical', label: 'Analytical' },
    { id: 'creative_design', label: 'Creative or Design' },
    { id: 'financial_analysis', label: 'Financial analysis' },
    { id: 'data analysis', label: 'Data analysis' },
    { id: 'Project_management', label: 'Project management' },
    { id: 'research', label: 'Research' },
    { id: 'writing_editing', label: 'Writing and editing' },
  ];


  const handleNext = (event) => {

    event?.preventDefault();

    const educationDetails = {
      educationQualification,
      institute,
      university,
      yearOfPassing,
      fieldOfEducation,
      aboutYou,
      experience,
      checkedItems,
      fieldOfExperience,
      jobTitle,
      certificate

    };

    localStorage.setItem("educationInfo", JSON.stringify(educationDetails));

    onNext({ educationDetails, tabKey: 2 });
  }

  const backPersonal = (event) => {
    event?.preventDefault();

    const educationDetails = {
      educationQualification,
      institute,
      university,
      yearOfPassing,
      fieldOfEducation,
      aboutYou,
      experience,
      checkedItems,
      fieldOfExperience,
      jobTitle,
      certificate

    };

    localStorage.setItem("educationInfo", JSON.stringify(educationDetails));

    onBack()
  }

  useEffect(() => {

    let loaclInfo = localStorage.getItem("educationInfo")
    let infoData = JSON.parse(loaclInfo)

    if (infoData) {
      setEducationQualification(infoData?.educationQualification);
      setInstitute(infoData?.institute);
      setUniversity(infoData?.university);
      setYearOfPassing(infoData?.yearOfPassing);
      setFieldOfEducation(infoData?.fieldOfEducation);
      setAboutYou(infoData?.aboutYou);
      setExperience(infoData?.experience);
      setCheckedItems(infoData?.checkedItems);
      setFieldOfExperience(infoData?.fieldOfExperience);
      setJobTitle(infoData?.jobTitle);
      setCertificate(infoData?.certificate);

      // setPreviewImage(URL.createObjectURL(infoData?.selectedFile))
    }

  }, []);


  // const validateLanguagesKnown = (value) => {
  //   const containsNumbers = /\d/.test(value);
  //   if (containsNumbers) {
  //     seteducationError('Language known should not contain numbers.');
  //     return false;
  //   } else {
  //     seteducationError('');
  //     return true;
  //   }
  // };

  const validateEducationQualification = (event) => {
    const inputValue = event.target.value;
    const containsNumbers = /\d/.test(inputValue);
    const regex = /^[A-Za-z ]+$/;
    if (containsNumbers) {
      seteducationError(VALIDATION_MSG.education);
    } else {
      seteducationError('')
    }
    if (regex.test(inputValue)) {
      setEducationQualification(inputValue);
    }

  };
  const validatecollege = (event) => {

    const inputValue = event.target.value;
    const regex = /^[A-Za-z ]+$/;
    const containsNumbers = /\d/.test(inputValue);

    if (containsNumbers) {
      setcollegeError(VALIDATION_MSG.instite);
    } else {
      setcollegeError('')
    }
    if (regex.test(inputValue)) {
      setInstitute(inputValue);
    }

  };
  const validateuniversity = (event) => {

    const inputValue = event.target.value;
    const containsNumbers = /\d/.test(inputValue);

    const regex = /^[A-Za-z ]+$/;

    if (containsNumbers) {
      setuniversityError(VALIDATION_MSG.university);
    } else {
      setuniversityError('')
    }

    if (regex.test(inputValue)) {
      setUniversity(inputValue);
    } 

  };
  const validatefield = (event) => {

    const inputValue = event.target.value;
    const containsNumbers = /\d/.test(inputValue);
    const regex = /^[A-Za-z ]+$/; 

    if (containsNumbers) {
      setfieldError(VALIDATION_MSG.educationfeild);
    } else {
      setfieldError('')
    }
    if (regex.test(inputValue)) {
    setFieldOfEducation(inputValue);
    }
  };
  const validateyearofpssing = (event) => {

    const inputValue = event.target.value.replace(/[^0-9]/g, '').slice(0, 10);
    const containsNumbers = /\D/g.test(inputValue);

    if (containsNumbers) {
      setyearError(VALIDATION_MSG.yearpusing);
    } else {
      setyearError('')
    }
    setYearOfPassing(inputValue);
  };




  return (
    <div className="WizardFormContainer">
      <div className="title">Educational Details</div>
      <form>
        <Grid container spacing={5}>
          <Grid item xs={12} sm={12}>
            <TextField
              id="company_name"
              label="Highest education qualification"
              className="Detailsform-control"
              // onBlur={validateEducationQualification}
              value={educationQualification}
              error={!!educationError}
              helperText={educationError}
              InputProps={{
                type: 'text',
              }}
              onChange={validateEducationQualification}
              inputProps={{
                maxLength: 50
              }}
            />
          </Grid>
          <Grid item xs={12} sm={12}>
            <div className="subTitle">College</div>
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              id="company_website"
              label="Name of the Institute"
              className="form-control"
              error={!!collegeError}
              helperText={collegeError}
              value={institute}
              onChange={validatecollege}
              InputProps={{
                type: 'text',
              }}
              inputProps={{
                maxLength: 50
              }}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              id="company_address"
              label="University"
              className="form-control"
              value={university}
              onChange={validateuniversity}
              error={!!universityError}
              helperText={universityError}
              InputProps={{
                type: 'text',
              }}
              inputProps={{
                maxLength: 50
              }}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              id="state"
              label="Year of passing or pursuing"
              error={!!yearError}
              helperText={yearError}
              className="form-control"
              value={yearOfPassing}
              onChange={validateyearofpssing}
              // InputProps={{ inputMode: 'numeric' }}
              inputProps={{
                maxLength: 4,
                inputMode: 'tel',
                pattern: '[0-9]*',
              }}

            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              id="city "
              label="Field of education "
              className="form-control"
              value={fieldOfEducation}
              onChange={validatefield}
              error={!!fielldError}
              helperText={fielldError}
              InputProps={{
                type: 'text',
              }}
              inputProps={{
                maxLength: 50
              }}
            />
          </Grid>
          <Grid item xs={12} sm={12}>
            <div className="title">About you</div>
          </Grid>


          <Grid item xs={12} sm={12}>
            <TextareaAutosize
              minRows={8}
              maxRows={8}
              className="form-control"
              placeholder="Write a description about you"
              value={aboutYou}
              onChange={(e) => setAboutYou(e.target.value)}
            />
          </Grid>
          <Grid item xs={12} sm={12}>
            <div className="title">Experience Details</div>
          </Grid>

          <Grid item xs={12} sm={12}>
            <RadioGroup
              aria-labelledby="demo-radio-buttons-group-label"
              defaultValue="fresher"
              name="radio-buttons-group"
              className="experienceRadio"
              value={experience}
              onChange={handleRadioChange}
            >
              <FormControlLabel value="fresher" control={<Radio icon={<UncheckIcon />}
                checkedIcon={<CheckIcon />} />} label="Fresher" />
              <FormControlLabel value="experienced" control={<Radio icon={<UncheckIcon />}
                checkedIcon={<CheckIcon />} />} label="Experienced" />
            </RadioGroup>
          </Grid>

          <Grid item xs={12} sm={12}>
            <FormControl>
              <div className="experienceCheckBoxContainer">
                <div className="experienceCheckBoxBind">
                  {checkedItems.map((itemId) => (
                    <div className="checkBoxTag" key={itemId}>{`${checkboxData.find((checkbox) => checkbox.id === itemId).label}`}</div>
                  ))}
                </div>
                <div className="experienceCheckBox">
                  {checkboxData.map((checkbox) => (
                    <FormControlLabel
                      key={checkbox.id}
                      control={
                        <Checkbox
                          checked={checkedItems.includes(checkbox.id)}
                          onChange={handleCheckboxChange}
                          name={checkbox.id}
                        />
                      }
                      label={checkbox.label}
                    />
                  ))}
                </div>
              </div>
            </FormControl>
          </Grid>
          {experience == "experienced" ? <> <Grid item xs={12} sm={6}>
            <TextField
              id=""
              label="Field of experience"
              className="form-control"
              value={fieldOfExperience}
              // onChange={(e) => setFieldOfExperience(e.target.value)}
              onChange={(e) => {
                const inputVal = e.target.value;

                const regex = /^[A-Za-z0-9 ]+$/;

                if (regex.test(inputVal)) {
                  setFieldOfExperience(inputVal);
                }
            }}
              InputProps={{
                type: 'text',
              }}
              inputProps={{
                maxLength: 50
              }}
            />
          </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                id="jobTitle"
                label="Job title"
                className="form-control"
                value={jobTitle}
                // onChange={(e) => setJobTitle(e.target.value)}
                onChange={(e) => {
                  const inputVal = e.target.value;

                  const regex = /^[A-Za-z0-9 ]+$/;

                  if (regex.test(inputVal)) {
                    setJobTitle(inputVal);
                  }
              }}
                InputProps={{
                  type: 'text',
                }}
                inputProps={{
                  maxLength: 50
                }}
              />
            </Grid> </> : null}


          {/* <Grid item xs={12} sm={12}>
                <TextField
                        id="outlined-uncontrolled"
                        label="Upload your resume"
                        className="form-control fileAttachmentInput"
                        InputLabelProps={{ shrink: true }}
                        InputProps={{
                            type: 'file',
                            endAdornment: (
                                <InputAdornment position="end">
                                <AttachFileIcon />
                                </InputAdornment>
                            ),
                        }}
                    />
                </Grid> */}

          <Grid item xs={12} sm={12}>
            <TextField
              id="about_company"
              label="Any certification"
              className="form-control"
              value={certificate}
              // onChange={(e) => setCertificate(e.target.value)}
              onChange={(e) => {
                const inputVal = e.target.value;

                const regex = /^[A-Za-z0-9 ]+$/;

                if (regex.test(inputVal)) {
                    setCertificate(inputVal);
                }
            }}
              InputProps={{
                type: 'text',
              }}
              inputProps={{
                maxLength: 50
              }}
            />
          </Grid>
          <Grid item xs={12} sm={12}>
            <div className="WizardNextStep">
              <Button variant="contained" className="WizardNextBtn" onClick={handleNext}>
                <span>Next</span>
                <EastIcon />
              </Button>
              <Button variant="outlined" className="WizardBackBtn" onClick={backPersonal}>
                <EastIcon />
                <span>Back</span>
              </Button>
              <Button className="WizardBackBtn" onClick={onNext}>
                <span>Skip and continue</span>
              </Button>
            </div>
          </Grid>
        </Grid>
      </form>
    </div>
  );
}

export default EducationInfo;
